// CSV verilerini yükleme fonksiyonları
const loadCSVData = async () => {
  try {
    const [kadroResponse, maasResponse] = await Promise.all([
      fetch('/Kadro_Yetenek_Listesi__Vmin_2__Vmax_350__Ymin_20__Ymax_92_.csv'),
      fetch('/Manager_Salary_Table__20_100__step_1_.csv')
    ]);
    
    const kadroText = await kadroResponse.text();
    const maasText = await maasResponse.text();
    
    // CSV'leri parse et
    const kadroData = kadroText.split('\\n').slice(1).map(line => {
      const [kadroDegeri, yetenek] = line.split(',');
      return { kadroDegeri: parseFloat(kadroDegeri), yetenek: parseFloat(yetenek) };
    });
    
    const maasData = maasText.split('\\n').slice(1).map(line => {
      const [yetenek, maas] = line.split(',');
      return { yetenek: parseFloat(yetenek), maas: parseFloat(maas) };
    });
    
    return { kadroData, maasData };
  } catch (error) {
    console.error('CSV yükleme hatası:', error);
    return null;
  }
};

// Kadro değerine göre önerilen menajer yeteneği hesaplama
const getRecommendedManagerSkill = (kadroDegeri, kadroData) => {
  if (!kadroData || kadroData.length === 0) return 50; // Varsayılan değer
  
  // En yakın kadro değerini bul
  let closest = kadroData[0];
  let minDiff = Math.abs(kadroDegeri - closest.kadroDegeri);
  
  for (const data of kadroData) {
    const diff = Math.abs(kadroDegeri - data.kadroDegeri);
    if (diff < minDiff) {
      minDiff = diff;
      closest = data;
    }
  }
  
  return closest.yetenek;
};

// Yeteneğe göre maaş hesaplama
const getManagerSalary = (yetenek, maasData) => {
  if (!maasData || maasData.length === 0) return 1000; // Varsayılan değer
  
  // En yakın yetenek değerini bul
  let closest = maasData[0];
  let minDiff = Math.abs(yetenek - closest.yetenek);
  
  for (const data of maasData) {
    const diff = Math.abs(yetenek - data.yetenek);
    if (diff < minDiff) {
      minDiff = diff;
      closest = data;
    }
  }
  
  return closest.maas;
};

// Türk menajer isimleri
export const turkishManagerNames = [
  'Ahmet Yılmaz', 'Mehmet Kaya', 'Ali Demir', 'Mustafa Çelik', 'Hasan Şahin',
  'Hüseyin Yıldız', 'İbrahim Yıldırım', 'Murat Özdemir', 'Ömer Arslan', 'Yusuf Doğan',
  'Emre Kılıç', 'Can Aydın', 'Burak Özkan', 'Deniz Erdoğan', 'Ege Koç',
  'Kaan Aslan', 'Mert Çetin', 'Ozan Kurt', 'Serkan Özkan', 'Tolga Şen',
  'Uğur Güneş', 'Volkan Yalçın', 'Yasin Polat', 'Zeki Taş', 'Arda Korkmaz',
  'Berk Özer', 'Cem Aktaş', 'Derya Keskin', 'Eren Türk', 'Furkan Güler'
];

// Menajer taktikleri
export const managerTactics = [
  '4-4-2 Dikine Oyun',
  '4-4-2 Pas Oyunu', 
  '3-4-3 Dikine Oyun',
  '3-4-3 Pas Oyunu',
  '4-3-3 Dikine Oyun',
  '4-3-3 Pas Oyunu',
  '3-5-2 Dikine Oyun',
  '3-5-2 Pas Oyunu',
  '4-2-3-1 Dikine Oyun',
  '4-2-3-1 Pas Oyunu',
  '5-3-2 Dikine Oyun',
  '5-3-2 Pas Oyunu'
];

// Menajer oluşturma fonksiyonu
export const generateManager = (name, leagueName) => {
  // Lig bazında menajer kalitesi
  const leagueSettings = {
    'Süper Lig': { min: 25, max: 40 },
    '1. Lig': { min: 20, max: 35 },
    '2. Lig Beyaz': { min: 15, max: 30 },
    '2. Lig Kırmızı': { min: 15, max: 30 }
  };
  
  const settings = leagueSettings[leagueName] || { min: 15, max: 30 };
  
  // M, A, T değerleri (Menajerlik, Hücum, Taktik)
  const management = Math.floor(Math.random() * (settings.max - settings.min + 1)) + settings.min;
  const attacking = Math.floor(Math.random() * (settings.max - settings.min + 1)) + settings.min;
  const tactical = Math.floor(Math.random() * (settings.max - settings.min + 1)) + settings.min;
  
  // Taktik seçimi
  const tactic = managerTactics[Math.floor(Math.random() * managerTactics.length)];
  
  // Taraftar desteği
  const fanSupport = Math.random() > 0.3 ? 'TAMAM' : 'Kararsız';
  
  // Tazminat ödemesi
  const hasCompensation = Math.random() > 0.4;
  const compensation = hasCompensation 
    ? Math.floor(Math.random() * 5) + 1 // 1-5k arası
    : 0;
  
  return {
    name: name,
    management: management,
    attacking: attacking,
    tactical: tactical,
    tactic: tactic,
    fanSupport: fanSupport,
    compensation: compensation,
    league: leagueName
  };
};

// 5 rastgele menajer oluşturma
export const generateManagerCandidates = (leagueName) => {
  const candidates = [];
  const availableNames = [...turkishManagerNames];
  
  for (let i = 0; i < 5; i++) {
    const randomIndex = Math.floor(Math.random() * availableNames.length);
    const managerName = availableNames[randomIndex];
    availableNames.splice(randomIndex, 1); // İsmi listeden çıkar
    
    candidates.push(generateManager(managerName, leagueName));
  }
  
  return candidates;
};
