import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { turkishLeagues, allSquads } from '../data';

const StandingsModal = ({ setShowStandings, club }) => {
  const leagueTeams = turkishLeagues[club.league] || [];

  return (
    <div className="fc-modal-backdrop">
      <div className="fc-modal">
        <h3>PUAN DURUMU - {club.league}</h3>
        <div className="fc-panel">
          <table className="fc-table">
            <thead>
              <tr>
                <th>Pos</th>
                <th>TakÄ±m</th>
                <th>O</th>
                <th>G</th>
                <th>B</th>
                <th>M</th>
                <th>A</th>
                <th>Y</th>
                <th>AV</th>
                <th>P</th>
              </tr>
            </thead>
            <tbody>
              {leagueTeams.map((team, index) => (
                <tr 
                  key={index}
                  style={{
                    background: team.name === club.name ? 'var(--win)' : '',
                    color: team.name === club.name ? '#fff' : '',
                    fontWeight: team.name === club.name ? 'bold' : 'normal'
                  }}
                >
                  <td>{team.position}</td>
                  <td>{team.name}</td>
                  <td>{team.wins + team.draws + team.losses}</td>
                  <td>{team.wins}</td>
                  <td>{team.draws}</td>
                  <td>{team.losses}</td>
                  <td>{team.goalsFor}</td>
                  <td>{team.goalsAgainst}</td>
                  <td>{team.goalsFor - team.goalsAgainst}</td>
                  <td>{team.points}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className="fc-footerbar">
          <button className="fc-btn" onClick={() => setShowStandings(false)}>Geri</button>
        </div>
      </div>
    </div>
  );
};

StandingsModal.propTypes = {
  setShowStandings: PropTypes.func.isRequired,
  club: PropTypes.object.isRequired,
};

export default StandingsModal;
