import React from 'react';
import PropTypes from 'prop-types';
import { calculateWeeklyExpenses } from '../data';

const BudgetCalculationModal = ({ setShowBudgetCalculation, club, onWeekAdvance }) => {
  const weeklyExpenses = calculateWeeklyExpenses(club);
  
  // Haftalık gelir hesaplama (basit)
  const calculateWeeklyIncome = () => {
    let income = 0;
    
    // Stadyum geliri (basit hesaplama)
    const stadiumIncome = 5000; // Sabit gelir
    income += stadiumIncome;
    
    // Maç geliri (eğer bu hafta maç oynandıysa)
    if (club.matchResults && club.matchResults.length > 0) {
      const lastMatch = club.matchResults[club.matchResults.length - 1];
      if (lastMatch.week === club.gameTime.week) {
        const matchIncome = 10000; // Sabit maç geliri
        income += matchIncome;
      }
    }
    
    return income;
  };
  
  const weeklyIncome = calculateWeeklyIncome();
  const netIncome = weeklyIncome - weeklyExpenses;
  
  // Detaylı gider listesi
  const getExpenseBreakdown = () => {
    const breakdown = [];
    
    // Menajer maaşı
    if (club.manager && club.manager.salary) {
      breakdown.push({
        category: 'Menajer Maaşı',
        amount: club.manager.salary,
        description: `${club.manager.name} - ${club.manager.salary}₺`
      });
    }
    
    // Oyuncu maaşları
    if (club.squad) {
      const playerSalaries = club.squad.firstTeam.concat(club.squad.substitutes || [])
        .reduce((total, player) => total + (player.salary || 0), 0);
      
      breakdown.push({
        category: 'Oyuncu Maaşları',
        amount: playerSalaries,
        description: `${club.squad.firstTeam.length + (club.squad.substitutes?.length || 0)} oyuncu`
      });
    }
    
    // Tesis giderleri
    const facilityCosts = (club.antrenman + club.altyapi) * 100;
    breakdown.push({
      category: 'Tesis Giderleri',
      amount: facilityCosts,
      description: `Antrenman (${club.antrenman}) + Altyapı (${club.altyapi})`
    });
    
    return breakdown;
  };
  
  // Detaylı gelir listesi
  const getIncomeBreakdown = () => {
    const breakdown = [];
    
    // Stadyum geliri
    breakdown.push({
      category: 'Stadyum Geliri',
      amount: 5000,
      description: 'Haftalık stadyum geliri'
    });
    
    // Maç geliri
    if (club.matchResults && club.matchResults.length > 0) {
      const lastMatch = club.matchResults[club.matchResults.length - 1];
      if (lastMatch.week === club.gameTime.week) {
        breakdown.push({
          category: 'Maç Geliri',
          amount: 10000,
          description: `${lastMatch.awayTeam} maçı`
        });
      }
    }
    
    return breakdown;
  };
  
  const expenseBreakdown = getExpenseBreakdown();
  const incomeBreakdown = getIncomeBreakdown();
  
  const handleContinue = () => {
    setShowBudgetCalculation(false);
    onWeekAdvance();
  };
  
  return (
    <div className="fc-modal-backdrop">
      <div className="fc-modal" style={{ maxWidth: '700px', maxHeight: '90vh', overflow: 'auto' }}>
        <div style={{ 
          background: 'linear-gradient(135deg, #2c466b, #1a365d)',
          padding: '20px',
          borderRadius: '8px 8px 0 0',
          color: 'white',
          textAlign: 'center'
        }}>
          <div style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '10px' }}>
            HAFTA {club.gameTime.week} BÜTÇE HESAPLAMA
          </div>
          <div style={{ fontSize: '16px' }}>
            Gelir ve Gider Tablosu
          </div>
        </div>

        <div className="fc-panel" style={{ padding: '20px' }}>
          {/* Özet */}
          <div style={{ 
            background: '#f0f8ff',
            borderRadius: '8px',
            padding: '15px',
            marginBottom: '20px',
            border: '2px solid #2c466b'
          }}>
            <div style={{ 
              display: 'flex', 
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '10px'
            }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--blue)' }}>
                Haftalık Özet
              </div>
              <div style={{ 
                fontSize: '20px', 
                fontWeight: 'bold',
                color: netIncome >= 0 ? 'var(--win)' : 'var(--loss)'
              }}>
                {netIncome >= 0 ? '+' : ''}{netIncome}₺
              </div>
            </div>
            
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <div>
                <span style={{ color: 'var(--win)', fontWeight: 'bold' }}>Gelir: +{weeklyIncome}₺</span>
              </div>
              <div>
                <span style={{ color: 'var(--loss)', fontWeight: 'bold' }}>Gider: -{weeklyExpenses}₺</span>
              </div>
            </div>
          </div>
          
          {/* Gelir Detayları */}
          <div style={{ marginBottom: '20px' }}>
            <h4 style={{ 
              margin: '0 0 10px 0', 
              color: 'var(--win)',
              fontSize: '16px',
              borderBottom: '2px solid var(--win)',
              paddingBottom: '5px'
            }}>
              📈 GELİRLER
            </h4>
            
            <div style={{ 
              background: '#f0fff0',
              borderRadius: '8px',
              padding: '10px'
            }}>
              {incomeBreakdown.map((item, index) => (
                <div key={index} style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  padding: '8px 0',
                  borderBottom: index < incomeBreakdown.length - 1 ? '1px solid #e0e0e0' : 'none'
                }}>
                  <div>
                    <div style={{ fontWeight: 'bold', color: 'var(--win)' }}>
                      {item.category}
                    </div>
                    <div style={{ fontSize: '12px', color: '#666' }}>
                      {item.description}
                    </div>
                  </div>
                  <div style={{ 
                    fontWeight: 'bold',
                    color: 'var(--win)',
                    fontSize: '16px'
                  }}>
                    +{item.amount}₺
                  </div>
                </div>
              ))}
            </div>
          </div>
          
          {/* Gider Detayları */}
          <div style={{ marginBottom: '20px' }}>
            <h4 style={{ 
              margin: '0 0 10px 0', 
              color: 'var(--loss)',
              fontSize: '16px',
              borderBottom: '2px solid var(--loss)',
              paddingBottom: '5px'
            }}>
              📉 GİDERLER
            </h4>
            
            <div style={{ 
              background: '#fff0f0',
              borderRadius: '8px',
              padding: '10px'
            }}>
              {expenseBreakdown.map((item, index) => (
                <div key={index} style={{ 
                  display: 'flex', 
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  padding: '8px 0',
                  borderBottom: index < expenseBreakdown.length - 1 ? '1px solid #e0e0e0' : 'none'
                }}>
                  <div>
                    <div style={{ fontWeight: 'bold', color: 'var(--loss)' }}>
                      {item.category}
                    </div>
                    <div style={{ fontSize: '12px', color: '#666' }}>
                      {item.description}
                    </div>
                  </div>
                  <div style={{ 
                    fontWeight: 'bold',
                    color: 'var(--loss)',
                    fontSize: '16px'
                  }}>
                    -{item.amount}₺
                  </div>
                </div>
              ))}
            </div>
          </div>
          
          {/* Yeni Bakiye */}
          <div style={{ 
            background: '#f8f8f8',
            borderRadius: '8px',
            padding: '15px',
            textAlign: 'center',
            border: '2px solid #ddd'
          }}>
            <div style={{ fontSize: '14px', color: '#666', marginBottom: '5px' }}>
              Mevcut Bakiye
            </div>
            <div style={{ fontSize: '24px', fontWeight: 'bold', color: 'var(--blue)' }}>
              {club.money}₺
            </div>
            <div style={{ fontSize: '14px', color: '#666', marginTop: '5px' }}>
              Yeni Bakiye: {Math.max(0, club.money - weeklyExpenses)}₺
            </div>
          </div>
        </div>

        <div className="fc-footerbar">
          <button 
            className="fc-btn" 
            onClick={handleContinue}
            style={{ 
              background: 'var(--win)',
              fontSize: '16px',
              padding: '12px 24px'
            }}
          >
            Devam Et
          </button>
        </div>
      </div>
    </div>
  );
};

BudgetCalculationModal.propTypes = {
  setShowBudgetCalculation: PropTypes.func.isRequired,
  club: PropTypes.object.isRequired,
  onWeekAdvance: PropTypes.func.isRequired,
};

export default BudgetCalculationModal;
