const generateRealisticFixtures = teams => {
  const fixtures = [];
  const teamNames = teams.map(team => team.name);
  const numTeams = teamNames.length;

  // Çift sayılı takım için standart round-robin
  const totalWeeks = numTeams - 1;
  const matchesPerWeek = Math.floor(numTeams / 2);

  // Berger Table algoritması ile fikstür oluştur
  const teamsCopy = [...teamNames];

  // Tüm haftalar için fikstür oluştur
  for (let round = 0; round < totalWeeks; round++) {
    for (let i = 0; i < matchesPerWeek; i++) {
      const homeIndex = i;
      const awayIndex = numTeams - 1 - i;
      const homeTeam = teamsCopy[homeIndex];
      const awayTeam = teamsCopy[awayIndex];
      fixtures.push({
        homeTeam: homeTeam,
        awayTeam: awayTeam,
        week: round + 1,
        result: null,
        played: false
      });
    }

    // Takımları döndür (Berger Table) - sadece 1. pozisyondan sonraki takımları döndür
    const lastTeam = teamsCopy.pop();
    teamsCopy.splice(1, 0, lastTeam);
  }

  // İkinci yarı için roller değişti
  const teamsCopy2 = [...teamNames];
  for (let round = 0; round < totalWeeks; round++) {
    for (let i = 0; i < matchesPerWeek; i++) {
      const homeIndex = i;
      const awayIndex = numTeams - 1 - i;

      // Roller değişti - ikinci yarıda
      const homeTeam = teamsCopy2[awayIndex];
      const awayTeam = teamsCopy2[homeIndex];
      fixtures.push({
        homeTeam: homeTeam,
        awayTeam: awayTeam,
        week: round + totalWeeks + 1,
        result: null,
        played: false
      });
    }

    // Takımları döndür
    const lastTeam = teamsCopy2.pop();
    teamsCopy2.splice(1, 0, lastTeam);
  }

  // Maçları haftaya göre sırala ve home/away dengesini iyileştir
  const sortedFixtures = fixtures.sort((a, b) => a.week - b.week);

  // Home/away dengesini iyileştirmek için bazı maçları yeniden düzenle
  const balancedFixtures = [];
  const teamHomeCounts = {};
  teamNames.forEach(team => teamHomeCounts[team] = 0);

  // Her takımın haftalık maçlarını kontrol et ve dengesizlikleri düzelt
  for (let week = 1; week <= totalWeeks * 2; week++) {
    const weekFixtures = sortedFixtures.filter(f => f.week === week);
    weekFixtures.forEach(fixture => {
      // Eğer bir takım çok fazla ev sahibi olmuşsa, roller değiştir
      if (teamHomeCounts[fixture.homeTeam] > teamHomeCounts[fixture.awayTeam] + 2) {
        // Roller değiştir
        const temp = fixture.homeTeam;
        fixture.homeTeam = fixture.awayTeam;
        fixture.awayTeam = temp;
      }
      teamHomeCounts[fixture.homeTeam]++;
      balancedFixtures.push(fixture);
    });
  }
  return balancedFixtures;
};

