import React, { useState } from 'react';
import TrainingMenu from './TrainingMenu';
import { club as initialClub } from './data';
import './styles.css';
import FinanceModal from './components/FinanceModal';
import StandingsModal from './components/StandingsModal';
import FixtureModal from './components/FixtureModal';

const getResultColor = (result) => {
  if (result === 'M') return 'var(--loss)';
  if (result === 'G') return 'var(--win)';
  if (result === 'B') return 'var(--draw)';
  return '#ccc';
};

function App() {
  const [club] = useState(initialClub);
  const [showFixture, setShowFixture] = useState(false);
  const [showStandings, setShowStandings] = useState(false);
  const [showFinance, setShowFinance] = useState(false);
  const [showTrainingMenu, setShowTrainingMenu] = useState(false);

  return (
    <div className="fc-container">
      <div className="fc-header">
        <div className="fc-title">FOOTBALL CHAIRMAN</div>
        <div className="fc-ribbon">{club.name}</div>
        <div className="fc-sub">{club.season} &nbsp;|&nbsp; {club.league} &nbsp;|&nbsp; {club.date}</div>
      </div>

      <div className="fc-grid">
        <button className="fc-card white" onClick={() => setShowFixture(true)}>
          HF SONU<span className="big">{club.hfSonu}</span>
        </button>
        <button className="fc-card white" onClick={() => setShowStandings(true)}>
          Lƒ∞G<span className="big">{club.lig}.</span>
        </button>
        <button className="fc-card white" onClick={() => setShowFinance(true)}>
          PARA<span className="big" style={{ color: 'var(--win)' }}>{new Intl.NumberFormat('tr-TR', { style: 'currency', currency: 'TRY', maximumFractionDigits: 0 }).format(club.money)}</span>
        </button>
      </div>

      {showFixture && <FixtureModal setShowFixture={setShowFixture} />}
      {showStandings && <StandingsModal setShowStandings={setShowStandings} />}
      {showFinance && <FinanceModal setShowFinance={setShowFinance} club={club} />}

      <div className="fc-grid" style={{ marginTop: '10px' }}>
        <div className="fc-card navy">KADRO<br/>{club.kadro}</div>
        <div className="fc-card navy">MENAJER<br/>{club.menajer}</div>
        <div className="fc-card navy">ANTRENMAN<br/>{club.antrenman}</div>
        <div className="fc-card navy">ALTYAPI<br/>{club.altyapi}</div>
      </div>

      <div className="fc-grid" style={{ marginTop: '10px' }}>
        <div className="fc-card green">KUPALAR<br/>{club.kupalar}</div>
        <div className="fc-card green">BA≈ûARILDI<br/>{club.basarildi}</div>
        <div className="fc-card green">REKORLAR<br/><span style={{fontSize: '1.2em'}}>üèÜ</span></div>
        <div className="fc-card green">GE√áMƒ∞≈û<br/><span style={{fontSize: '1.2em'}}>üìú</span></div>
      </div>

      <div className="fc-grid" style={{ marginTop: '10px' }}>
        <div className="fc-card light">TARAFTARLAR MUTLU MU?<br/><span style={{fontSize: '1.2em'}}>{club.taraftarMutlu}% MENAJER<br/>{club.baskanMutlu}% BA≈ûKAN</span></div>
        <div className="fc-card light">STADYUM<br/><span style={{fontSize: '1.2em'}}>üèüÔ∏è</span></div>
        <div className="fc-card light">BA≈ûKAN<br/><span style={{fontSize: '1.2em'}}>üë§</span></div>
      </div>

      <div className="fc-dots">
        {club.matchResults.map((result, idx) => (
          <div key={idx} className="fc-dot" style={{ background: getResultColor(result) }}>{result}</div>
        ))}
      </div>

      <div className="fc-next">
        {club.nextMatch}
      </div>

      <div style={{display: 'flex', justifyContent: 'center', marginTop: '20px'}}>
        <button
          className="fc-btn"
          onClick={() => setShowTrainingMenu(true)}
        >
          ANTRENMAN
        </button>

        {showTrainingMenu && (
          <div className="fc-modal-backdrop">
            <TrainingMenu />
            <button
              className="fc-btn"
              style={{ position: 'absolute', top: '20px', right: '20px', background: 'red' }}
              onClick={() => setShowTrainingMenu(false)}
            >
              Kapat
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
