              <button onClick={()=>setTrainingTarget(Math.max(1, trainingTarget-1))} style={{background:'#c0392b', color:'#fff', border:'none', borderRadius:24, padding:'10px 14px', fontWeight:'bold', cursor:'pointer'}}>&lt; 1</button>
              <div style={{fontSize:'2em', fontWeight:'bold'}}>{trainingTarget}</div>
              <button onClick={()=>setTrainingTarget(Math.min(99, trainingTarget+1))} style={{background:'#2c7b2c', color:'#fff', border:'none', borderRadius:24, padding:'10px 14px', fontWeight:'bold', cursor:'pointer'}}>1 &gt;</button>
              <button onClick={()=>setTrainingTarget(Math.min(99, trainingTarget+5))} style={{background:'#2c7b2c', color:'#fff', border:'none', borderRadius:24, padding:'10px 14px', fontWeight:'bold', cursor:'pointer'}}>5 &gt;&gt;</button>
            </div>
            <hr style={{border:'none', borderTop:'1px solid #ddd'}}/>
            {(() => { const cost = calcUpgradeCost(trainingRating, trainingTarget); const newWeekly = calcWeeklyCost(trainingTarget); return (
              <div style={{color:'#222'}}>
                <div style={{display:'flex', justifyContent:'space-between', margin:'8px 0'}}>
                  <span>Masraf</span>
                  <span style={{fontWeight:'bold'}}>{formatTRY(cost)}</span>
                </div>
                <div style={{display:'flex', justifyContent:'space-between', margin:'8px 0'}}>
                  <span>Yeni haftalık maliyet</span>
                  <span style={{fontWeight:'bold'}}>{formatTRY(newWeekly)}</span>
                </div>
                <div style={{display:'flex', justifyContent:'space-between', margin:'8px 0'}}>
                  <span>Banka Bakiyesi</span>
                  <span style={{fontWeight:'bold'}}>{formatTRY(bankBalance - cost)}</span>
                </div>
              </div>
            ); })()}
            <div style={{display:'flex', justifyContent:'space-between', marginTop:16}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowTraining(false)}>Geri</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={() => {
                const cost = calcUpgradeCost(trainingRating, trainingTarget);
                if (trainingTarget <= trainingRating) { setShowInfo({title:'Bilgi', message:'Hedef seviye mevcut seviyeden yüksek olmalı.'}); return; }
                if (bankBalance < cost) { setShowInfo({title:'Üzgünüz!', message:'Yetersiz bakiye.'}); return; }
                setTrainingRating(trainingTarget);
                setBankBalance(b => b - cost);
                setShowInfo({ title:'Yükseltme Başarılı', message:`Antrenman seviyesi ${trainingTarget} oldu.`});
                setShowTraining(false);
              }}>Yükselt</button>
            </div>
          </div>
        </div>
      )}

      {/* Youth (Altyapı) Modal */}
      {showYouth && (
        <div style={{position: 'fixed', inset: 0, background: 'rgba(44,70,107,0.95)', zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
          <div style={{background: 'rgba(255,255,255,0.95)', color: '#2c466b', borderRadius: 18, padding: 20, minWidth: 360, maxWidth: '95vw', maxHeight: '85vh', overflowY: 'auto', boxShadow: '0 2px 16px rgba(0,0,0,0.2)'}}>
            <div style={{display:'flex', gap:16, alignItems:'center', marginBottom:12}}>
              <div style={{width:96,height:96,borderRadius:8,background:'#e6f4e6',display:'flex',alignItems:'center',justifyContent:'center'}}>
                <span style={{fontSize:'3em'}}>📋</span>
              </div>
              <div style={{flex:1}}>
                <div style={{fontSize:'1.2em', fontWeight:'bold'}}>ALTYAPI ANTRENÖRLÜĞÜ</div>
                <div style={{display:'flex', gap:8, alignItems:'center', marginTop:6}}>
                  <div style={{background:'#2c466b', color:'#bfe3bf', borderRadius:4, padding:'2px 6px', fontWeight:'bold'}}>REYTING</div>
                  <div style={{background:'#bfe3bf', color:'#000', borderRadius:4, padding:'2px 6px', fontWeight:'bold'}}>{youthRating}</div>
                </div>
                <div style={{color:'#333', marginTop:6}}>Haftalık ücret: {formatTRY(calcWeeklyCostYouth(youthRating))}</div>
              </div>
            </div>
            <hr style={{border:'none', borderTop:'1px solid #ddd'}}/>
            <div style={{display:'flex', alignItems:'center', justifyContent:'space-between', margin:'12px 0'}}>
              <button onClick={()=>setYouthTarget(Math.max(1, youthTarget-5))} style={{background:'#c0392b', color:'#fff', border:'none', borderRadius:24, padding:'10px 14px', fontWeight:'bold', cursor:'pointer'}}>&lt;&lt; 5</button>
              <button onClick={()=>setYouthTarget(Math.max(1, youthTarget-1))} style={{background:'#c0392b', color:'#fff', border:'none', borderRadius:24, padding:'10px 14px', fontWeight:'bold', cursor:'pointer'}}>&lt; 1</button>
              <div style={{fontSize:'2em', fontWeight:'bold'}}>{youthTarget}</div>
              <button onClick={()=>setYouthTarget(Math.min(99, youthTarget+1))} style={{background:'#2c7b2c', color:'#fff', border:'none', borderRadius:24, padding:'10px 14px', fontWeight:'bold', cursor:'pointer'}}>1 &gt;</button>
              <button onClick={()=>setYouthTarget(Math.min(99, youthTarget+5))} style={{background:'#2c7b2c', color:'#fff', border:'none', borderRadius:24, padding:'10px 14px', fontWeight:'bold', cursor:'pointer'}}>5 &gt;&gt;</button>
            </div>
            <hr style={{border:'none', borderTop:'1px solid #ddd'}}/>
            {(() => { const cost = calcUpgradeCostYouth(youthRating, youthTarget); const newWeekly = calcWeeklyCostYouth(youthTarget); return (
              <div style={{color:'#222'}}>
                <div style={{display:'flex', justifyContent:'space-between', margin:'8px 0'}}>
                  <span>Masraf</span>
                  <span style={{fontWeight:'bold'}}>{formatTRY(cost)}</span>
                </div>
                <div style={{display:'flex', justifyContent:'space-between', margin:'8px 0'}}>
                  <span>Yeni haftalık maliyet</span>
                  <span style={{fontWeight:'bold'}}>{formatTRY(newWeekly)}</span>
                </div>
                <div style={{display:'flex', justifyContent:'space-between', margin:'8px 0'}}>
                  <span>Banka Bakiyesi</span>
                  <span style={{fontWeight:'bold'}}>{formatTRY(bankBalance - cost)}</span>
                </div>
              </div>
            ); })()}
            <div style={{display:'flex', justifyContent:'space-between', marginTop:16}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowYouth(false)}>Geri</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={() => {
                const cost = calcUpgradeCostYouth(youthRating, youthTarget);
                if (youthTarget <= youthRating) { setShowInfo({title:'Bilgi', message:'Hedef seviye mevcut seviyeden yüksek olmalı.'}); return; }
                if (bankBalance < cost) { setShowInfo({title:'Üzgünüz!', message:'Yetersiz bakiye.'}); return; }
                setYouthRating(youthTarget);
                setBankBalance(b => b - cost);
                setShowInfo({ title:'Yükseltme Başarılı', message:`Altyapı seviyesi ${youthTarget} oldu.`});
                setShowYouth(false);
              }}>Yükselt</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={() => { setShowYouth(false); setShowSquad(true); }}>Kadro</button>
            </div>
          </div>
        </div>
      )}

      {/* Trophies Modal */}
      {showTrophies && (
        <div style={{position:'fixed', inset:0, background:'rgba(44,70,107,0.95)', zIndex:1000, display:'flex', alignItems:'center', justifyContent:'center'}}>
          <div style={{background:'rgba(255,255,255,0.95)', color:'#2c466b', borderRadius:18, padding:20, minWidth:360, maxWidth:'95vw', maxHeight:'85vh', overflowY:'auto', boxShadow:'0 2px 16px rgba(0,0,0,0.2)'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:12}}>KUPALAR</div>
            {trophies.length === 0 ? (
              <div style={{background:'#fff', borderRadius:12, padding:16, boxShadow:'0 1px 6px rgba(0,0,0,0.08)'}}>
                <div style={{fontSize:'1.1em', fontWeight:'bold', marginBottom:6}}>HENÜZ KUPA YOK!</div>
                <div style={{color:'#333'}}>Bu sayfa kulübünün kupalarını gösterecek.</div>
                <div style={{color:'#333', marginTop:8}}>Ama şimdilik bu bölüm boş... bir şey kazandığında geri gel!</div>
              </div>
            ) : (
              <div>
                {trophies.map((t, idx) => (
                  <div key={idx} style={{display:'flex', alignItems:'center', gap:10, background:'#fff', borderRadius:12, padding:12, marginBottom:8, boxShadow:'0 1px 6px rgba(0,0,0,0.06)'}}>
                    <div style={{fontSize:'1.6em'}}>🏆</div>
                    <div style={{flex:1}}>
                      <div style={{fontWeight:'bold'}}>{t.name}</div>
                      <div style={{color:'#333'}}>{t.season}</div>
                      {t.note && <div style={{color:'#555'}}>{t.note}</div>}
                    </div>
                  </div>
                ))}
              </div>
            )}
            <div style={{display:'flex', justifyContent:'space-between', marginTop:16}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowTrophies(false)}>Geri</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Mevcut kulüp detayları yakında.'})}>Mevcut Kulüp</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Başkan ekranı yakında.'})}>Başkan</button>
            </div>
          </div>
        </div>
      )}

      {/* Achievements Modal */}
      {showAchievements && (
        <div style={{position:'fixed', inset:0, background:'rgba(44,70,107,0.95)', zIndex:1000, display:'flex', alignItems:'center', justifyContent:'center'}}>
          <div style={{background:'rgba(255,255,255,0.95)', color:'#2c466b', borderRadius:18, padding:20, minWidth:360, maxWidth:'95vw', maxHeight:'85vh', overflowY:'auto', boxShadow:'0 2px 16px rgba(0,0,0,0.2)'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:12}}>BAŞARIMLAR ({achievements.filter(a=>a.achieved).length}/{achievements.length})</div>
            <table style={{width:'100%', borderCollapse:'separate', borderSpacing:0}}>
              <thead>
                <tr>
                  <th style={{textAlign:'left', padding:'8px', color:'#2c466b'}}>Başarım</th>
                  <th style={{textAlign:'right', padding:'8px', color:'#2c466b'}}>Tarih</th>
                </tr>
              </thead>
              <tbody>
                {achievements.map((ac, idx) => (
                  <tr key={idx} style={{background:'#fff', boxShadow:'0 1px 4px rgba(0,0,0,0.06)'}}>
                    <td style={{padding:'10px 8px', borderBottom:'8px solid transparent', fontWeight: ac.achieved ? 'bold' : 'normal', color: ac.achieved ? '#000' : '#777'}}>
                      <span style={{marginRight:8}}>{ac.achieved ? '⭐' : '☆'}</span>
                      {ac.title}
                    </td>
                    <td style={{padding:'10px 8px', textAlign:'right', borderBottom:'8px solid transparent', color:'#000'}}>{ac.achieved ? ac.date : '-'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div style={{display:'flex', justifyContent:'space-between', marginTop:16}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowAchievements(false)}>Geri</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Mevcut kulüp detayları yakında.'})}>Mevcut Kulüp</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Başkan ekranı yakında.'})}>Başkan</button>
            </div>
          </div>
        </div>
      )}

      {/* Records Modal */}
      {showRecords && (
        <div style={{position:'fixed', inset:0, background:'rgba(44,70,107,0.95)', zIndex:1000, display:'flex', alignItems:'center', justifyContent:'center'}}>
          <div style={{background:'rgba(255,255,255,0.95)', color:'#2c466b', borderRadius:18, padding:20, minWidth:360, maxWidth:'95vw', maxHeight:'85vh', overflowY:'auto', boxShadow:'0 2px 16px rgba(0,0,0,0.2)'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:12}}>KULÜP REKORLAR</div>
            <div>
              {records.map((r, idx) => (
                <div key={idx} style={{display:'flex', gap:12, alignItems:'center', background:'#fff', color:'#2c466b', borderRadius:12, padding:12, marginBottom:10, boxShadow:'0 1px 6px rgba(0,0,0,0.06)'}}>
                  <div style={{width:56, height:56, borderRadius:8, background:'#e6f4e6', display:'flex', alignItems:'center', justifyContent:'center', fontSize:'1.6em'}}>{r.icon}</div>
                  <div style={{flex:1}}>
                    <div style={{fontWeight:'bold'}}>{r.title}</div>
                    <div style={{display:'inline-block', background:'#2c7b2c', color:'#fff', borderRadius:6, padding:'2px 10px', fontWeight:'bold', margin:'6px 0'}}>{r.value}</div>
                    <div style={{color:'#333'}}>{r.details}</div>
                  </div>
                </div>
              ))}
            </div>
            <div style={{display:'flex', justifyContent:'space-between', marginTop:16}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowRecords(false)}>Geri</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Mevcut kulüp detayları yakında.'})}>Mevcut Kulüp</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Başkan ekranı yakında.'})}>Başkan</button>
            </div>
          </div>
        </div>
      )}
      <div style={{display: 'flex', justifyContent: 'center', flexWrap: 'wrap', gap: '10px', marginTop: '10px'}}>
        <button style={{...cardStyle, background: '#3e5c2c', color: '#fff', cursor: 'pointer'}} onClick={() => setShowTrophies(true)}>KUPALAR<br/>{trophies.length}</button>
        <button style={{...cardStyle, background: '#3e5c2c', color: '#fff', cursor: 'pointer'}} onClick={() => setShowAchievements(true)}>BAŞARIMLAR<br/>{achievements.filter(a=>a.achieved).length}/{achievements.length}</button>
        <button style={{...cardStyle, background: '#3e5c2c', color: '#fff', cursor: 'pointer'}} onClick={() => setShowRecords(true)}>REKORLAR<br/><span style={{fontSize: '1.2em'}}>📈</span></button>
        <div style={{...cardStyle, background: '#3e5c2c', color: '#fff'}}>GEÇMİŞ<br/><span style={{fontSize: '1.2em'}}>📜</span></div>
      </div>
      <div style={{display: 'flex', justifyContent: 'center', flexWrap: 'wrap', gap: '10px', marginTop: '10px'}}>
        <div style={{...cardStyle, background: '#e6f4e6', color: '#2c466b'}}>TARAFTARLAR MUTLU MU?<br/><span style={{fontSize: '1.2em'}}>{club.taraftarMutlu}% MENAJER<br/>{club.baskanMutlu}% BAŞKAN</span></div>
        <div style={{...cardStyle, background: '#e6f4e6', color: '#2c466b'}}>STADYUM<br/><span style={{fontSize: '1.2em'}}>🏟️</span></div>
        <div style={{...cardStyle, background: '#e6f4e6', color: '#2c466b'}}>BAŞKAN<br/><span style={{fontSize: '1.2em'}}>👔</span></div>
      </div>
      <div style={{display: 'flex', justifyContent: 'center', gap: '10px', marginTop: '20px'}}>
        {club.matchResults.map((result, idx) => {
          let bg = '#b88c2c';
          if (result === 'M') bg = '#c00';
          if (result === 'G') bg = '#2c7b2c';
          return (
            <div key={idx} style={{width: '30px', height: '30px', borderRadius: '50%', background: bg, display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#fff', fontWeight: 'bold'}}>{result}</div>
          );
        })}
      </div>
      <div style={{margin: '30px auto 0 auto', background: '#3e5c2c', color: '#fff', borderRadius: '6px', padding: '10px 0', textAlign: 'center', maxWidth: '300px', fontWeight: 'bold', fontSize: '1.1em'}}>
        {club.nextMatch}
      </div>
      <div style={{display:'flex', justifyContent:'center', gap:'10px', marginTop:'10px'}}>
        <button style={{...cardStyle, background:'#3e5c2c', color:'#fff', cursor:'pointer'}} onClick={()=>setShowHistory(true)}>GEÇMİŞ<br/><span style={{fontSize:'1.2em'}}>📜</span></button>
      </div>
    </div>
  );
}

export default App;
