import React, { useState } from 'react';
import PropTypes from 'prop-types';

const ManagerDetailModal = ({ setShowManagerDetail, selectedManager, club }) => {
  const [activeTab, setActiveTab] = useState('records'); // 'records' or 'staff'
  
  // Örnek menajer rekorları (gerçek uygulamada bu veriler dinamik olacak)
  const managerRecords = [
    {
      name: selectedManager?.name || 'E FRIAS',
      avatar: selectedManager?.name?.split(' ')[0][0] || 'E',
      mbp: '1.67',
      cups: 0,
      period: 'Nis 2025 -',
      stats: { played: 12, won: 6, drawn: 2, lost: 4 },
      status: 'current'
    },
    {
      name: 'A POMBO',
      avatar: 'A',
      mbp: '1.64',
      cups: 0,
      period: 'Tem 2023 - Nis 2025',
      stats: { played: 90, won: 42, drawn: 22, lost: 26 },
      status: 'fired'
    }
  ];

  // Teknik kadro verileri
  const technicalStaff = {
    assistant: [
      { name: 'M Asistan', avatar: 'M', filled: true },
      { name: 'Boş', avatar: '', filled: false }
    ],
    physio: [
      { name: 'K Fizyoterapist', avatar: 'K', filled: true },
      { name: 'Boş', avatar: '', filled: false }
    ],
    coach: [
      { name: 'A Antrenör', avatar: 'A', filled: true },
      { name: 'B Antrenör', avatar: 'B', filled: true },
      { name: 'Boş', avatar: '', filled: false },
      { name: 'Boş', avatar: '', filled: false }
    ],
    scout: [
      { name: 'Y Yetenek Avcısı', avatar: 'Y', filled: true },
      { name: 'Z Yetenek Avcısı', avatar: 'Z', filled: true },
      { name: 'Boş', avatar: '', filled: false },
      { name: 'Boş', avatar: '', filled: false }
    ]
  };

  const renderManagerProfile = () => (
    <div style={{ 
      display: 'flex', 
      alignItems: 'center', 
      gap: '15px',
      padding: '15px',
      background: '#f5f5f5',
      borderRadius: '8px',
      marginBottom: '20px'
    }}>
      {/* Avatar */}
      <div style={{
        width: '60px',
        height: '60px',
        borderRadius: '50%',
        background: '#4CAF50',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontSize: '24px',
        fontWeight: 'bold',
        color: '#fff'
      }}>
        {selectedManager?.name?.split(' ')[0][0] || 'E'}
      </div>
      
      {/* Manager Info */}
      <div style={{ flex: 1 }}>
        <div style={{ 
          fontWeight: 'bold', 
          fontSize: '20px',
          marginBottom: '10px'
        }}>
          {selectedManager?.name || 'E FRIAS'}
        </div>
        
        {/* Attributes */}
        <div style={{ display: 'flex', gap: '8px', marginBottom: '10px' }}>
          <span style={{
            background: '#4CAF50',
            color: '#fff',
            padding: '4px 8px',
            borderRadius: '4px',
            fontSize: '14px',
            fontWeight: 'bold'
          }}>
            M {selectedManager?.management || 37}
          </span>
          <span style={{
            background: '#4CAF50',
            color: '#fff',
            padding: '4px 8px',
            borderRadius: '4px',
            fontSize: '14px',
            fontWeight: 'bold'
          }}>
            A {selectedManager?.attacking || 37}
          </span>
          <span style={{
            background: '#4CAF50',
            color: '#fff',
            padding: '4px 8px',
            borderRadius: '4px',
            fontSize: '14px',
            fontWeight: 'bold'
          }}>
            T {selectedManager?.tactical || 38}
          </span>
        </div>
        
        {/* Tactic & Status */}
        <div style={{ fontSize: '16px', marginBottom: '5px' }}>
          {selectedManager?.tactic || '4-3-3 Pas Oyunu'}
        </div>
        <div style={{ fontSize: '16px', marginBottom: '5px' }}>
          86% mutlu
        </div>
        <div style={{ fontSize: '16px' }}>
          Ücret: €69 hftlk
        </div>
      </div>
    </div>
  );

  const renderTabs = () => (
    <div style={{ display: 'flex', marginBottom: '20px' }}>
      <button
        onClick={() => setActiveTab('records')}
        style={{
          flex: 1,
          padding: '10px',
          background: activeTab === 'records' ? 'var(--win)' : '#fff',
          color: activeTab === 'records' ? '#fff' : '#666',
          border: '1px solid #ddd',
          cursor: 'pointer',
          fontSize: '14px',
          fontWeight: 'bold'
        }}
      >
        Menajer Rekorları
      </button>
      <button
        onClick={() => setActiveTab('staff')}
        style={{
          flex: 1,
          padding: '10px',
          background: activeTab === 'staff' ? 'var(--win)' : '#fff',
          color: activeTab === 'staff' ? '#fff' : '#666',
          border: '1px solid #ddd',
          cursor: 'pointer',
          fontSize: '14px',
          fontWeight: 'bold'
        }}
      >
        Teknik Kadro
      </button>
    </div>
  );

  const renderManagerRecords = () => (
    <div>
      {managerRecords.map((record, index) => (
        <div key={index} style={{
          display: 'flex',
          alignItems: 'center',
          gap: '15px',
          padding: '15px',
          background: '#fff',
          borderRadius: '8px',
          marginBottom: '10px',
          border: '1px solid #ddd'
        }}>
          {/* Avatar */}
          <div style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            background: record.status === 'current' ? '#4CAF50' : '#666',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '16px',
            fontWeight: 'bold',
            color: '#fff'
          }}>
            {record.avatar}
          </div>
          
          {/* Record Info */}
          <div style={{ flex: 1 }}>
            <div style={{ 
              fontWeight: 'bold', 
              fontSize: '16px',
              marginBottom: '5px'
            }}>
              {record.name} - {record.mbp} MBP, {record.cups} kupalar
            </div>
            <div style={{ fontSize: '14px', marginBottom: '5px' }}>
              {record.period} {record.status === 'fired' ? '(Kovuldu)' : ''}
            </div>
            <div style={{ fontSize: '14px' }}>
              O {record.stats.played} G {record.stats.won} B {record.stats.drawn} M {record.stats.lost}
            </div>
          </div>
        </div>
      ))}
    </div>
  );

  const renderTechnicalStaff = () => (
    <div>
      {/* Asistan */}
      <div style={{ marginBottom: '20px' }}>
        <h4 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>Asistan</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
          {technicalStaff.assistant.map((staff, index) => (
            <div key={index} style={{
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              padding: '10px',
              background: staff.filled ? '#f0f8ff' : '#f5f5f5',
              borderRadius: '8px',
              border: '1px solid #ddd'
            }}>
              <div style={{
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                background: staff.filled ? '#4CAF50' : '#ccc',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
                fontWeight: 'bold',
                color: '#fff'
              }}>
                {staff.avatar || '?'}
              </div>
              <span style={{ fontSize: '12px' }}>{staff.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Fizyoterapist */}
      <div style={{ marginBottom: '20px' }}>
        <h4 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>Fizyoterapist</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
          {technicalStaff.physio.map((staff, index) => (
            <div key={index} style={{
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              padding: '10px',
              background: staff.filled ? '#f0f8ff' : '#f5f5f5',
              borderRadius: '8px',
              border: '1px solid #ddd'
            }}>
              <div style={{
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                background: staff.filled ? '#4CAF50' : '#ccc',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
                fontWeight: 'bold',
                color: '#fff'
              }}>
                {staff.avatar || '?'}
              </div>
              <span style={{ fontSize: '12px' }}>{staff.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Antrenör */}
      <div style={{ marginBottom: '20px' }}>
        <h4 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>Antrenör</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
          {technicalStaff.coach.map((staff, index) => (
            <div key={index} style={{
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              padding: '10px',
              background: staff.filled ? '#f0f8ff' : '#f5f5f5',
              borderRadius: '8px',
              border: '1px solid #ddd'
            }}>
              <div style={{
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                background: staff.filled ? '#4CAF50' : '#ccc',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
                fontWeight: 'bold',
                color: '#fff'
              }}>
                {staff.avatar || '?'}
              </div>
              <span style={{ fontSize: '12px' }}>{staff.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Yetenek Avcısı */}
      <div style={{ marginBottom: '20px' }}>
        <h4 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>Yetenek Avcısı</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
          {technicalStaff.scout.map((staff, index) => (
            <div key={index} style={{
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              padding: '10px',
              background: staff.filled ? '#f0f8ff' : '#f5f5f5',
              borderRadius: '8px',
              border: '1px solid #ddd'
            }}>
              <div style={{
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                background: staff.filled ? '#4CAF50' : '#ccc',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
                fontWeight: 'bold',
                color: '#fff'
              }}>
                {staff.avatar || '?'}
              </div>
              <span style={{ fontSize: '12px' }}>{staff.name}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  return (
    <div className="fc-modal-backdrop">
      <div className="fc-modal" style={{ maxWidth: '600px', maxHeight: '90vh', overflow: 'auto' }}>
        <h3>MENAJRLR</h3>
        
        {renderManagerProfile()}
        {renderTabs()}
        
        <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
          {activeTab === 'records' ? renderManagerRecords() : renderTechnicalStaff()}
        </div>
        
        <div className="fc-footerbar" style={{ marginTop: '20px' }}>
          <button className="fc-btn" onClick={() => setShowManagerDetail(false)}>
            Geri
          </button>
          <button className="fc-btn" style={{ background: 'var(--loss)' }}>
            Menajeri Kov
          </button>
          <button className="fc-btn" style={{ background: 'var(--green)' }}>
            Diğer Mnjrlr
          </button>
        </div>
      </div>
    </div>
  );
};

ManagerDetailModal.propTypes = {
  setShowManagerDetail: PropTypes.func.isRequired,
  selectedManager: PropTypes.object,
  club: PropTypes.object.isRequired,
};

export default ManagerDetailModal;
