import React, { useState } from 'react';
import TrainingMenu from './TrainingMenu';
import { createClubData } from './data';
import './styles.css';
import FinanceModal from './components/FinanceModal';
import StandingsModal from './components/StandingsModal';
import FixtureModal from './components/FixtureModal';
import SquadModal from './components/SquadModal';
import TeamSelection from './components/TeamSelection';
import ManagerSelectionModal from './components/ManagerSelectionModal';

const getResultColor = (result) => {
  if (result === 'M') return 'var(--loss)';
  if (result === 'G') return 'var(--win)';
  if (result === 'B') return 'var(--draw)';
  return '#ccc';
};

function App() {
  const [club, setClub] = useState(null);
  const [showFixture, setShowFixture] = useState(false);
  const [showStandings, setShowStandings] = useState(false);
  const [showFinance, setShowFinance] = useState(false);
  const [showSquad, setShowSquad] = useState(false);
  const [showTrainingMenu, setShowTrainingMenu] = useState(false);
  const [showTeamSelection, setShowTeamSelection] = useState(true);

  const handleTeamSelect = (selectedTeam, selectedLeague) => {
    const newClubData = createClubData(selectedTeam, selectedLeague);
    setClub(newClubData);
    setShowTeamSelection(false);
  };

  // Takım seçilmemişse seçim ekranını göster
  if (showTeamSelection || !club) {
    return <TeamSelection onTeamSelect={handleTeamSelect} />;
  }

  return (
    <div className="fc-container">
      <div className="fc-header">
        <div className="fc-title">FOOTBALL CHAIRMAN</div>
        <div className="fc-ribbon">{club.name}</div>
        <div className="fc-sub">{club.season} &nbsp;|&nbsp; {club.league} &nbsp;|&nbsp; {club.date}</div>
      </div>

      <div className="fc-grid">
        <button className="fc-card white" onClick={() => setShowFixture(true)}>
          HF SONU<span className="big">{club.hfSonu}</span>
        </button>
        <button className="fc-card white" onClick={() => setShowStandings(true)}>
          LİG<span className="big">{club.lig}.</span>
        </button>
        <button className="fc-card white" onClick={() => setShowFinance(true)}>
          PARA<span className="big" style={{ color: 'var(--win)' }}>{new Intl.NumberFormat('tr-TR', { style: 'currency', currency: 'TRY', maximumFractionDigits: 0 }).format(club.money)}</span>
        </button>
      </div>

      {showFixture && <FixtureModal setShowFixture={setShowFixture} club={club} />}
      {showStandings && <StandingsModal setShowStandings={setShowStandings} club={club} />}
      {showFinance && <FinanceModal setShowFinance={setShowFinance} club={club} />}
      {showSquad && <SquadModal setShowSquad={setShowSquad} club={club} />}

      <div className="fc-grid" style={{ marginTop: '10px' }}>
        <button className="fc-card navy" onClick={() => setShowSquad(true)} style={{ cursor: 'pointer' }}>
          KADRO<br/>{club.kadro}
        </button>
        <div className="fc-card navy">MENAJER<br/>{club.menajer}</div>
        <div className="fc-card navy">ANTRENMAN<br/>{club.antrenman}</div>
        <div className="fc-card navy">ALTYAPI<br/>{club.altyapi}</div>
      </div>

      <div className="fc-grid" style={{ marginTop: '10px' }}>
        <div className="fc-card green">KUPALAR<br/>{club.kupalar}</div>
        <div className="fc-card green">BAŞARILDI<br/>{club.basarildi}</div>
        <div className="fc-card green">REKORLAR<br/><span style={{fontSize: '1.2em'}}>🏆</span></div>
        <div className="fc-card green">GEÇMİŞ<br/><span style={{fontSize: '1.2em'}}>📜</span></div>
      </div>

      <div className="fc-grid" style={{ marginTop: '10px' }}>
        <div className="fc-card light">TARAFTARLAR MUTLU MU?<br/><span style={{fontSize: '1.2em'}}>{club.taraftarMutlu}% MENAJER<br/>{club.baskanMutlu}% BAŞKAN</span></div>
        <div className="fc-card light">STADYUM<br/><span style={{fontSize: '1.2em'}}>🏟️</span></div>
        <div className="fc-card light">BAŞKAN<br/><span style={{fontSize: '1.2em'}}>👤</span></div>
      </div>

      <div className="fc-dots">
        {club.matchResults.length > 0 ? (
          club.matchResults.map((result, idx) => (
            <div key={idx} className="fc-dot" style={{ background: getResultColor(result) }}>{result}</div>
          ))
        ) : (
          <div style={{ textAlign: 'center', color: '#ccc', fontSize: '14px' }}>
            Henüz maç oynanmadı
          </div>
        )}
      </div>

      <div className="fc-next">
        {club.nextMatch}
      </div>

      <div style={{display: 'flex', justifyContent: 'center', marginTop: '20px', gap: '10px'}}>
        <button
          className="fc-btn"
          onClick={() => setShowTrainingMenu(true)}
        >
          ANTRENMAN
        </button>
        <button
          className="fc-btn"
          onClick={() => setShowTeamSelection(true)}
          style={{ background: 'var(--blue)' }}
        >
          TAKIM DEĞİŞTİR
        </button>
      </div>

      {showTrainingMenu && (
        <div className="fc-modal-backdrop">
          <TrainingMenu setShowTrainingMenu={setShowTrainingMenu} />
          <button
            className="fc-btn"
            style={{ position: 'absolute', top: '20px', right: '20px', background: 'red' }}
            onClick={() => setShowTrainingMenu(false)}
          >
            Kapat
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
