import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';

const MatchPlayModal = ({ setShowMatchPlay, club, matchData, onMatchEnd }) => {
  const [currentMinute, setCurrentMinute] = useState(0);
  const [homeScore, setHomeScore] = useState(0);
  const [awayScore, setAwayScore] = useState(0);
  const [matchEvents, setMatchEvents] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isFastForward, setIsFastForward] = useState(false);

  const homeTeam = club.name;
  const awayTeam = matchData.awayTeam;

  const generateMatchEvent = (minute) => {
    const events = [
      { type: 'goal', team: 'home', player: 'Oyuncu', description: 'Gol!' },
      { type: 'goal', team: 'away', player: 'Rakip Oyuncu', description: 'Gol!' },
      { type: 'yellow', team: 'home', player: 'Oyuncu', description: 'Sarı Kart' },
      { type: 'yellow', team: 'away', player: 'Rakip Oyuncu', description: 'Sarı Kart' },
      { type: 'red', team: 'home', player: 'Oyuncu', description: 'Kırmızı Kart' },
      { type: 'red', team: 'away', player: 'Rakip Oyuncu', description: 'Kırmızı Kart' },
      { type: 'injury', team: 'home', player: 'Oyuncu', description: 'Sakatlık' },
      { type: 'injury', team: 'away', player: 'Rakip Oyuncu', description: 'Sakatlık' }
    ];

    const randomEvent = events[Math.floor(Math.random() * events.length)];
    const event = {
      minute: minute,
      type: randomEvent.type,
      team: randomEvent.team,
      player: randomEvent.player,
      description: randomEvent.description
    };

    if (event.type === 'goal') {
      if (event.team === 'home') {
        setHomeScore(prev => prev + 1);
      } else {
        setAwayScore(prev => prev + 1);
      }
    }

    setMatchEvents(prev => [...prev, event]);
  };

  const advanceTime = () => {
    if (currentMinute < 90) {
      const newMinute = currentMinute + 1;
      setCurrentMinute(newMinute);

      // Rastgele olay üretimi (düşük olasılık)
      if (Math.random() < 0.1) {
        generateMatchEvent(newMinute);
      }

      // Yarı zaman kontrolü
      if (newMinute === 45) {
        setMatchEvents(prev => [...prev, { minute: 45, type: 'half', description: 'İlk Yarı Sonu' }]);
      }
    } else {
      // Maç bitti
      setIsPlaying(false);
      onMatchEnd({ homeScore, awayScore, events: matchEvents });
      setShowMatchPlay(false);
    }
  };

  const fastForwardToEnd = () => {
    setIsFastForward(true);
    setCurrentMinute(90);
    
    // Son dakikaya kadar hızlı ilerlet
    for (let i = currentMinute + 1; i <= 90; i++) {
      if (Math.random() < 0.15) {
        generateMatchEvent(i);
      }
    }
    
    setIsFastForward(false);
    setIsPlaying(false);
    onMatchEnd({ homeScore, awayScore, events: matchEvents });
    setShowMatchPlay(false);
  };

  useEffect(() => {
    if (isPlaying && !isFastForward) {
      const interval = setInterval(() => {
        advanceTime();
      }, 1000);
      return () => clearInterval(interval);
    }
  }, [isPlaying, currentMinute, isFastForward]);

  const getEventIcon = (type) => {
    switch (type) {
      case 'goal': return '⚽';
      case 'yellow': return '🟨';
      case 'red': return '🟥';
      case 'injury': return '🏥';
      case 'half': return '⏸️';
      default: return '📝';
    }
  };

  return (
    <div className="fc-modal-backdrop">
      <div className="fc-modal" style={{ maxWidth: '600px', maxHeight: '90vh', overflow: 'auto' }}>
        <div style={{ 
          background: 'linear-gradient(135deg, #2c466b, #1a365d)',
          padding: '20px',
          borderRadius: '8px 8px 0 0',
          color: 'white',
          textAlign: 'center'
        }}>
          <div style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '10px' }}>
            {homeTeam} vs {awayTeam}
          </div>
          <div style={{ fontSize: '16px' }}>
            {currentMinute} dk
          </div>
        </div>

        <div className="fc-panel" style={{ padding: '20px' }}>
          {/* Skor Paneli */}
          <div style={{ 
            background: '#f5f5f5', 
            padding: '20px', 
            borderRadius: '8px',
            marginBottom: '20px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '32px', fontWeight: 'bold', color: 'var(--blue)' }}>
              {homeScore} - {awayScore}
            </div>
            <div style={{ fontSize: '18px', marginTop: '10px' }}>
              {homeTeam} vs {awayTeam}
            </div>
          </div>

          {/* Maç Kontrolleri */}
          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            gap: '10px',
            marginBottom: '20px'
          }}>
            <button
              className="fc-btn"
              onClick={() => setIsPlaying(!isPlaying)}
              style={{
                background: isPlaying ? 'var(--loss)' : 'var(--win)',
                color: 'white',
                padding: '10px 20px',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: 'bold'
              }}
            >
              {isPlaying ? 'Duraklat' : 'Başlat'}
            </button>
            <button
              className="fc-btn"
              onClick={fastForwardToEnd}
              style={{
                background: 'var(--blue)',
                color: 'white',
                padding: '10px 20px',
                border: 'none',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: 'bold'
              }}
            >
              Sona Atla
            </button>
          </div>

          {/* Maç Olayları */}
          <div style={{ marginBottom: '20px' }}>
            <h4 style={{ 
              margin: '0 0 15px 0', 
              textAlign: 'center', 
              color: 'var(--blue)',
              fontSize: '16px'
            }}>
              MAÇ OLAYLARI
            </h4>
            
            <div style={{ 
              maxHeight: '300px', 
              overflowY: 'auto',
              background: '#f9f9f9',
              borderRadius: '8px',
              padding: '10px'
            }}>
              {matchEvents.length === 0 ? (
                <div style={{ textAlign: 'center', color: '#666', padding: '20px' }}>
                  Henüz olay yok...
                </div>
              ) : (
                matchEvents.map((event, index) => (
                  <div key={index} style={{ 
                    display: 'flex', 
                    alignItems: 'center',
                    padding: '8px',
                    borderBottom: '1px solid #eee',
                    fontSize: '14px'
                  }}>
                    <span style={{ 
                      fontWeight: 'bold', 
                      color: 'var(--blue)',
                      marginRight: '10px',
                      minWidth: '30px'
                    }}>
                      {event.minute}'
                    </span>
                    <span style={{ marginRight: '10px', fontSize: '16px' }}>
                      {getEventIcon(event.type)}
                    </span>
                    <span style={{ color: '#333' }}>
                      {event.description}
                    </span>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>

        <div className="fc-footerbar">
          <button 
            className="fc-btn" 
            onClick={() => setShowMatchPlay(false)}
          >
            Çıkış
          </button>
        </div>
      </div>
    </div>
  );
};

MatchPlayModal.propTypes = {
  setShowMatchPlay: PropTypes.func.isRequired,
  club: PropTypes.object.isRequired,
  matchData: PropTypes.object.isRequired,
  onMatchEnd: PropTypes.func.isRequired,
};

export default MatchPlayModal;
