      'Isparta 32 Spor': 2.52
    };
    
    return teamValueData[teamName] || 20; // Varsayılan değer
  };
  
  // CSV'den tesis seviyesi hesaplama fonksiyonu
  const getFacilityLevel = async (teamName) => {
    try {
      // turkish_leagues_with_values.csv'den takım değerini al
      const response = await fetch('/turkish_leagues_with_values.csv');
      const csvText = await response.text();
      const lines = csvText.split('\n').slice(1); // İlk satırı atla (başlık)
      
      let teamValue = null;
      for (const line of lines) {
        const [takim, , deger] = line.split(','); // 'lig' değişkenini atla
        if (takim === teamName) {
          teamValue = parseFloat(deger);
          break;
        }
      }
      
      if (!teamValue) {
        console.warn(`Takım değeri bulunamadı: ${teamName}`);
        return Math.floor(Math.random() * 11) + 25; // Varsayılan değer
      }
      
      // Kadro_Yetenek_Listesi.csv'den interpolasyon yap
      const kadroResponse = await fetch('/Kadro_Yetenek_Listesi__Vmin_2__Vmax_350__Ymin_20__Ymax_92_.csv');
      const kadroText = await kadroResponse.text();
      const kadroLines = kadroText.split('\n').slice(1); // İlk satırı atla
      
      const kadroData = kadroLines.map(line => {
        const [kadroDegeri, yetenek] = line.split(',');
        return {
          kadro: parseFloat(kadroDegeri),
          yetenek: parseFloat(yetenek)
        };
      });
      
      // Yetenek değerini direkt tesis seviyesi olarak kullan (20-92 -> 20-92)
      let baseYetenek = 20; // Initialize with min level
      
      const exactMatch = kadroData.find(item => item.kadro === teamValue);
      if (exactMatch) {
        baseYetenek = exactMatch.yetenek;
      } else {
        // İnterpolasyon yap
        for (let i = 0; i < kadroData.length - 1; i++) {
          const current = kadroData[i];
          const next = kadroData[i + 1];
          
          if (teamValue >= current.kadro && teamValue <= next.kadro) {
            const ratio = (teamValue - current.kadro) / (next.kadro - current.kadro);
            baseYetenek = current.yetenek + ratio * (next.yetenek - current.yetenek);
            break;
          }
        }
      }
      // Yetenek değerini 20-92 arasına sınırla
      const variation = Math.floor(Math.random() * 11) - 5; // Rastgele ±5 varyasyon ekle
      return Math.max(20, Math.min(92, Math.round(baseYetenek + variation)));
      
    } catch (error) {
      console.error('CSV okuma hatası:', error);
      return Math.floor(Math.random() * 11) + 25; // Hata durumunda varsayılan değer
    }
  };
  
  // Menajer oluşturma fonksiyonu
  const generateManager = (teamValue) => {
    // Takım değerine göre menajer seviyesi belirle
    let baseLevel;
    if (teamValue >= 100) baseLevel = Math.floor(Math.random() * 20) + 80; // 80-99
    else if (teamValue >= 50) baseLevel = Math.floor(Math.random() * 20) + 60; // 60-79
    else if (teamValue >= 20) baseLevel = Math.floor(Math.random() * 20) + 40; // 40-59
    else baseLevel = Math.floor(Math.random() * 20) + 20; // 20-39

    // M-A-T seviyelerini oluştur (ortalaması baseLevel olacak)
    const variation = Math.floor(Math.random() * 10) - 5; // ±5 varyasyon
    const managerLevel = Math.max(20, Math.min(99, baseLevel + variation));
    const trainingLevel = Math.max(20, Math.min(99, baseLevel + Math.floor(Math.random() * 10) - 5));
    const tacticsLevel = Math.max(20, Math.min(99, baseLevel + Math.floor(Math.random() * 10) - 5));

    // Futbol anlayışını belirle
    const philosophies = Object.values(FOOTBALL_PHILOSOPHIES);
    const philosophy = philosophies[Math.floor(Math.random() * philosophies.length)];
    
    // Anlayışa göre taktik seç
    let formation;
    switch (philosophy) {
      case FOOTBALL_PHILOSOPHIES.ATTACKING:
        formation = FORMATIONS.ATTACKING[Math.floor(Math.random() * FORMATIONS.ATTACKING.length)];
        break;
      case FOOTBALL_PHILOSOPHIES.BALANCED:
        formation = FORMATIONS.BALANCED[Math.floor(Math.random() * FORMATIONS.BALANCED.length)];
        break;
      case FOOTBALL_PHILOSOPHIES.DEFENSIVE:
        formation = FORMATIONS.DEFENSIVE[Math.floor(Math.random() * FORMATIONS.DEFENSIVE.length)];
        break;
    }

    return {
      name: generateManagerName(),
      managerLevel: managerLevel,
      trainingLevel: trainingLevel,
      tacticsLevel: tacticsLevel,
      level: Math.round((managerLevel + trainingLevel + tacticsLevel) / 3), // Ortalama seviye
      philosophy: philosophy,
      formation: formation
    };
  };

      return {
      name: selectedTeam,
      season: '2024/25',
      league: leagueName,
      date: 'Ağu 2024',
      hfSonu: 'Hazırlık 1', // Sezon başlangıcı - hazırlık 1'deyiz
      lig: team ? team.position : Math.floor(Math.random() * 20) + 1,
      money: 50000, // Sabit başlangıç parası
      kadro: getTeamValue(selectedTeam), // Doğru kadro değeri
      menajer: generateManager(getTeamValue(selectedTeam)).level,
      antrenman: await getFacilityLevel(selectedTeam),
      altyapi: await getFacilityLevel(selectedTeam),
      kupalar: 0,
      basarildi: 0,
      taraftarMutlu: 50,
      baskanMutlu: 50,
      stadium: 'Stadyum',
      baskan: 'Başkan',
      matchResults: [], // Henüz hiç maç oynanmamış
      nextMatch: `${leagueName}: ${turkishLeagues[leagueName][Math.floor(Math.random() * turkishLeagues[leagueName].length)].name} (D)`,
      squad: squad, // Kadro bilgisi
      youthSquad: youthData ? youthData.squad : [], // Altyapı kadrosu bilgisi
      manager: generateManager(getTeamValue(selectedTeam), squad ? squad.averageRating : null), // Menajer bilgisi
      gameTime: { week: 1, day: 'Hafta Başı' } // Oyun zamanı
