  return `${firstName} ${lastName}`;
};

// Menajer oluşturma fonksiyonu
const generateManager = (teamValue, squadAverageRating = null) => {
  // Eğer squadAverageRating varsa onu kullan, yoksa teamValue'ya göre hesapla
  let baseLevel;
  
  if (squadAverageRating !== null) {
    // Takımın kadro yetenek ortalamasına göre menajer seviyesi belirle (27+-5)
