initializeManagers();

// Haftalık gider hesaplama fonksiyonu
export const calculateWeeklyExpenses = (club) => {
  let totalExpenses = 0;
  
  // Menajer maaşı (basit hesaplama)
  if (club.manager) {
    totalExpenses += club.manager.level * 1000;
  }
  
  // Oyuncu maaşları
  if (club.squad) {
    club.squad.forEach(player => {
      if (player.salary) {
        totalExpenses += player.salary;
      }
    });
  }
  
  // Tesis giderleri (basit hesaplama)
  const facilityCosts = (club.antrenman + club.altyapi) * 100;
  totalExpenses += facilityCosts;
  
  return totalExpenses;
};

// Hafta geçişi fonksiyonu
export const advanceWeek = (club) => {
  const weeklyExpenses = calculateWeeklyExpenses(club);
  const newMoney = Math.max(0, club.money - weeklyExpenses);
  
  return {
    ...club,
    money: newMoney,
    gameTime: {
      week: club.gameTime.week + 1,
      day: 'Hafta Başı'
    }
  };
};
