import React, { useState } from 'react';
import { STR, WEEK_SLICES_LBL } from './constants';

const mainBg = {
  background: 'linear-gradient(to bottom, #2c466b 60%, #3e5c2c 100%)',
  minHeight: '100vh',
  color: '#fff',
  fontFamily: 'sans-serif',
};

const card = {
  background: '#fff', color: '#2c466b', borderRadius: 10, padding: '10px 20px',
  margin: 10, minWidth: 80, textAlign: 'center', fontWeight: 'bold',
  boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
};

export default function App() {
  const [clubName, setClubName] = useState('Afyonspor');
  const [leagueShown, setLeagueShown] = useState('2. Lig');
  const [currentWeek, setCurrentWeek] = useState(0);
  const [currentSlice] = useState(0);
  const [showManager, setShowManager] = useState(false);
  const [showFinance, setShowFinance] = useState(false);
  const [showStandings, setShowStandings] = useState(false);
  const [showFixture, setShowFixture] = useState(false);
  const [managerTab, setManagerTab] = useState('staff');
  const [showInfo, setShowInfo] = useState(null); // {title, message}
  const [bankBalance, setBankBalance] = useState(12000);
  const incomeWin = 800, incomeDraw = 300, incomeLoss = 100, weekCost = 500;
  const [groupName, setGroupName] = useState(''); // Beyaz | Kirmizi
  const [groupTeams, setGroupTeams] = useState([]); // string[]
  const [fixtures, setFixtures] = useState([]); // {hft, home, away, a, y, result}
  const [standings, setStandings] = useState([]); // {team,O,G,B,M,A,Y,AV,PN}
  const [allTeams, setAllTeams] = useState([]);

  const staffSlots = [
    { role: 'Asistan',   filled: true,  icon: '/icons/assistant.svg' },
    { role: 'Asistan',   filled: false },
    { role: 'Fizyo',     filled: true,  icon: '/icons/physio.svg' },
    { role: 'Fizyo',     filled: false },
    { role: 'Antrenor',  filled: true,  icon: '/icons/coach.svg' },
    { role: 'Antrenor',  filled: true,  icon: '/icons/coach.svg' },
    { role: 'Antrenor',  filled: false },
    { role: 'Antrenor',  filled: false },
    { role: 'Scout',     filled: true,  icon: '/icons/scout.svg' },
    { role: 'Scout',     filled: true,  icon: '/icons/scout.svg' },
    { role: 'Scout',     filled: false },
    { role: 'Scout',     filled: false },
  ];

  const onStaffClick = (s) => {
    if (s.filled) return;
    const msg = s.role === 'Asistan'
      ? 'Amator kume seviyesinde maksimum 1 asistan atayabilirsin.'
      : (s.role === 'Antrenor'
          ? 'Amator kume seviyesinde maksimum 2 antrenor atayabilirsin.'
          : 'Mevcut seviyede ek slot yok.');
    setShowInfo({ title: STR.UYARI, message: msg });
  };

  // CSV -> detect group and load fixtures/standings for selected team
  React.useEffect(() => {
    const load = async () => {
      try {
        const res = await fetch(process.env.PUBLIC_URL + '/turkish_leagues_with_values.csv');
        const text = await res.text();
        const rows = text.split(/\r?\n/).filter(Boolean).slice(1).map(l => l.split(','));
        const in2 = rows.filter(r => (r[1] || '').includes('2. Lig'));

        // keep a global list for dropdown
        const all = in2.map(r => (r[0] || '').trim()).filter(Boolean);
        setAllTeams(all);

        // normalize helper for Turkish-insensitive matching
        const norm = (s='') => s.normalize('NFKD').replace(/[\u0300-\u036f]/g,'').replace(/ı/g,'i').replace(/İ/g,'I').toLowerCase();
        const isGroup = (s, grp) => {
          const n = norm(s);
          return grp === 'Beyaz' ? n.includes('beyaz') : n.includes('kirmizi');
        };

        // detect club and group
        const me = in2.find(r => norm(r[0]) === norm(clubName));
        const leagueStr = me ? (me[1] || '').trim() : '';
        const g = norm(leagueStr).includes('kirmizi') ? 'Kırmızı' : 'Beyaz';
        setGroupName(g);
        setLeagueShown('2. Lig ' + g);

        const teamList = in2.filter(r => isGroup(r[1], g === 'Kırmızı' ? 'Kirmizi' : 'Beyaz')).map(r => (r[0] || '').trim());
        // fallback if empty
        const teams = teamList.length > 0 ? teamList : in2.map(r => (r[0] || '').trim());
        setGroupTeams(teams);
        // init standings
        const st = teams.map(t => ({ team: t, O:0,G:0,B:0,M:0,A:0,Y:0,AV:0,PN:0 }));
        setStandings(st);
        // fixtures (double round robin simple)
        const opps = teams.filter(t => t.toLowerCase() !== clubName.toLowerCase());
        const single = opps.map((t,i)=> ({ hft: i+1, home: i%2===0 ? clubName : t, away: i%2===0 ? t : clubName, a:'-', y:'-', result:'' }));
        const second = opps.map((t,i)=> ({ hft: opps.length + i + 1, home: single[i].home===clubName? t: clubName, away: single[i].home===clubName? clubName: t, a:'-', y:'-', result:'' }));
        setFixtures([...single, ...second]);
      } catch {}
    };
    load();
  }, [clubName]);

  const recomputePositions = React.useCallback((stArr) => {
    const arr = [...stArr].map(r => ({...r, AV: (r.A - r.Y)}));
    arr.sort((a,b)=> b.PN - a.PN || (b.A - b.Y) - (a.A - a.Y) || b.A - a.A || a.team.localeCompare(b.team));
    return arr;
  }, []);

  const playWeek = () => {
    const week = currentWeek;
    const idx = fixtures.findIndex(f => f.hft === week && f.result==='');
    if (idx < 0) { setCurrentWeek(w=>w+1); return; }
    const f = fixtures[idx];
    // simulate
    const gf = Math.floor(Math.random()*4); // our goals if we are home
    const ga = Math.floor(Math.random()*4);
    const homeIsUs = f.home.toLowerCase() === clubName.toLowerCase();
    const ourGoals = homeIsUs ? gf : ga;
    const theirGoals = homeIsUs ? ga : gf;
    const resChar = ourGoals>theirGoals? 'G' : (ourGoals<theirGoals? 'M' : 'B');
    const newFixtures = [...fixtures];
    newFixtures[idx] = { ...f, a: ourGoals, y: theirGoals, result: resChar };
    setFixtures(newFixtures);
    // update finance
    const inc = resChar === 'G' ? incomeWin : (resChar === 'B' ? incomeDraw : incomeLoss);
    setBankBalance(b => b + inc - weekCost);
    // update standings for both teams
    const sArr = standings.map(x=>({...x}));
    const upd = (team, gf1, ga1) => {
      const r = sArr.find(x => x.team === team);
      if (!r) return;
      r.O += 1; r.A += gf1; r.Y += ga1; r.AV = r.A - r.Y;
      if (gf1>ga1){ r.G+=1; r.PN+=3; } else if (gf1<ga1){ r.M+=1; } else { r.B+=1; r.PN+=1; }
    };
    upd(f.home, gf, ga);
    upd(f.away, ga, gf);
    setStandings(sArr);
    setCurrentWeek(w=>w+1);
  };

  return (
    <div className="fc-container">
      <div className="fc-header">
        <div className="fc-title">FOOTBALL CHAIRMAN</div>
        <div className="fc-ribbon">{clubName.toUpperCase()}</div>
        <div className="fc-sub">2025/26 | {leagueShown} | Hafta {currentWeek}</div>
        <div style={{marginTop:6}}>
          <select value={clubName} onChange={(e)=>{ setClubName(e.target.value); setCurrentWeek(0); }} style={{padding:6, borderRadius:6, border:'none'}}>
            {[clubName, ...allTeams.filter(t=>t!==clubName)].map((t,i)=>(
              <option key={i} value={t}>{t}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Hızlı kartlar */}
      <div className="fc-grid">
        <button className="fc-card white" onClick={()=>setShowFixture(true)}>
          {STR.HF_SONU}<span className="big">{currentWeek}</span>
        </button>
        <button className="fc-card white" onClick={() => setShowStandings(true)}>
          {STR.LIG}<span className="big">-</span>
        </button>
        <button className="fc-card white" onClick={()=>setShowFinance(true)}>
          {STR.PARA}
          <span className="big" style={{color:'#2c7b2c'}}>
            {new Intl.NumberFormat('tr-TR',{style:'currency',currency:'TRY',maximumFractionDigits:0}).format(bankBalance)}
          </span>
        </button>
      </div>

      {showManager && (
        <div style={{position: 'fixed', inset: 0, background: 'rgba(44,70,107,0.95)', zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
          <div style={{background: 'rgba(255,255,255,0.95)', color: '#2c466b', borderRadius: 18, padding: 20, minWidth: 360, maxWidth: '95vw', maxHeight: '85vh', overflowY: 'auto', boxShadow: '0 2px 16px rgba(0,0,0,0.2)'}}>
            <div style={{fontSize: '1.2em', fontWeight: 'bold', textAlign: 'center', marginBottom: 12}}>{STR.MENAJER}</div>
            <div style={{display:'flex', gap:8, marginBottom:12}}>
              <button onClick={() => setManagerTab('records')} style={{flex:1, border:'none', borderRadius:8, padding:'8px', cursor:'pointer', fontWeight:'bold', background: managerTab==='records' ? '#bfe3bf' : '#fff', color:'#2c466b', boxShadow:'0 1px 4px rgba(0,0,0,0.08)'}}>{STR.KAYITLAR}</button>
              <button onClick={() => setManagerTab('staff')} style={{flex:1, border:'none', borderRadius:8, padding:'8px', cursor:'pointer', fontWeight:'bold', background: managerTab==='staff' ? '#bfe3bf' : '#fff', color:'#2c466b', boxShadow:'0 1px 4px rgba(0,0,0,0.08)'}}>{STR.TEKNIK_KADRO}</button>
            </div>
            {managerTab==='staff' && (
              <div>
                <div style={{display:'grid', gridTemplateColumns:'repeat(4, minmax(70px,1fr))', gap:12, marginTop:8}}>
                  {staffSlots.map((s, idx) => (
                    <button key={idx} onClick={()=>onStaffClick(s)} style={{border:'none', background:'transparent', cursor:'pointer'}}>
                      <div style={{width:70, height:70, borderRadius:8, background: s.filled ? '#e6f4e6' : '#d9d9d9', display:'flex', alignItems:'center', justifyContent:'center', boxShadow:'0 1px 4px rgba(0,0,0,0.06)'}}>
                        {s.filled ? (
                          s.icon ? <img src={s.icon} alt="" style={{width:36,height:36,objectFit:'contain'}}/> : <span style={{fontSize:'1.6em'}}>*</span>
                        ) : (
                          <span style={{fontSize:'1.6em'}}>*</span>
                        )}
                      </div>
                      <div style={{fontSize:'0.9em', marginTop:4, color:'#333'}}>{s.role}</div>
                    </button>
                  ))}
                </div>
              </div>
            )}
            <div style={{display:'flex', justifyContent:'space-between', marginTop:16}}>
              <button style={{background: '#3e5c2c', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 16px', fontWeight: 'bold', cursor:'pointer'}} onClick={() => setShowManager(false)}>{STR.GERI}</button>
            </div>
          </div>
        </div>
      )}

      {showInfo && (
        <div style={{position:'fixed', inset:0, zIndex:1100, background:'rgba(0,0,0,0.3)', display:'flex', alignItems:'center', justifyContent:'center'}} onClick={()=>setShowInfo(null)}>
          <div style={{background:'#fff', borderRadius:12, padding:20, minWidth:300, maxWidth:'80vw', boxShadow:'0 2px 16px rgba(0,0,0,0.25)'}} onClick={(e)=>e.stopPropagation()}>
            <div style={{fontWeight:'bold', fontSize:'1.2em', marginBottom:8}}>{showInfo.title}</div>
            <div style={{color:'#333', marginBottom:16}}>{showInfo.message}</div>
            <div style={{display:'flex', justifyContent:'flex-end'}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 16px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo(null)}>{STR.TAMAM}</button>
            </div>
          </div>
        </div>
      )}

      {showFixture && (
        <div style={{position:'fixed', inset:0, background:'rgba(44,70,107,0.95)', zIndex:1000, display:'flex', alignItems:'center', justifyContent:'center'}}>
          <div style={{background:'#fff', color:'#2c466b', borderRadius:12, padding:16, minWidth:360, maxWidth:'95vw', maxHeight:'85vh', overflowY:'auto'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:8}}>{STR.FIKSTURLER_SONUCLAR}</div>
            <table style={{width:'100%', borderCollapse:'collapse'}}>
              <thead>
                <tr style={{background:'#e6f4e6'}}>
                  <th style={{padding:6}}>{STR.HFT}</th>
                  <th style={{padding:6}}>{STR.RAKIP}</th>
                  <th style={{padding:6}}>{STR.A}</th>
                  <th style={{padding:6}}>{STR.Y}</th>
                  <th style={{padding:6}}>{STR.SONUC}</th>
                </tr>
              </thead>
              <tbody>
                {fixtures.map((f,i)=> (
                  <tr key={i} style={{background: i%2===0? '#f5f5f5':'#e6f4e6'}}>
                    <td style={{padding:6, textAlign:'center'}}>{f.hft}</td>
                    <td style={{padding:6}}>{f.home} - {f.away}</td>
                    <td style={{padding:6, textAlign:'center'}}>{f.a}</td>
                    <td style={{padding:6, textAlign:'center'}}>{f.y}</td>
                    <td style={{padding:6, textAlign:'center'}}>{f.result}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div style={{textAlign:'center', marginTop:12}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 16px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowFixture(false)}>{STR.GERI}</button>
            </div>
          </div>
        </div>
      )}

      {showStandings && (
        <div style={{position:'fixed', inset:0, background:'rgba(44,70,107,0.95)', zIndex:1000, display:'flex', alignItems:'center', justifyContent:'center'}}>
          <div style={{background:'#fff', color:'#2c466b', borderRadius:12, padding:16, minWidth:360, maxWidth:'95vw', maxHeight:'85vh', overflowY:'auto'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:8}}>{STR.PUAN_DURUMU}</div>
            <table style={{width:'100%', borderCollapse:'collapse'}}>
              <thead>
                <tr style={{background:'#e6f4e6'}}>
                  <th style={{padding:6}}>#</th>
                  <th style={{padding:6}}>{STR.TAKIM}</th>
                  <th style={{padding:6}}>{STR.O_COL}</th>
                  <th style={{padding:6}}>{STR.G_COL}</th>
                  <th style={{padding:6}}>{STR.B_COL}</th>
                  <th style={{padding:6}}>{STR.M_COL}</th>
                  <th style={{padding:6}}>{STR.PN_COL}</th>
                </tr>
              </thead>
              <tbody>
                {recomputePositions(standings).map((row,i)=>(
                  <tr key={i} style={{background: i%2===0? '#f5f5f5':'#e6f4e6'}}>
                    <td style={{padding:6, textAlign:'center'}}>{i+1}</td>
                    <td style={{padding:6}}>{row.team}</td>
                    <td style={{padding:6, textAlign:'center'}}>{row.O}</td>
                    <td style={{padding:6, textAlign:'center'}}>{row.G}</td>
                    <td style={{padding:6, textAlign:'center'}}>{row.B}</td>
                    <td style={{padding:6, textAlign:'center'}}>{row.M}</td>
                    <td style={{padding:6, textAlign:'center'}}>{row.PN}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div style={{textAlign:'center', marginTop:12}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 16px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowStandings(false)}>{STR.GERI}</button>
            </div>
          </div>
        </div>
      )}

      {showFinance && (
        <div style={{position:'fixed', inset:0, background:'rgba(44,70,107,0.95)', zIndex:1000, display:'flex', alignItems:'center', justifyContent:'center'}}>
          <div style={{background:'#fff', color:'#2c466b', borderRadius:12, padding:16, minWidth:360, maxWidth:'95vw', maxHeight:'85vh', overflowY:'auto'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:8}}>{STR.FINANS}</div>
            <div style={{display:'flex', justifyContent:'space-between'}}>
              <div>Banka</div>
              <div>12.000</div>
            </div>
            <div style={{textAlign:'center', marginTop:12}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 16px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowFinance(false)}>{STR.GERI}</button>
            </div>
          </div>
        </div>
      )}
      {/* Orta lacivert kartlar */}
      <div className="fc-grid" style={{marginTop:8}}>
        <div className="fc-card navy">{STR.KADRO}<span className="big">-</span></div>
        <button className="fc-card navy" onClick={()=>setShowManager(true)}>{STR.MENAJER}<span className="big">-</span></button>
        <div className="fc-card navy">{STR.ANTRENMAN}<span className="big">-</span></div>
        <div className="fc-card navy">{STR.ALTYAPI}<span className="big">-</span></div>
      </div>

      {/* Yeşil özellik kartları */}
      <div className="fc-grid" style={{marginTop:4}}>
        <div className="fc-card green">{STR.KUPALAR}<span className="big">0</span></div>
        <div className="fc-card green">{STR.BASARIMLAR}<span className="big">0</span></div>
        <div className="fc-card green">{STR.REKORLAR}<span className="big">*</span></div>
        <button className="fc-card green" onClick={()=>setShowFixture(true)}>{STR.GECMIS}<span className="big">*</span></button>
      </div>

      {/* Açık yeşil bilgi kartları */}
      <div className="fc-grid" style={{marginTop:4}}>
        <div className="fc-card light" style={{minWidth:220}}>
          {STR.TARAFTARLAR_MUTLU_MU}
          <span className="big">-</span>
        </div>
        <div className="fc-card light">{STR.STADYUM}<span className="big">⚽</span></div>
        <div className="fc-card light">{STR.BASKAN}<span className="big">👕</span></div>
      </div>

      {/* Son sonuçlar */}
      <div className="fc-dots">
        {['M','G','G','G','B','B'].map((r,i)=> (
          <div key={i} className="fc-dot" style={{background: r==='G'? '#2c7b2c' : (r==='B'? '#b88c2c':'#c00')}}>{r}</div>
        ))}
      </div>

      {/* Sonraki maç */}
      <div className="fc-next">{groupName? (groupName + ' | '): ''}{STR.FIKSTUR}: {fixtures[0] ? `${fixtures[0].home} - ${fixtures[0].away}` : '-'}</div>
    </div>
  );
}
