import React from 'react';
import PropTypes from 'prop-types';
import { fixtureData } from '../data';

const getResultColor = (result) => {
  if (result === 'M') return 'var(--loss)';
  if (result === 'G') return 'var(--win)';
  if (result === 'B') return 'var(--draw)';
  return '#ccc';
};

const FixtureModal = ({ setShowFixture }) => (
  <div className="fc-modal-backdrop">
    <div className="fc-modal">
      <h3>FİKSTÜRLER/SONUÇLAR</h3>
      <table className="fc-table">
        <thead>
          <tr>
            <th>hft</th>
            <th>Rakip</th>
            <th>A</th>
            <th>Y</th>
            <th>Sonuç</th>
            <th>Kalabalık</th>
            <th>Poz</th>
          </tr>
        </thead>
        <tbody>
          {fixtureData.map((row, idx) => (
            <tr key={idx}>
              <td>{row.hft}</td>
              <td>{row.rakip}</td>
              <td>{row.a}</td>
              <td>{row.y}</td>
              <td>
                {row.result && (
                  <span
                    className="form-badge"
                    style={{ backgroundColor: getResultColor(row.result) }}
                  >
                    {row.result}
                  </span>
                )}
              </td>
              <td>{row.crowd}</td>
              <td>{row.pos}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="fc-btn-row">
        <button className="fc-btn" onClick={() => setShowFixture(false)}>Geri</button>
        <button className="fc-btn">Tablo</button>
      </div>
    </div>
  </div>
);

FixtureModal.propTypes = {
    setShowFixture: PropTypes.func.isRequired,
};

export default FixtureModal;
