          <div style={{background:'rgba(255,255,255,0.95)', color:'#2c466b', borderRadius:18, padding:20, minWidth:360, maxWidth:'95vw', maxHeight:'85vh', overflowY:'auto', boxShadow:'0 2px 16px rgba(0,0,0,0.2)'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:12}}>{stadium.name}</div>
            <div style={{display:'flex', justifyContent:'center', marginBottom:12}}>
              <div style={{width:180, height:90, borderRadius:12, background:'#e6f4e6', display:'flex', alignItems:'center', justifyContent:'center'}}>🏟️</div>
            </div>
            <table style={{width:'100%', borderCollapse:'separate', borderSpacing:0, fontSize:'1em'}}>
              <thead>
                <tr style={{color:'#2c466b'}}>
                  <th style={{textAlign:'left', padding:'8px'}}>Bölüm</th>
                  <th style={{textAlign:'center', padding:'8px'}}>İnşa Edildi</th>
                  <th style={{textAlign:'right', padding:'8px'}}>Kapasite</th>
                </tr>
              </thead>
              <tbody>
                {(['north','east','south','west','corners']).map((k,idx)=>{
                  const st = stadium.stands[k];
                  return (
                    <tr key={k} style={{background:'#fff', boxShadow:'0 1px 4px rgba(0,0,0,0.06)'}}>
                      <td style={{padding:'10px 8px', borderBottom:'8px solid transparent'}}>
                        <button onClick={()=>{ setActiveStandKey(k); setStandTarget(st.capacity); setStandSpeed('slow'); setShowStandEditor(true); }} style={{border:'none', background:'transparent', color:'#2c466b', fontWeight:'bold', cursor:'pointer'}}>{st.name}</button>
                      </td>
                      <td style={{padding:'10px 8px', textAlign:'center', borderBottom:'8px solid transparent'}}>{st.built}</td>
                      <td style={{padding:'10px 8px', textAlign:'right', borderBottom:'8px solid transparent'}}>{st.capacity}</td>
                    </tr>
                  );
                })}
                <tr style={{background:'transparent'}}>
                  <td style={{padding:'10px 8px', fontWeight:'bold'}}>Saha ({stadium.stands.pitch.quality}/{stadium.stands.pitch.max})</td>
                  <td></td>
                  <td style={{padding:'10px 8px', textAlign:'right', fontWeight:'bold'}}>{totalCapacity()}</td>
                </tr>
              </tbody>
            </table>
            <div style={{display:'flex', justifyContent:'space-between', marginTop:16}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowStadium(false)}>Geri</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Tesisler ekranı yakında.'})}>Tesisler</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Süper Stadyum yükseltmesi yakında.'})}>Süper Stadyum</button>
            </div>
          </div>
        </div>
      )}

      {/* Stand Editor Modal */}
      {showStandEditor && activeStandKey && (
        <div style={{position:'fixed', inset:0, background:'rgba(44,70,107,0.95)', zIndex:1100, display:'flex', alignItems:'center', justifyContent:'center'}}>
          {(() => { const st = stadium.stands[activeStandKey]; const from = st.capacity; const to = Math.max(from, standTarget); const weeks = standWeeks(from, to, standSpeed); const cost = (to - from) * standCostPerSeat(standSpeed); return (
          <div style={{background:'rgba(255,255,255,0.95)', color:'#2c466b', borderRadius:18, padding:20, minWidth:360, maxWidth:'95vw', boxShadow:'0 2px 16px rgba(0,0,0,0.2)'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:12}}>{st.name.toUpperCase()}</div>
            <div style={{display:'flex', justifyContent:'center', marginBottom:12}}>
              <div style={{width:180, height:90, borderRadius:12, background:'#e6f4e6', display:'flex', alignItems:'center', justifyContent:'center'}}>🏟️</div>
            </div>
            <div style={{display:'flex', gap:8, marginBottom:12}}>
              <button style={{flex:1, background:'#bfe3bf', color:'#2c466b', border:'none', borderRadius:8, padding:8, fontWeight:'bold'}}>Tribünü Yeniden İnşa Et</button>
              <button style={{flex:1, background:'#fff', color:'#2c466b', border:'none', borderRadius:8, padding:8, fontWeight:'bold'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Yeniden adlandırma yakında.'})}>Tribünü Yeniden Adlandır</button>
            </div>
            <div style={{display:'flex', alignItems:'center', justifyContent:'space-between', margin:'12px 0'}}>
              <button onClick={()=>setStandTarget(Math.max(from, standTarget-50))} style={{background:'#ccc', color:'#222', border:'none', borderRadius:24, padding:'8px 12px', fontWeight:'bold', cursor:'pointer'}}>&lt;&lt;&lt;</button>
              <div style={{fontSize:'2em', fontWeight:'bold'}}>{to}</div>
              <button onClick={()=>setStandTarget(standTarget+50)} style={{background:'#2c7b2c', color:'#fff', border:'none', borderRadius:24, padding:'8px 12px', fontWeight:'bold', cursor:'pointer'}}>&gt;&gt;&gt;</button>
            </div>
            <div style={{display:'flex', gap:8, marginBottom:8}}>
              <button onClick={()=>setStandSpeed('slow')} style={{flex:1, background: standSpeed==='slow' ? '#bfe3bf' : '#fff', color:'#2c466b', border:'none', borderRadius:8, padding:8, fontWeight:'bold'}}>Yavaş</button>
              <button onClick={()=>setStandSpeed('fast')} style={{flex:1, background: standSpeed==='fast' ? '#bfe3bf' : '#fff', color:'#2c466b', border:'none', borderRadius:8, padding:8, fontWeight:'bold'}}>Hızlı</button>
            </div>
            <div style={{color:'#222'}}>
              <div style={{display:'flex', justifyContent:'space-between', margin:'6px 0'}}><span>İnşa Maliyeti</span><span style={{fontWeight:'bold'}}>{formatTRY(cost)}</span></div>
              <div style={{display:'flex', justifyContent:'space-between', margin:'6px 0'}}><span>İnşa Süresi</span><span style={{fontWeight:'bold'}}>{weeks} hafta</span></div>
              <div style={{display:'flex', justifyContent:'space-between', margin:'6px 0'}}><span>Banka Bakiyesi</span><span style={{fontWeight:'bold'}}>{formatTRY(bankBalance - cost)}</span></div>
            </div>
            <div style={{display:'flex', justifyContent:'space-between', marginTop:12}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowStandEditor(false)}>Geri</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>{
                if (to <= from) { setShowInfo({title:'Bilgi', message:'Hedef kapasite mevcut kapasiteden büyük olmalı.'}); return; }
                const costNow = cost;
                if (bankBalance < costNow) { setShowInfo({title:'Üzgünüz!', message:'Yetersiz bakiye.'}); return; }
                setStadium(prev => ({
                  ...prev,
                  stands: { ...prev.stands, [activeStandKey]: { ...prev.stands[activeStandKey], capacity: to } }
                }));
                setBankBalance(b => b - costNow);
                setShowStandEditor(false);
                setShowInfo({title:'Başarılı', message:`${st.name} kapasitesi ${to} olarak güncellendi.`});
              }}>Yeniden İnşa Et</button>
            </div>
          </div> ); })()}
        </div>
      )}

      {/* Chairman Modal */}
      {showChairman && (
        <div style={{position:'fixed', inset:0, background:'rgba(44,70,107,0.95)', zIndex:1100, display:'flex', alignItems:'center', justifyContent:'center'}}>
          <div style={{background:'rgba(255,255,255,0.95)', color:'#2c466b', borderRadius:18, padding:20, minWidth:360, maxWidth:'95vw', maxHeight:'85vh', overflowY:'auto', boxShadow:'0 2px 16px rgba(0,0,0,0.2)'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:12}}>BAŞKANLIK KARİYERİ</div>
            <div style={{display:'flex', gap:12, alignItems:'center', background:'#fff', borderRadius:12, padding:12, boxShadow:'0 1px 6px rgba(0,0,0,0.06)'}}>
              <div style={{width:72, height:72, borderRadius:8, background:'#e6f4e6', display:'flex', alignItems:'center', justifyContent:'center', fontSize:'2.2em'}}>🧑🏻‍💼</div>
              <div style={{flex:1}}>
                <div style={{fontWeight:'bold', fontSize:'1.1em'}}>JUAN PEREZ</div>
                <div style={{color:'#333'}}>BENIDORM</div>
                <div style={{color:'#333'}}>2023 - ŞİMDİ (2 YILLAR)</div>
                <div style={{marginTop:6, display:'flex', gap:16}}>
                  <div><span style={{marginRight:6}}>🏆</span>0</div>
                  <div><span style={{marginRight:6}}>⭐</span>1</div>
                </div>
              </div>
            </div>
            <div style={{marginTop:12, background:'#fff', borderRadius:12, padding:12, boxShadow:'0 1px 6px rgba(0,0,0,0.06)'}}>
              <div style={{color:'#333', marginBottom:8}}>103 oyunlar  <b>G 48</b>  <b>B 25</b>  <b>M 30</b>  <b>1.64 MBP</b></div>
              <div style={{display:'flex', justifyContent:'space-between', margin:'6px 0'}}>
                <span>Şu Ana Kadarki Toplam Kar/Zarar</span>
                <span style={{fontWeight:'bold', color:'#c00'}}>{formatTRY(-38260)}</span>
              </div>
              <div style={{display:'flex', justifyContent:'space-between', margin:'6px 0'}}>
                <span>Alınan Transfer Ücretleri</span>
                <span style={{fontWeight:'bold', color:'#2c7b2c'}}>{formatTRY(1700)}</span>
              </div>
              <div style={{display:'flex', justifyContent:'space-between', margin:'6px 0'}}>
                <span>Ödenen Transfer Ücretleri</span>
                <span style={{fontWeight:'bold', color:'#c00'}}>{formatTRY(10980)}</span>
              </div>
              <div style={{display:'flex', justifyContent:'space-between', margin:'6px 0'}}>
                <span>Menajerler (Ort oyun)</span>
                <span style={{fontWeight:'bold'}}>2 (52 oyunlar)</span>
              </div>
              <div style={{display:'flex', justifyContent:'space-between', margin:'6px 0'}}>
                <span>Ömür Boyu Taraftar Onayı</span>
                <span style={{fontWeight:'bold'}}>84.3%</span>
              </div>
              <div style={{display:'flex', justifyContent:'space-between', margin:'6px 0'}}>
                <span>Ömür Boyu Menajer Onayı</span>
                <span style={{fontWeight:'bold'}}>96.6%</span>
              </div>
            </div>
            <div style={{display:'flex', justifyContent:'space-between', marginTop:16}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowChairman(false)}>Geri</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Kulüp listesi yakında.'})}>Kulüp Listesi</button>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 24px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo({title:'Bilgi', message:'Yüksek skorlar yakında.'})}>Yüksek Skorlar</button>
            </div>
          </div>
        </div>
      )}
      <div style={{display: 'flex', justifyContent: 'center', flexWrap: 'wrap', gap: '10px', marginTop: '10px'}}>
        <button style={{...cardStyle, background: '#3e5c2c', color: '#fff', cursor: 'pointer'}} onClick={() => setShowTrophies(true)}>KUPALAR<br/>{trophies.length}</button>
        <button style={{...cardStyle, background: '#3e5c2c', color: '#fff', cursor: 'pointer'}} onClick={() => setShowAchievements(true)}>BAŞARIMLAR<br/>{achievements.filter(a=>a.achieved).length}/{achievements.length}</button>
        <button style={{...cardStyle, background: '#3e5c2c', color: '#fff', cursor: 'pointer'}} onClick={() => setShowRecords(true)}>REKORLAR<br/><span style={{fontSize: '1.2em'}}>📈</span></button>
        <button style={{...cardStyle, background: '#3e5c2c', color: '#fff', cursor: 'pointer'}} onClick={() => setShowHistory(true)}>GEÇMİŞ<br/><span style={{fontSize: '1.2em'}}>📜</span></button>
      </div>
      <div style={{display: 'flex', justifyContent: 'center', flexWrap: 'wrap', gap: '10px', marginTop: '10px'}}>
        <button style={{...cardStyle, background: '#e6f4e6', color: '#2c466b', cursor: 'pointer'}} onClick={()=>setShowFans(true)}>TARAFTARLAR MUTLU MU?<br/><span style={{fontSize: '1.2em'}}>{club.taraftarMutlu}% MENAJER<br/>{club.baskanMutlu}% BAŞKAN</span></button>
        <button style={{...cardStyle, background: '#e6f4e6', color: '#2c466b', cursor: 'pointer'}} onClick={()=>setShowStadium(true)}>STADYUM<br/><span style={{fontSize: '1.2em'}}>🏟️</span></button>
        <button style={{...cardStyle, background: '#e6f4e6', color: '#2c466b', cursor: 'pointer'}} onClick={()=>setShowChairman(true)}>BAŞKAN<br/><span style={{fontSize: '1.2em'}}>👔</span></button>
      </div>
      <div style={{display: 'flex', justifyContent: 'center', gap: '10px', marginTop: '20px'}}>
        {club.matchResults.map((result, idx) => {
          let bg = '#b88c2c';
          if (result === 'M') bg = '#c00';
          if (result === 'G') bg = '#2c7b2c';
          return (
            <div key={idx} style={{width: '30px', height: '30px', borderRadius: '50%', background: bg, display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#fff', fontWeight: 'bold'}}>{result}</div>
          );
        })}
      </div>
      <div style={{margin: '30px auto 0 auto', background: '#3e5c2c', color: '#fff', borderRadius: '6px', padding: '10px 0', textAlign: 'center', maxWidth: '300px', fontWeight: 'bold', fontSize: '1.1em'}}>
        {nextMatchLabel || club.nextMatch}
      </div>
    </div>
  );
}

export default App;
