      <div style={{margin: '30px auto 0 auto', background: '#3e5c2c', color: '#fff', borderRadius: '6px', padding: '10px 0', textAlign: 'center', maxWidth: '300px', fontWeight: 'bold', fontSize: '1.1em'}}>
        {nextMatchLabel || club.nextMatch}
      </div>
    </div>
  );
}

export default App;
