import React, { useState } from 'react';

const mainBg = {
  background: 'linear-gradient(to bottom, #2c466b 60%, #3e5c2c 100%)',
  minHeight: '100vh',
  color: '#fff',
  fontFamily: 'sans-serif',
  padding: '0',
  margin: '0',
};

const cardStyle = {
  background: '#fff',
  color: '#2c466b',
  borderRadius: '10px',
  padding: '10px 20px',
  margin: '10px',
  minWidth: '80px',
  textAlign: 'center',
  fontWeight: 'bold',
  boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
};

const formatTRY = (v) => new Intl.NumberFormat('tr-TR', { style: 'currency', currency: 'TRY', maximumFractionDigits: 0 }).format(v);

// Static figure icons served from public/figures
const FIG_FILLED = process.env.PUBLIC_URL + '/figures/human_filled_1024.png';
const FIG_OUTLINE = process.env.PUBLIC_URL + '/figures/human_outline_1024.png';

const FanBar = ({ percent = 0, size = 18, gap = 4 }) => {
  const filled = Math.max(0, Math.min(10, Math.floor(percent / 10)));
  return (
    <div style={{display:'flex', alignItems:'center', gap}}>
      {Array.from({length:10}).map((_, i) => (
        <img
          key={i}
          src={i < filled ? FIG_FILLED : FIG_OUTLINE}
          alt=""
          style={{width:size, height:size, objectFit:'contain'}}
        />
      ))}
    </div>
  );
};

function App() {
  const [club] = useState({
    name: 'BENIDORM',
    season: '2025/26',
    league: 'Tercera C',
    date: 'AÃ„Å¸u 2025',
    hfSonu: 5,
    lig: 5,
    money: 12000,
    kadro: 36,
    menajer: 37,
    antrenman: 37,
    altyapi: 35,
    kupalar: 0,
    basarildi: 1,
    taraftarMutlu: 97,
    baskanMutlu: 52,
    stadium: 'Stadyum',
    baskan: 'BaÃ…Å¸kan',
    matchResults: ['M', 'G', 'G', 'G', 'B', 'B'],
    nextMatch: 'Tercera C: Guijuelo (D)',
  });

  const [showFixture, setShowFixture] = useState(false);
  const [showStandings, setShowStandings] = useState(false);
  const [showFinance, setShowFinance] = useState(false);
  const [showSquad, setShowSquad] = useState(false);
  const [showManager, setShowManager] = useState(false);
  const [managerTab, setManagerTab] = useState('records');
  const [showInfo, setShowInfo] = useState(null); // { title, message }
  const [showConfirmFire, setShowConfirmFire] = useState(false);
  const [showTraining, setShowTraining] = useState(false);
  const [trainingRating, setTrainingRating] = useState(37); // shows on card
  const [trainingTarget, setTrainingTarget] = useState(37); // edited in modal
  const [bankBalance, setBankBalance] = useState(club.money);
  const [showYouth, setShowYouth] = useState(false);
  const [youthRating, setYouthRating] = useState(35);
  const [youthTarget, setYouthTarget] = useState(35);
  const [fixture, setFixture] = useState([]); // generated when starting a new game
  const [currentWeek, setCurrentWeek] = useState(0);
  const [currentSlice, setCurrentSlice] = useState(0); // 0: baÃ…Å¸Ã„Â±, 1: ortasÃ„Â±, 2: sonu
  const [nextMatchLabel, setNextMatchLabel] = useState('');
  const [clubName, setClubName] = useState('BENIDORM');
  const [leagueShown, setLeagueShown] = useState('Tercera C');
  const [chairmanName, setChairmanName] = useState('BaÃ…Å¸kan');
  const [showTrophies, setShowTrophies] = useState(false);
  const [trophies, setTrophies] = useState([]); // {name, season, note}
  const [showAchievements, setShowAchievements] = useState(false);
  const [achievements] = useState([
    { title: '500 kiÃ…Å¸ilik taraftar topluluÃ„Å¸u', achieved: true, date: '2025' },
    { title: 'Tercera C Lig AtladÃ„Â±', achieved: false },
    { title: 'Tercera B Lig AtladÃ„Â±', achieved: false },
    { title: 'Tercera A Lig AtladÃ„Â±', achieved: false },
    { title: 'Segunda Reg Lig AtladÃ„Â±', achieved: false },
    { title: 'Primera Reg Lig AtladÃ„Â±', achieved: false },
    { title: 'La Liga 2 Lig AtladÃ„Â±', achieved: false },
    { title: 'La Liga Ã…ÂampiyonlarÃ„Â±', achieved: false },
    { title: "Euroliga'a katÃ„Â±lmaya hak kazandÃ„Â±", achieved: false },
    { title: "Liga de Campeones'a katÃ„Â±lmaya hak kazandÃ„Â±", achieved: false },
  ]);
  const [showRecords, setShowRecords] = useState(false);
  const [records] = useState([
    { icon: 'ÄŸÅ¸ÂÅ¸Ã¯Â¸Â', title: 'En BÃƒÂ¼yÃƒÂ¼k KalabalÃ„Â±k', value: '588', details: 'vs Lealtad, May 2025' },
    { icon: 'ÄŸÅ¸â€œÂ', title: 'Rekor Ã„Â°mza', value: formatTRY(2100), details: 'W Acevedo, Ã…Âub 2024' },
    { icon: 'ÄŸÅ¸â€™Â¶', title: 'SatÃ„Â±Ã…Å¸ Rekoru', value: formatTRY(600), details: 'B Pareja, Tem 2024' },
    { icon: 'ÄŸÅ¸â€˜Å¸', title: 'Oyuncunun En Ãƒâ€¡ok Ãƒâ€¡Ã„Â±ktÃ„Â±Ã„Å¸Ã„Â± MaÃƒÂ§ SayÃ„Â±sÃ„Â±', value: '99', details: 'J Alonso, 2023-2025' },
    { icon: 'ÄŸÅ¸Â¥â€¦', title: 'En Ãƒâ€¡ok Gol (Toplam)', value: '26', details: 'J Alonso, 2023-2025' },
    { icon: 'ÄŸÅ¸ÂÂ¯', title: 'En Ãƒâ€¡ok Gol (Sezon)', value: '14', details: 'J Alonso, 2024/25' },
    { icon: 'ÄŸÅ¸Â§â€˜ÄŸÅ¸ÂÂ¼Ã¢â‚¬ÂÄŸÅ¸Â¦Â²', title: 'En Uzun Hizmet Eden Menajer', value: '90', details: 'oyunlar A Pombo' },
    { icon: 'ÄŸÅ¸â€ºÂ¡Ã¯Â¸Â', title: 'MaÃ„Å¸lup OlunmamÃ„Â±Ã…Å¸ MaÃƒÂ§lar', value: '10', details: 'Tercera C, 2024' },
  ]);
  const [showFans, setShowFans] = useState(false);
  const [showChairman, setShowChairman] = useState(false);
  const [showStadium, setShowStadium] = useState(false);
  const [showStandEditor, setShowStandEditor] = useState(false);
  const [activeStandKey, setActiveStandKey] = useState(null);
  const [standTarget, setStandTarget] = useState(0);
  const [standSpeed, setStandSpeed] = useState('slow'); // 'slow' | 'fast'
  const [stadium, setStadium] = useState({
    name: 'Estadio Municipal',
    stands: {
      north: { name: 'Kuzey TribÃƒÂ¼nÃƒÂ¼', built: 2023, capacity: 100 },
      east:  { name: 'DoÃ„Å¸u TribÃƒÂ¼nÃƒÂ¼',  built: 2023, capacity: 200 },
      south: { name: 'GÃƒÂ¼ney TribÃƒÂ¼nÃƒÂ¼', built: 2023, capacity: 100 },
      west:  { name: 'BatÃ„Â± TribÃƒÂ¼nÃƒÂ¼',  built: 2023, capacity: 200 },
      corners: { name: 'TribÃƒÂ¼n KÃƒÂ¶Ã…Å¸eleri', built: '-', capacity: 0 },
      pitch: { name: 'Saha', quality: 46, max: 99 }
    }
  });
  const [showHistory, setShowHistory] = useState(false);
  const [history] = useState([
    { year: 2025, league: 'Tercera C', pos: 4, cup: 'Sadece Pro uygulamasÃ„Â±nda *', crowd: 369 },
    { year: 2024, league: 'Tercera C', pos: 3, cup: 'Sadece Pro uygulamasÃ„Â±nda *', crowd: 313 },
  ]);

  const calcWeeklyCost = (r) => Math.round(60 + r * 0.65); // simple illustrative formula
  const baseCostForLevel = (lvl) => Math.round(1000 + lvl * 75); // step cost
  const calcUpgradeCost = (from, to) => {
    if (to <= from) return 0;
    let total = 0;
    for (let l = from; l < to; l++) total += baseCostForLevel(l);
    return total;
  };
  // Youth facility costs (slightly different scaling)
  const calcWeeklyCostYouth = (r) => Math.round(55 + r * 0.60);
  const baseCostForYouth = (lvl) => Math.round(900 + lvl * 65);
  const calcUpgradeCostYouth = (from, to) => {
    if (to <= from) return 0;
    let total = 0;
    for (let l = from; l < to; l++) total += baseCostForYouth(l);
    return total;
  };
  // Onboarding state
  const [showOnboarding, setShowOnboarding] = useState(false);
  const [teams2Lig, setTeams2Lig] = useState([]); // combined list with group info
  const [teams2LigBeyaz, setTeams2LigBeyaz] = useState([]);
  const [teams2LigKirmizi, setTeams2LigKirmizi] = useState([]);
  const [selectedTeam, setSelectedTeam] = useState('');
  const [inputChair, setInputChair] = useState('');
  const [groupOfClub, setGroupOfClub] = useState(null); // 'Beyaz' | 'KÃ„Â±rmÃ„Â±zÃ„Â±'

  React.useEffect(() => {
    try {
      const raw = localStorage.getItem('profile');
      if (raw) {
        const p = JSON.parse(raw);
        if (p && p.team) {
          setClubName(p.team);
          setLeagueShown(p.league || '2. Lig'); if (p.group) setGroupOfClub(p.group);
          setChairmanName(p.chair || 'BaÃ…Å¸kan');
          if (typeof p.bank === 'number') setBankBalance(p.bank);
          return;
        }
      }
    } catch {}
    fetch(process.env.PUBLIC_URL + '/turkish_leagues_with_values.csv')
      .then(r => r.text())
      .then(txt => {
        const rows = txt.split(/\r?\n/).slice(1).map(l => l.split(','));
        const itemsAll = rows
          .filter(r => r[1] && r[1].includes('2. Lig'))
          .map(r => {
            const leagueStr = (r[1] || '').trim();
            const group = leagueStr.includes('Beyaz') ? 'Beyaz' : 'KÃ„Â±rmÃ„Â±zÃ„Â±';
            return { team: r[0], league: leagueStr, value: parseFloat(r[2]) || 0, group };
          });
        const beyaz = itemsAll.filter(i => i.group === 'Beyaz');
        const kirmizi = itemsAll.filter(i => i.group === 'KÃ„Â±rmÃ„Â±zÃ„Â±');
        setTeams2Lig(itemsAll);
        setTeams2LigBeyaz(beyaz);
        setTeams2LigKirmizi(kirmizi);
        setSelectedTeam(itemsAll[0]?.team || '');
        setShowOnboarding(true);
      })
      .catch(() => setShowOnboarding(true));
  }, []);

  const generateFixture = (team, opponents) => {
    // simple single round schedule starting week 0, alternate home/away
    const list = [];
    const weeks = Math.min(20, opponents.length); // cap for demo
    for (let i = 0; i < weeks; i++) {
      const opp = opponents[i];
      const home = i % 2 === 0; // alternate
      list.push({ hft: i + 1, rakip: `${opp} (${home ? 'D' : 'E'})`, a: '-', y: '-', result: '', crowd: '-', pos: '-' });
    }
    return list;
  };

  // Zaman akÃ„Â±Ã…Å¸Ã„Â± yardÃ„Â±mcÃ„Â±larÃ„Â±
  const WEEK_SLICES = ['Hafta baÃ…Å¸Ã„Â±', 'Hafta ortasÃ„Â±', 'Hafta sonu'];
  const advanceSlice = () => {
    setCurrentSlice((s) => {
      if (s < 2) return s + 1;
      setCurrentWeek((w) => w + 1);
      return 0;
    });
  };

  const findFixtureForWeek = (week) => fixture.find((f) => f.hft === week && !f.result);
  const nextAction = () => {
    if (currentSlice < 2) return { type: 'advance', label: 'Devam' };
    const f = findFixtureForWeek(currentWeek);
    if (f) return { type: 'match', label: f.rakip };
    return { type: 'advance', label: 'Devam' };
  };

  // MaÃƒÂ§ simÃƒÂ¼lasyonu (basit)
  const [showMatch, setShowMatch] = useState(false);
  const [matchMinute, setMatchMinute] = useState(0);
  const [matchEvents, setMatchEvents] = useState([]); // {min, text}
  const [matchScore, setMatchScore] = useState({ us: 0, them: 0 });
  const [activeFixtureIndex, setActiveFixtureIndex] = useState(-1);
  const [matchRunning, setMatchRunning] = useState(false);

  const startMatch = () => {
    setMatchRunning(true);
    setMatchMinute(0);
    setMatchEvents([{ min: 0, text: 'Başlama vuruşu', type: 'start' }]);
    setMatchXG({ us: 0, them: 0 });
  };

  React.useEffect(() => {
    if (!showMatch || !matchRunning) return;
    const id = setInterval(() => {
      setMatchMinute((m) => {
        const nm = Math.min(90, m + Math.ceil(Math.random() * 4));
                // Rastgele olaylar
        const roll = Math.random();
        if (roll < 0.22) {
          const forUs = Math.random() < 0.55;
          let xg = Math.max(0.02, Math.random() * 0.45);
          const isGoal = Math.random() < xg * 0.9;
          if (isGoal) {
            xg = Math.max(xg, 0.25);
            setMatchScore((s) => ({ us: s.us + (forUs ? 1 : 0), them: s.them + (forUs ? 0 : 1) }));
            setMatchEvents((ev) => [{ min: nm, text: `${forUs ? 'Gol Benidorm' : 'Gol Rakip'}`, type: 'goal' }, ...ev]);
          } else {
            const shotTexts = ['Tehlikeli atak', 'Şut', 'Korner sonrası şut', 'Uzaktan şut'];
            const t = shotTexts[Math.floor(Math.random() * shotTexts.length)];
            setMatchEvents((ev) => [{ min: nm, text: t, type: 'shot' }, ...ev]);
          }
          setMatchXG((x) => ({ us: x.us + (forUs ? xg : 0), them: x.them + (forUs ? 0 : xg) }));
        } else if (roll < 0.26) {
          const forUs = Math.random() < 0.5;
          setMatchEvents((ev) => [{ min: nm, text: `${forUs ? 'Sarı kart Benidorm' : 'Sarı kart Rakip'}`, type: 'yellow' }, ...ev]);
        } else if (roll < 0.27) {
          const forUs = Math.random() < 0.45;
          setMatchEvents((ev) => [{ min: nm, text: `${forUs ? 'Kırmızı kart Benidorm' : 'Kırmızı kart Rakip'}`, type: 'red' }, ...ev]);
        } else if (roll < 0.30) {
          const texts = ['Sakatlık nedeniyle oyun durdu', 'Tedavi için oyun durdu', 'VAR incelemesi'];
          const t = texts[Math.floor(Math.random() * texts.length)];
          setMatchEvents((ev) => [{ min: nm, text: t, type: 'stoppage' }, ...ev]);
        }
if (nm >= 90) {
          clearInterval(id);
          setMatchRunning(false);
          // MaÃƒÂ§ bitti => sonucu fikstÃƒÂ¼re iÃ…Å¸le, zamanÃ„Â± ilerlet
          if (activeFixtureIndex >= 0) {
            setFixture((fx) => {
              const copy = [...fx];
              const row = { ...copy[activeFixtureIndex] };
              row.a = matchScore.us;
              row.y = matchScore.them;
              row.result = matchScore.us > matchScore.them ? 'G' : (matchScore.us < matchScore.them ? 'M' : 'B');
              copy[activeFixtureIndex] = row;
              return copy;
            });
          }
          advanceSlice();
        }
        return nm;
      });
    }, 900);
    return () => clearInterval(id);
  }, [showMatch, matchRunning, activeFixtureIndex, matchScore.us, matchScore.them]);

  const openMatchIfDue = () => {
    const act = nextAction();
    if (act.type === 'advance') {
      advanceSlice();
      return;
    }
    // Find index of fixture for current week
    const idx = fixture.findIndex((f) => f.hft === currentWeek && !f.result);
    if (idx >= 0) {
      setActiveFixtureIndex(idx);
      setMatchMinute(0);
      setMatchEvents([]);
      setMatchScore({ us: 0, them: 0 });
      setShowMatch(true);
      setMatchRunning(false);
    }
  };

  // Stadium helpers
  const totalCapacity = () => {
    const { north, east, south, west, corners } = stadium.stands;
    return north.capacity + east.capacity + south.capacity + west.capacity + corners.capacity;
  };
  const standCostPerSeat = (speed) => speed === 'fast' ? 9 : 7; // simple demo cost
  const standWeeks = (from, to, speed) => {
    const delta = Math.max(0, to - from);
    const perWeek = speed === 'fast' ? 80 : 50;
    return Math.max(1, Math.ceil(delta / perWeek));
  };

  const fixtureData = fixture;

  const getResultColor = (result) => {
    if (result === 'M') return '#c00';
    if (result === 'G') return '#2c7b2c';
    if (result === 'B') return '#b88c2c';
    return '#ccc';
  };

  const [standingsData, setStandingsData] = useState([]); // for non-2. Lig use
  const [standingsBeyaz, setStandingsBeyaz] = useState([]);
  const [standingsKirmizi, setStandingsKirmizi] = useState([]);

  const currentLeaguePosition = () => {
    if (leagueShown === '2. Lig') {
      const arr = groupOfClub === 'Beyaz' ? standingsBeyaz : standingsKirmizi;
      const idx = arr.findIndex(r => r.team === clubName);
      return idx >= 0 ? (idx + 1) : '-';
    }
    const idx = standingsData.findIndex(r => r.team === clubName);
    return idx >= 0 ? (idx + 1) : '-';
  };

  return (
    <div style={mainBg}>
      <div style={{padding: '20px 0', textAlign: 'center'}}>
        <div style={{fontSize: '1.5em', fontWeight: 'bold'}}>FOOTBALL CHAIRMAN</div>
        <div style={{margin: '10px auto', display: 'inline-block', background: '#fff', color: '#2c466b', borderRadius: '8px', padding: '5px 30px', fontSize: '2em', fontWeight: 'bold'}}>{clubName}</div>
        <div style={{marginTop: '5px', fontSize: '1em'}}>{club.season} &nbsp;|&nbsp; {leagueShown} &nbsp;|&nbsp; {club.date}</div>
      </div>
      <div style={{display: 'flex', justifyContent: 'center', flexWrap: 'wrap', gap: '10px'}}>
        <button style={{...cardStyle, cursor: 'pointer'}} onClick={() => setShowFixture(true)}>
          HF SONU<br/><span style={{fontSize: '1.5em'}}>{currentWeek}</span>
        </button>
        <button style={{...cardStyle, cursor: 'pointer'}} onClick={() => setShowStandings(true)}>
          LÄ°G<br/><span style={{fontSize: '1.5em'}}>{currentLeaguePosition()}.</span>
        </button>
        <button style={{...cardStyle, color: '#2c7b2c', cursor: 'pointer'}} onClick={() => setShowFinance(true)}>
          PARA<br/><span style={{fontSize: '1.5em', color: '#2c7b2c'}}>{formatTRY(bankBalance)}</span>
        </button>
      </div>

      {/* Squad Modal */}
      {showSquad && (
        <div style={{position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(44,70,107,0.95)', zIndex: 1000, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center'}}>
          <div style={{background: '#fff', color: '#2c466b', borderRadius: '12px', padding: '20px', minWidth: '340px', maxWidth: '95vw', maxHeight: '85vh', overflowY: 'auto', boxShadow: '0 2px 16px rgba(0,0,0,0.2)'}}>
            <div style={{fontSize: '1.3em', fontWeight: 'bold', textAlign: 'center', marginBottom: '10px'}}>KADRO</div>
            <table style={{width: '100%', borderCollapse: 'collapse', fontSize: '1em'}}>
              <thead>
                <tr style={{background: '#e6f4e6'}}>
                  <th style={{padding: '6px', textAlign: 'left'}}>#</th>
                  <th style={{padding: '6px', textAlign: 'left'}}>Oyuncu</th>
                  <th style={{padding: '6px', textAlign: 'center'}}>Mevki</th>
                  <th style={{padding: '6px', textAlign: 'center'}}>YaÃ…Å¸</th>
                  <th style={{padding: '6px', textAlign: 'center'}}>OVR</th>
                  <th style={{padding: '6px', textAlign: 'right'}}>MaaÃ…Å¸</th>
                </tr>
              </thead>
              <tbody>
                {[
                  { no: 1, name: 'J. Perez', pos: 'KL', age: 29, ovr: 63, wage: 120 },
                  { no: 2, name: 'M. Garcia', pos: 'STP', age: 27, ovr: 66, wage: 140 },
                  { no: 3, name: 'A. Lopez', pos: 'STP', age: 24, ovr: 64, wage: 110 },
                  { no: 4, name: 'C. Silva', pos: 'OS', age: 26, ovr: 68, wage: 160 },
                  { no: 5, name: 'D. Ruiz', pos: 'OS', age: 21, ovr: 62, wage: 90 },
                  { no: 6, name: 'E. Torres', pos: 'FS', age: 25, ovr: 67, wage: 150 },
                  { no: 7, name: 'B. Nunez', pos: 'FS', age: 28, ovr: 65, wage: 130 },
                  { no: 8, name: 'L. Gomez', pos: 'Ãƒâ€“N', age: 23, ovr: 61, wage: 85 },
                  { no: 9, name: 'R. Diaz', pos: 'FOR', age: 22, ovr: 69, wage: 170 },
                  { no: 10, name: 'K. Marin', pos: 'FOR', age: 20, ovr: 58, wage: 70 },
                ].map((p, idx) => (
                  <tr key={idx} style={{background: idx % 2 === 0 ? '#f5f5f5' : '#e6f4e6'}}>
                    <td style={{padding: '6px'}}>{p.no}</td>
                    <td style={{padding: '6px'}}>{p.name}</td>
                    <td style={{padding: '6px', textAlign: 'center'}}>{p.pos}</td>
                    <td style={{padding: '6px', textAlign: 'center'}}>{p.age}</td>
                    <td style={{padding: '6px', textAlign: 'center'}}>{p.ovr}</td>
                    <td style={{padding: '6px', textAlign: 'right'}}>{formatTRY(p.wage)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div style={{display: 'flex', justifyContent: 'space-between', marginTop: '16px'}}>
              <button style={{background: '#3e5c2c', color: '#fff', border: 'none', borderRadius: '6px', padding: '8px 24px', fontWeight: 'bold', fontSize: '1em', cursor: 'pointer'}} onClick={() => setShowSquad(false)}>Geri</button>
              <button style={{background: '#3e5c2c', color: '#fff', border: 'none', borderRadius: '6px', padding: '8px 24px', fontWeight: 'bold', fontSize: '1em', cursor: 'pointer'}}>TaktiÃ„Å¸e Git</button>
            </div>
          </div>
        </div>
      )}

      {/* Onboarding Modal */}
