import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { allSquads } from '../data';

const MatchPlayModal = ({ setShowMatchPlay, club, matchData, onMatchEnd }) => {
  const [currentMinute, setCurrentMinute] = useState(0);
  const [homeScore, setHomeScore] = useState(0);
  const [awayScore, setAwayScore] = useState(0);
  const [matchEvents, setMatchEvents] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isFastForward, setIsFastForward] = useState(false);
  const [matchEnded, setMatchEnded] = useState(false);

  const homeTeam = club.name;
  const awayTeam = matchData.awayTeam;

  const generateMatchEvent = (minute) => {
    // Ger√ßek oyuncu isimlerini al - t√ºm liglerde ara
    let homeSquad = null;
    let awaySquad = null;
    
    // T√ºm liglerde takƒ±mlarƒ± ara
    Object.keys(allSquads).forEach(league => {
      if (allSquads[league][homeTeam]) {
        homeSquad = allSquads[league][homeTeam];
      }
      if (allSquads[league][awayTeam]) {
        awaySquad = allSquads[league][awayTeam];
      }
    });
    
    const homePlayers = homeSquad ? [...homeSquad.firstTeam, ...homeSquad.substitutes].map(player => player.name) : [];
    const awayPlayers = awaySquad ? [...awaySquad.firstTeam, ...awaySquad.substitutes].map(player => player.name) : [];
    
    // Eƒüer kadro yoksa varsayƒ±lan isimler kullan
    const defaultHomePlayers = ['Alfaro', 'Valcarcel', 'Monreal', 'Olmo', 'Puente', 'Arroyo', 'Segarra', 'Ordoez', 'PGA', 'Jodar', 'Alonso'];
    const defaultAwayPlayers = ['Kaleci', 'Defans1', 'Defans2', 'Defans3', 'Defans4', 'Orta1', 'Orta2', 'Orta3', 'Forvet1', 'Forvet2', 'Forvet3'];
    
    const finalHomePlayers = homePlayers.length > 0 ? homePlayers : defaultHomePlayers;
    const finalAwayPlayers = awayPlayers.length > 0 ? awayPlayers : defaultAwayPlayers;
    
    // Daha ger√ßek√ßi olay olasƒ±lƒ±klarƒ±
    const random = Math.random();
    let event;
    
    if (random < 0.15) { // %15 gol
      const team = Math.random() < 0.5 ? 'home' : 'away';
      const player = team === 'home' ? 
        finalHomePlayers[Math.floor(Math.random() * finalHomePlayers.length)] :
        finalAwayPlayers[Math.floor(Math.random() * finalAwayPlayers.length)];
      const teamName = team === 'home' ? homeTeam : awayTeam;
      
      event = {
        minute: minute,
        type: 'goal',
        team: team,
        description: `${teamName}li ${player} muhte≈üem bir gol attƒ±!`,
        player: player
      };
      
      if (team === 'home') {
        setHomeScore(prev => prev + 1);
      } else {
        setAwayScore(prev => prev + 1);
      }
    } else if (random < 0.25) { // %10 sarƒ± kart
      const team = Math.random() < 0.5 ? 'home' : 'away';
      const player = team === 'home' ? 
        finalHomePlayers[Math.floor(Math.random() * finalHomePlayers.length)] :
        finalAwayPlayers[Math.floor(Math.random() * finalAwayPlayers.length)];
      const teamName = team === 'home' ? homeTeam : awayTeam;
      
      event = {
        minute: minute,
        type: 'yellow',
        team: team,
        description: `${teamName}li ${player} sert m√ºdahalesi nedeniyle sarƒ± kart g√∂rd√º!`,
        player: player
      };
    } else if (random < 0.28) { // %3 kƒ±rmƒ±zƒ± kart
      const team = Math.random() < 0.5 ? 'home' : 'away';
      const player = team === 'home' ? 
        finalHomePlayers[Math.floor(Math.random() * finalHomePlayers.length)] :
        finalAwayPlayers[Math.floor(Math.random() * finalAwayPlayers.length)];
      const teamName = team === 'home' ? homeTeam : awayTeam;
      
      event = {
        minute: minute,
        type: 'red',
        team: team,
        description: `${teamName}li ${player} √ßok sert m√ºdahalesi nedeniyle kƒ±rmƒ±zƒ± kart g√∂rd√º!`,
        player: player
      };
    } else if (random < 0.32) { // %4 sakatlƒ±k
      const team = Math.random() < 0.5 ? 'home' : 'away';
      const player = team === 'home' ? 
        finalHomePlayers[Math.floor(Math.random() * finalHomePlayers.length)] :
        finalAwayPlayers[Math.floor(Math.random() * finalAwayPlayers.length)];
      const teamName = team === 'home' ? homeTeam : awayTeam;
      const injuries = ['ayak bileƒüi burkulmasƒ±', 'kas yƒ±rtƒ±ƒüƒ±', 'menisk√ºs yƒ±rtƒ±ƒüƒ±', '√ßapraz baƒü kopmasƒ±', 'kƒ±rƒ±k ayak'];
      const injury = injuries[Math.floor(Math.random() * injuries.length)];
      
      event = {
        minute: minute,
        type: 'injury',
        team: team,
        description: `${teamName}li ${player} ${injury} nedeniyle sakatlandƒ±!`,
        player: player,
        injuryType: injury
      };
    } else if (random < 0.40) { // %8 asist
      const team = Math.random() < 0.5 ? 'home' : 'away';
      const player = team === 'home' ? 
        finalHomePlayers[Math.floor(Math.random() * finalHomePlayers.length)] :
        finalAwayPlayers[Math.floor(Math.random() * finalAwayPlayers.length)];
      const teamName = team === 'home' ? homeTeam : awayTeam;
      
      event = {
        minute: minute,
        type: 'assist',
        team: team,
        description: `${teamName}li ${player} m√ºkemmel bir asist yaptƒ±!`,
        player: player
      };
    } else { // %40 normal oyun
      const team = Math.random() < 0.5 ? 'home' : 'away';
      const teamName = team === 'home' ? homeTeam : awayTeam;
      const actions = ['kaleci m√ºkemmel kurtarƒ±≈ü yaptƒ±', 'kale direƒüinden d√∂nd√º', 'ofsayt pozisyonu', 'korner kazandƒ±', 'serbest vuru≈ü kazandƒ±'];
      const action = actions[Math.floor(Math.random() * actions.length)];
      
      event = {
        minute: minute,
        type: 'action',
        team: team,
        description: `${teamName} ${action}!`
      };
    }

    setMatchEvents(prev => [...prev, event]);
  };

  const advanceTime = () => {
    if (currentMinute < 90) {
      const newMinute = currentMinute + 1;
      setCurrentMinute(newMinute);

      // Rastgele olay √ºretimi (d√º≈ü√ºk olasƒ±lƒ±k)
      if (Math.random() < 0.1) {
        generateMatchEvent(newMinute);
      }

      // Yarƒ± zaman kontrol√º
      if (newMinute === 45) {
        setMatchEvents(prev => [...prev, { minute: 45, type: 'half', description: 'ƒ∞lk Yarƒ± Sonu' }]);
      }
    } else {
      // Ma√ß bitti
      setIsPlaying(false);
      setMatchEnded(true);
    }
  };

  const fastForwardToEnd = () => {
    setIsFastForward(true);
    setCurrentMinute(90);
    
    // Son dakikaya kadar hƒ±zlƒ± ilerlet
    for (let i = currentMinute + 1; i <= 90; i++) {
      if (Math.random() < 0.15) {
        generateMatchEvent(i);
      }
    }
    
    setIsFastForward(false);
    setIsPlaying(false);
    setMatchEnded(true);
  };

  useEffect(() => {
    if (isPlaying && !isFastForward) {
      const interval = setInterval(() => {
        advanceTime();
      }, 500); // 1 saniyeden 500ms'ye d√º≈ü√ºrd√ºk
      return () => clearInterval(interval);
    }
  }, [isPlaying, currentMinute, isFastForward]);

  const getEventIcon = (type) => {
    switch (type) {
      case 'goal': return '‚öΩ';
      case 'yellow': return 'üü®';
      case 'red': return 'üü•';
      case 'injury': return 'üè•';
      case 'assist': return 'üéØ';
      case 'action': return '‚ö°';
      case 'half': return '‚è∏Ô∏è';
      default: return 'üìù';
    }
  };

  return (
    <div className="fc-modal-backdrop">
      <div className="fc-modal" style={{ maxWidth: '600px', maxHeight: '90vh', overflow: 'auto' }}>
        <div style={{ 
          background: 'linear-gradient(135deg, #2c466b, #1a365d)',
          padding: '20px',
          borderRadius: '8px 8px 0 0',
          color: 'white',
          textAlign: 'center'
        }}>
          <div style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '10px' }}>
            {homeTeam} vs {awayTeam}
