          <div style={{ fontSize: '16px' }}>
            {matchData.type === 'friendly' ? 'Hazƒ±rlƒ±k Ma√ßƒ±' : club.league} - {currentMinute} dk
          </div>
        </div>

        <div className="fc-panel" style={{ padding: '20px' }}>
          {/* Skor Paneli */}
          <div style={{ 
            background: '#f5f5f5', 
            padding: '20px', 
            borderRadius: '8px',
            marginBottom: '20px',
            textAlign: 'center'
          }}>
            <div style={{ fontSize: '32px', fontWeight: 'bold', color: 'var(--blue)' }}>
              {homeScore} - {awayScore}
            </div>
            <div style={{ fontSize: '18px', marginTop: '10px' }}>
              {homeTeam} vs {awayTeam}
            </div>
          </div>

          {/* Ma√ß Kontrolleri */}
          {!matchEnded ? (
            <div style={{ 
              display: 'flex', 
              justifyContent: 'center', 
              gap: '10px',
              marginBottom: '20px'
            }}>
              <button
                className="fc-btn"
                onClick={() => setIsPlaying(!isPlaying)}
                style={{
                  background: isPlaying ? 'var(--loss)' : 'var(--win)',
                  color: 'white',
                  padding: '10px 20px',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: 'bold'
                }}
              >
                {isPlaying ? 'Duraklat' : 'Ba≈ülat'}
              </button>
              <button
                className="fc-btn"
                onClick={fastForwardToEnd}
                style={{
                  background: 'var(--blue)',
                  color: 'white',
                  padding: '10px 20px',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: 'bold'
                }}
              >
                Sona Atla
              </button>
            </div>
          ) : (
            <div style={{ 
              display: 'flex', 
              justifyContent: 'center', 
              gap: '10px',
              marginBottom: '20px'
            }}>
              <button
                className="fc-btn"
                onClick={() => {
                  onMatchEnd({ homeScore, awayScore, events: matchEvents });
                  setShowMatchPlay(false);
                }}
                style={{
                  background: 'var(--win)',
                  color: 'white',
                  padding: '10px 20px',
                  border: 'none',
                  borderRadius: '6px',
                  cursor: 'pointer',
                  fontSize: '14px',
                  fontWeight: 'bold'
                }}
              >
                Devam Et
              </button>
            </div>
          )}

          {/* Takƒ±m Kadrolarƒ± */}
          <div style={{ marginBottom: '20px' }}>
            <h4 style={{ 
              margin: '0 0 15px 0', 
              textAlign: 'center', 
              color: 'var(--blue)',
              fontSize: '16px'
            }}>
              TAKIM KADROLARI
            </h4>
            
            <div style={{ 
              display: 'grid',
              gridTemplateColumns: '1fr 1fr',
              gap: '20px',
              maxHeight: '400px',
              overflowY: 'auto'
            }}>
              {/* Ev Sahibi Takƒ±m */}
              <div style={{ 
                background: '#f9f9f9',
                borderRadius: '8px',
                padding: '15px'
              }}>
                <h5 style={{ 
                  margin: '0 0 10px 0', 
                  textAlign: 'center', 
                  color: 'var(--blue)',
                  fontSize: '14px',
                  fontWeight: 'bold'
                }}>
                  {homeTeam}
                </h5>
                
                {/* ƒ∞lk 11 */}
                <div style={{ marginBottom: '15px' }}>
                  <div style={{ 
                    fontSize: '12px', 
                    fontWeight: 'bold', 
                    color: 'var(--blue)',
                    marginBottom: '5px'
                  }}>
                    ƒ∞LK 11
                  </div>
                  <div style={{ fontSize: '11px', lineHeight: '1.3' }}>
                    {renderTeamSquad(homeTeam, 'firstTeam')}
                  </div>
                </div>
                
                {/* Yedekler */}
                <div style={{ marginBottom: '15px' }}>
                  <div style={{ 
                    fontSize: '12px', 
                    fontWeight: 'bold', 
                    color: 'var(--blue)',
                    marginBottom: '5px'
                  }}>
                    YEDEKLER
                  </div>
                  <div style={{ fontSize: '11px', lineHeight: '1.3' }}>
                    {renderTeamSquad(homeTeam, 'substitutes')}
                  </div>
                </div>
                
                {/* Kart Cezalƒ±sƒ± ve Sakatlar */}
                <div>
                  <div style={{ 
                    fontSize: '12px', 
                    fontWeight: 'bold', 
                    color: 'var(--blue)',
                    marginBottom: '5px'
                  }}>
                    CEZALI/SAKAT
                  </div>
                  <div style={{ fontSize: '11px', lineHeight: '1.3', color: '#666' }}>
                    {renderSuspendedInjured(homeTeam)}
                  </div>
                </div>
              </div>
              
              {/* Deplasman Takƒ±mƒ± */}
              <div style={{ 
                background: '#f9f9f9',
                borderRadius: '8px',
                padding: '15px'
              }}>
                <h5 style={{ 
                  margin: '0 0 10px 0', 
                  textAlign: 'center', 
                  color: 'var(--blue)',
                  fontSize: '14px',
                  fontWeight: 'bold'
                }}>
                  {awayTeam}
                </h5>
                
                {/* ƒ∞lk 11 */}
                <div style={{ marginBottom: '15px' }}>
                  <div style={{ 
                    fontSize: '12px', 
                    fontWeight: 'bold', 
                    color: 'var(--blue)',
                    marginBottom: '5px'
                  }}>
                    ƒ∞LK 11
                  </div>
                  <div style={{ fontSize: '11px', lineHeight: '1.3' }}>
                    {renderTeamSquad(awayTeam, 'firstTeam')}
                  </div>
                </div>
                
                {/* Yedekler */}
                <div style={{ marginBottom: '15px' }}>
                  <div style={{ 
                    fontSize: '12px', 
                    fontWeight: 'bold', 
                    color: 'var(--blue)',
                    marginBottom: '5px'
                  }}>
                    YEDEKLER
                  </div>
                  <div style={{ fontSize: '11px', lineHeight: '1.3' }}>
                    {renderTeamSquad(awayTeam, 'substitutes')}
                  </div>
                </div>
                
                {/* Kart Cezalƒ±sƒ± ve Sakatlar */}
                <div>
                  <div style={{ 
                    fontSize: '12px', 
                    fontWeight: 'bold', 
                    color: 'var(--blue)',
                    marginBottom: '5px'
                  }}>
                    CEZALI/SAKAT
                  </div>
                  <div style={{ fontSize: '11px', lineHeight: '1.3', color: '#666' }}>
                    {renderSuspendedInjured(awayTeam)}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>


      </div>
    </div>
  );
};

MatchPlayModal.propTypes = {
  setShowMatchPlay: PropTypes.func.isRequired,
  club: PropTypes.object.isRequired,
