import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { allSquads } from '../data';

const SquadModal = ({ setShowSquad, club, selectedTeam = null, playerStats = {} }) => {
  const [activeTab, setActiveTab] = useState('squad'); // 'squad', 'stats'
  // Eğer selectedTeam varsa o takımın kadrosunu göster, yoksa kendi kadrosunu
  const targetTeam = selectedTeam || club.name;
  const targetSquad = selectedTeam ? (() => {
    // Tüm liglerde takımı ara
    let foundSquad = null;
    Object.keys(allSquads).forEach(league => {
      if (allSquads[league][selectedTeam]) {
        foundSquad = allSquads[league][selectedTeam];
      }
    });
    return foundSquad;
  })() : club.squad;
  
  if (!targetSquad) {
    return (
      <div className="fc-modal-backdrop">
        <div className="fc-modal">
          <h3>KADRO BİLGİSİ YOK</h3>
          <p>{targetTeam} takımı için kadro bilgisi bulunamadı.</p>
          <div className="fc-footerbar">
            <button className="fc-btn" onClick={() => setShowSquad(false)}>Geri</button>
          </div>
        </div>
      </div>
    );
  }

  const { firstTeam, substitutes } = targetSquad;

  const renderTabs = () => (
    <div style={{ display: 'flex', marginBottom: '20px' }}>
      <button
        onClick={() => setActiveTab('squad')}
        style={{
          flex: 1,
          padding: '10px',
          background: activeTab === 'squad' ? 'var(--win)' : '#fff',
          color: activeTab === 'squad' ? '#fff' : '#666',
          border: '1px solid #ddd',
          cursor: 'pointer',
          fontSize: '14px',
          fontWeight: 'bold'
        }}
      >
        KADRO
      </button>
      <button
        onClick={() => setActiveTab('stats')}
        style={{
          flex: 1,
          padding: '10px',
          background: activeTab === 'stats' ? 'var(--win)' : '#fff',
          color: activeTab === 'stats' ? '#fff' : '#666',
          border: '1px solid #ddd',
          cursor: 'pointer',
          fontSize: '14px',
          fontWeight: 'bold'
        }}
      >
        İSTATİSTİKLER
      </button>
    </div>
  );

  const renderStatsTab = () => {
    const playersWithCards = Object.entries(playerStats)
      .filter(([playerName, stats]) => stats.yellowCards > 0 || stats.redCards > 0)
      .sort((a, b) => (b[1].redCards * 2 + b[1].yellowCards) - (a[1].redCards * 2 + a[1].yellowCards));

    const injuredPlayers = Object.entries(playerStats)
      .filter(([playerName, stats]) => stats.injuries && stats.injuries.length > 0)
      .sort((a, b) => b[1].injuries.length - a[1].injuries.length);

    const playersWithGoals = Object.entries(playerStats)
      .filter(([playerName, stats]) => (stats.goals > 0 || stats.assists > 0))
      .sort((a, b) => (b[1].goals * 3 + b[1].assists) - (a[1].goals * 3 + a[1].assists));

    return (
      <div>
        <h4 style={{ margin: '0 0 15px 0', textAlign: 'center', color: 'var(--blue)' }}>
          OYUNCU İSTATİSTİKLERİ
        </h4>
        
        {/* Kartlar */}
        <div style={{ marginBottom: '20px' }}>
          <h5 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>KART İSTATİSTİKLERİ</h5>
          {playersWithCards.length === 0 ? (
            <div style={{ textAlign: 'center', color: '#666', padding: '20px' }}>
              Henüz kart gören oyuncu yok
            </div>
          ) : (
            <table className="fc-table">
              <thead>
                <tr>
                  <th>Oyuncu</th>
                  <th>🟨 Sarı</th>
                  <th>🟥 Kırmızı</th>
                  <th>Ceza Durumu</th>
                </tr>
              </thead>
              <tbody>
                {playersWithCards.map(([playerName, stats]) => {
                  const totalYellow = stats.yellowCards || 0;
                  const totalRed = stats.redCards || 0;
                  const yellowSuspension = Math.floor(totalYellow / 4);
                  const redSuspension = totalRed * (2 + Math.floor(Math.random() * 3)); // 2-4 maç
                  const totalSuspension = yellowSuspension + redSuspension;
                  
                  return (
                    <tr key={playerName}>
                      <td>{playerName}</td>
                      <td>{totalYellow}</td>
                      <td>{totalRed}</td>
                      <td>
                        {totalSuspension > 0 ? (
                          <span style={{ 
                            background: 'var(--loss)', 
                            color: '#fff', 
                            padding: '2px 8px', 
                            borderRadius: '4px',
                            fontSize: '12px'
                          }}>
                            {totalSuspension} maç ceza
                          </span>
                        ) : (
                          <span style={{ color: 'var(--win)' }}>Temiz</span>
                        )}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>

        {/* Sakatlıklar */}
        <div style={{ marginBottom: '20px' }}>
          <h5 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>SAKATLIK İSTATİSTİKLERİ</h5>
          {injuredPlayers.length === 0 ? (
            <div style={{ textAlign: 'center', color: '#666', padding: '20px' }}>
              Henüz sakatlanan oyuncu yok
            </div>
          ) : (
            <div>
              {injuredPlayers.map(([playerName, stats]) => (
                <div key={playerName} style={{ 
                  marginBottom: '10px',
                  padding: '10px',
                  background: '#f5f5f5',
                  borderRadius: '6px'
                }}>
                  <div style={{ fontWeight: 'bold', marginBottom: '5px' }}>
                    {playerName}
                  </div>
                  {stats.injuries.map((injury, index) => (
                    <div key={index} style={{ 
                      marginBottom: '3px',
                      fontSize: '13px',
                      color: '#666'
                    }}>
                      • {injury.type} - {injury.matchesOut} maç yok
                    </div>
                  ))}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Gol/Asist */}
        <div>
          <h5 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>GOL VE ASİST İSTATİSTİKLERİ</h5>
          {playersWithGoals.length === 0 ? (
            <div style={{ textAlign: 'center', color: '#666', padding: '20px' }}>
              Henüz gol/asist yapan oyuncu yok
            </div>
          ) : (
            <table className="fc-table">
              <thead>
                <tr>
                  <th>Oyuncu</th>
                  <th>⚽ Gol</th>
                  <th>🎯 Asist</th>
                  <th>Toplam Puan</th>
                </tr>
              </thead>
              <tbody>
                {playersWithGoals.map(([playerName, stats]) => {
                  const goals = stats.goals || 0;
                  const assists = stats.assists || 0;
                  const totalPoints = goals * 3 + assists;
                  
                  return (
                    <tr key={playerName}>
                      <td>{playerName}</td>
                      <td>{goals}</td>
                      <td>{assists}</td>
                      <td style={{ fontWeight: 'bold', color: 'var(--win)' }}>
                        {totalPoints}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>
      </div>
    );
  };

  const renderFormFitnessMorale = (formData) => {
    return formData.map((item, index) => {
      if (item.type === 'positive') {
        return <span key={index} style={{ color: 'green', fontWeight: 'bold' }}>+{item.value}</span>;
      } else if (item.type === 'negative') {
        return <span key={index} style={{ color: 'red', fontWeight: 'bold' }}>-{item.value}</span>;
      } else {
        return <span key={index} style={{ color: '#999' }}>-</span>;
      }
    });
  };

  const renderPlayerRow = (player) => (
    <tr key={player.id}>
      <td style={{ textAlign: 'left' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <div style={{ 
            width: '24px', 
            height: '24px', 
            borderRadius: '50%', 
            background: '#ddd',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '10px',
            fontWeight: 'bold'
          }}>
            {player.name.split(' ')[0][0]}
          </div>
          {player.name}
        </div>
      </td>
      <td>
        <span style={{ 
          background: getPositionColor(player.position),
          color: '#fff',
          padding: '2px 6px',
          borderRadius: '4px',
          fontSize: '12px',
          fontWeight: 'bold'
        }}>
          {player.position}
        </span>
      </td>
      <td style={{ fontWeight: 'bold' }}>{player.rating}</td>
      <td>
        <div style={{ display: 'flex', gap: '4px' }}>
          {renderFormFitnessMorale(player.form)}
        </div>
      </td>
      <td>{player.age}</td>
                      <td style={{ fontWeight: 'bold', color: 'var(--blue)' }}>
                  {player.salary?.toLocaleString() || '0'} TL
                </td>
      <td>{player.contractEnd}</td>
    </tr>
  );

  const getPositionColor = (position) => {
    const colors = {
      'K': '#2c7b2c', // Yeşil
      'D': '#2c466b', // Mavi
      'O': '#b88c2c', // Altın
      'F': '#c33'     // Kırmızı
    };
    return colors[position] || '#999';
  };

  return (
    <div className="fc-modal-backdrop">
      <div className="fc-modal" style={{ maxWidth: '800px', maxHeight: '90vh', overflow: 'auto' }}>
        <h3>KADRO - {targetTeam}</h3>
        
        {/* İlk Takım */}
        <div className="fc-panel" style={{ marginBottom: '20px' }}>
          <h4 style={{ margin: '0 0 15px 0', textAlign: 'center', color: 'var(--win)' }}>
            İLK TAKIM ({firstTeam.length} oyuncu)
          </h4>
          <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
            <table className="fc-table">
              <thead>
                <tr>
                  <th style={{ textAlign: 'left' }}>Oyuncu</th>
                  <th>Poz</th>
                  <th>Rey</th>
                  <th>Frm/Fit/Mut</th>
                  <th>Yaş</th>
                  <th>Maaş</th>
                  <th>Söz Bit</th>
                </tr>
              </thead>
              <tbody>
                {firstTeam.map((player, index) => renderPlayerRow(player, index))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Yedek Oyuncular */}
        <div className="fc-panel">
          <h4 style={{ margin: '0 0 15px 0', textAlign: 'center', color: 'var(--blue)' }}>
            YEDEK OYUNCULAR ({substitutes.length} oyuncu)
          </h4>
          <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
            <table className="fc-table">
              <thead>
                <tr>
                  <th style={{ textAlign: 'left' }}>Oyuncu</th>
                  <th>Poz</th>
                  <th>Rey</th>
                  <th>Frm/Fit/Mut</th>
                  <th>Yaş</th>
                  <th>Maaş</th>
                  <th>Söz Bit</th>
                </tr>
              </thead>
              <tbody>
                {substitutes.map((player, index) => renderPlayerRow(player, index))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Kadro İstatistikleri */}
        <div className="fc-panel" style={{ marginTop: '20px' }}>
          <h4 style={{ margin: '0 0 15px 0', textAlign: 'center', color: 'var(--gold)' }}>
            KADRO İSTATİSTİKLERİ
          </h4>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '10px' }}>
                         <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
               <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--blue)' }}>
                 {targetSquad.totalPlayers}
               </div>
               <div style={{ fontSize: '12px', color: '#666' }}>Toplam Oyuncu</div>
             </div>
             <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
               <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--win)' }}>
                 {targetSquad.averageRating}
               </div>
               <div style={{ fontSize: '12px', color: '#666' }}>Ortalama Reyting</div>
             </div>
             <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
               <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--gold)' }}>
                 {Math.round([...firstTeam, ...substitutes].reduce((sum, p) => sum + p.age, 0) / targetSquad.totalPlayers)}
               </div>
               <div style={{ fontSize: '12px', color: '#666' }}>Ortalama Yaş</div>
             </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--blue)' }}>
                {firstTeam.filter(p => p.position === 'K').length + substitutes.filter(p => p.position === 'K').length}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Kaleci</div>
            </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--blue)' }}>
                {firstTeam.filter(p => p.position === 'D').length + substitutes.filter(p => p.position === 'D').length}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Defans</div>
            </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--gold)' }}>
                {firstTeam.filter(p => p.position === 'O').length + substitutes.filter(p => p.position === 'O').length}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Orta Saha</div>
            </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--loss)' }}>
                {firstTeam.filter(p => p.position === 'F').length + substitutes.filter(p => p.position === 'F').length}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Forvet</div>
            </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
                                <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--win)' }}>
                    {([...firstTeam, ...substitutes].reduce((sum, p) => sum + (p.salary || 0), 0)).toLocaleString()} TL
                  </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Toplam Maaş</div>
            </div>
          </div>
        </div>

        <div className="fc-footerbar" style={{ marginTop: '20px' }}>
          <button className="fc-btn" onClick={() => setShowSquad(false)}>Geri</button>
        </div>
      </div>
    </div>
  );
};

SquadModal.propTypes = {
  setShowSquad: PropTypes.func.isRequired,
  club: PropTypes.object.isRequired,
  selectedTeam: PropTypes.string,
};

export default SquadModal;
