import React from 'react';
import PropTypes from 'prop-types';

const SquadModal = ({ setShowSquad, club }) => {
  if (!club.squad) {
    return (
      <div className="fc-modal-backdrop">
        <div className="fc-modal">
          <h3>KADRO BİLGİSİ YOK</h3>
          <p>Bu takım için kadro bilgisi bulunamadı.</p>
          <div className="fc-footerbar">
            <button className="fc-btn" onClick={() => setShowSquad(false)}>Geri</button>
          </div>
        </div>
      </div>
    );
  }

  const { firstTeam, substitutes } = club.squad;

  const renderFormFitnessMorale = (formData) => {
    return formData.map((item, index) => {
      if (item.type === 'positive') {
        return <span key={index} style={{ color: 'green', fontWeight: 'bold' }}>+{item.value}</span>;
      } else if (item.type === 'negative') {
        return <span key={index} style={{ color: 'red', fontWeight: 'bold' }}>-{item.value}</span>;
      } else {
        return <span key={index} style={{ color: '#999' }}>-</span>;
      }
    });
  };

  const renderPlayerRow = (player) => (
    <tr key={player.id}>
      <td style={{ textAlign: 'left' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <div style={{ 
            width: '24px', 
            height: '24px', 
            borderRadius: '50%', 
            background: '#ddd',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '10px',
            fontWeight: 'bold'
          }}>
            {player.name.split(' ')[0][0]}
          </div>
          {player.name}
        </div>
      </td>
      <td>
        <span style={{ 
          background: getPositionColor(player.position),
          color: '#fff',
          padding: '2px 6px',
          borderRadius: '4px',
          fontSize: '12px',
          fontWeight: 'bold'
        }}>
          {player.position}
        </span>
      </td>
      <td style={{ fontWeight: 'bold' }}>{player.rating}</td>
      <td>
        <div style={{ display: 'flex', gap: '4px' }}>
          {renderFormFitnessMorale(player.form)}
        </div>
      </td>
      <td>{player.age}</td>
      <td style={{ fontWeight: 'bold', color: 'var(--blue)' }}>
        €{player.salary?.toLocaleString() || '0'}
      </td>
      <td>{player.contractEnd}</td>
    </tr>
  );

  const getPositionColor = (position) => {
    const colors = {
      'K': '#2c7b2c', // Yeşil
      'D': '#2c466b', // Mavi
      'O': '#b88c2c', // Altın
      'F': '#c33'     // Kırmızı
    };
    return colors[position] || '#999';
  };

  return (
    <div className="fc-modal-backdrop">
      <div className="fc-modal" style={{ maxWidth: '800px', maxHeight: '90vh', overflow: 'auto' }}>
        <h3>SENİN KADRON - {club.name}</h3>
        
        {/* İlk Takım */}
        <div className="fc-panel" style={{ marginBottom: '20px' }}>
          <h4 style={{ margin: '0 0 15px 0', textAlign: 'center', color: 'var(--win)' }}>
            İLK TAKIM ({firstTeam.length} oyuncu)
          </h4>
          <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
            <table className="fc-table">
              <thead>
                <tr>
                  <th style={{ textAlign: 'left' }}>Oyuncu</th>
                  <th>Poz</th>
                  <th>Rey</th>
                  <th>Frm/Fit/Mut</th>
                  <th>Yaş</th>
                  <th>Maaş</th>
                  <th>Söz Bit</th>
                </tr>
              </thead>
              <tbody>
                {firstTeam.map((player, index) => renderPlayerRow(player, index))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Yedek Oyuncular */}
        <div className="fc-panel">
          <h4 style={{ margin: '0 0 15px 0', textAlign: 'center', color: 'var(--blue)' }}>
            YEDEK OYUNCULAR ({substitutes.length} oyuncu)
          </h4>
          <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
            <table className="fc-table">
              <thead>
                <tr>
                  <th style={{ textAlign: 'left' }}>Oyuncu</th>
                  <th>Poz</th>
                  <th>Rey</th>
                  <th>Frm/Fit/Mut</th>
                  <th>Yaş</th>
                  <th>Maaş</th>
                  <th>Söz Bit</th>
                </tr>
              </thead>
              <tbody>
                {substitutes.map((player, index) => renderPlayerRow(player, index))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Kadro İstatistikleri */}
        <div className="fc-panel" style={{ marginTop: '20px' }}>
          <h4 style={{ margin: '0 0 15px 0', textAlign: 'center', color: 'var(--gold)' }}>
            KADRO İSTATİSTİKLERİ
          </h4>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '10px' }}>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--blue)' }}>
                {club.squad.totalPlayers}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Toplam Oyuncu</div>
            </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--win)' }}>
                {club.squad.averageRating}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Ortalama Reyting</div>
            </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--gold)' }}>
                {Math.round([...firstTeam, ...substitutes].reduce((sum, p) => sum + p.age, 0) / club.squad.totalPlayers)}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Ortalama Yaş</div>
            </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--blue)' }}>
                {firstTeam.filter(p => p.position === 'K').length + substitutes.filter(p => p.position === 'K').length}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Kaleci</div>
            </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--blue)' }}>
                {firstTeam.filter(p => p.position === 'D').length + substitutes.filter(p => p.position === 'D').length}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Defans</div>
            </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--gold)' }}>
                {firstTeam.filter(p => p.position === 'O').length + substitutes.filter(p => p.position === 'O').length}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Orta Saha</div>
            </div>
            <div style={{ textAlign: 'center', padding: '10px', background: '#f5f5f5', borderRadius: '8px' }}>
              <div style={{ fontSize: '18px', fontWeight: 'bold', color: 'var(--loss)' }}>
                {firstTeam.filter(p => p.position === 'F').length + substitutes.filter(p => p.position === 'F').length}
              </div>
              <div style={{ fontSize: '12px', color: '#666' }}>Forvet</div>
            </div>
          </div>
        </div>

        <div className="fc-footerbar" style={{ marginTop: '20px' }}>
          <button className="fc-btn" onClick={() => setShowSquad(false)}>Geri</button>
        </div>
      </div>
    </div>
  );
};

SquadModal.propTypes = {
  setShowSquad: PropTypes.func.isRequired,
  club: PropTypes.object.isRequired,
};

export default SquadModal;
