import React, { useState } from 'react';
import { STR, WEEK_SLICES_LBL } from './constants';

const mainBg = {
  background: 'linear-gradient(to bottom, #2c466b 60%, #3e5c2c 100%)',
  minHeight: '100vh',
  color: '#fff',
  fontFamily: 'sans-serif',
};

const card = {
  background: '#fff', color: '#2c466b', borderRadius: 10, padding: '10px 20px',
  margin: 10, minWidth: 80, textAlign: 'center', fontWeight: 'bold',
  boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
};

export default function App() {
  const [clubName, setClubName] = useState('');
  const [leagueShown, setLeagueShown] = useState('2. Lig');
  const [currentWeek, setCurrentWeek] = useState(0);
  const [currentSlice] = useState(0);
  const [showManager, setShowManager] = useState(false);
  const [showFinance, setShowFinance] = useState(false);
  const [showStandings, setShowStandings] = useState(false);
  const [showFixture, setShowFixture] = useState(false);
  // stage: 'setup-team' | 'hire-manager' | 'game'
  const [stage, setStage] = useState('setup-team');
  const [managerTab, setManagerTab] = useState('staff');
  const [showInfo, setShowInfo] = useState(null); // {title, message}
  const [bankBalance, setBankBalance] = useState(12000);
  const [managerName, setManagerName] = useState('');
  const incomeWin = 800, incomeDraw = 300, incomeLoss = 100, weekCost = 500;
  const [groupName, setGroupName] = useState('Beyaz'); // Beyaz | Kirmizi
  const [groupTeams, setGroupTeams] = useState([]); // string[]
  const [fixtures, setFixtures] = useState([]); // {hft, home, away, a, y, result}
  const [standings, setStandings] = useState([]); // {team,O,G,B,M,A,Y,AV,PN}
  const [allTeams, setAllTeams] = useState([]);
  const [in2Rows, setIn2Rows] = useState([]); // raw rows for 2. Lig
  const [teamsBeyaz, setTeamsBeyaz] = useState([]);
  const [teamsKirmizi, setTeamsKirmizi] = useState([]);
  const [showSquad, setShowSquad] = useState(false);
  const [squad, setSquad] = useState([]);
  const [candidates, setCandidates] = useState([]);
  const [currentManager, setCurrentManager] = useState(null);
  const [clubStrength, setClubStrength] = useState(0.5); // 0..1, 2. Lig içi normalize
  const [clubValue, setClubValue] = useState(0);

  const staffSlots = [
    { role: 'Asistan',   filled: true,  icon: '/icons/assistant.svg' },
    { role: 'Asistan',   filled: false },
    { role: 'Fizyo',     filled: true,  icon: '/icons/physio.svg' },
    { role: 'Fizyo',     filled: false },
    { role: 'Antrenor',  filled: true,  icon: '/icons/coach.svg' },
    { role: 'Antrenor',  filled: true,  icon: '/icons/coach.svg' },
    { role: 'Antrenor',  filled: false },
    { role: 'Antrenor',  filled: false },
    { role: 'Scout',     filled: true,  icon: '/icons/scout.svg' },
    { role: 'Scout',     filled: true,  icon: '/icons/scout.svg' },
    { role: 'Scout',     filled: false },
    { role: 'Scout',     filled: false },
  ];

  const onStaffClick = (s) => {
    if (s.filled) return;
    const msg = s.role === 'Asistan'
      ? 'Amator kume seviyesinde maksimum 1 asistan atayabilirsin.'
      : (s.role === 'Antrenor'
          ? 'Amator kume seviyesinde maksimum 2 antrenor atayabilirsin.'
          : 'Mevcut seviyede ek slot yok.');
    setShowInfo({ title: STR.UYARI, message: msg });
  };

  // 1) CSV'yi yükle ve tüm 2. Lig gruplarını hazırla
  React.useEffect(() => {
    const load = async () => {
      try {
        const res = await fetch(process.env.PUBLIC_URL + '/turkish_leagues_with_values.csv');
        const text = await res.text();
        const rows = text.split(/\r?\n/).filter(Boolean).slice(1).map(l => l.split(','));
        const in2 = rows.filter(r => (r[1] || '').includes('2. Lig'));
        setIn2Rows(in2);
        const norm = (s='') => s.normalize('NFKD').replace(/[\u0300-\u036f]/g,'').replace(/ı/g,'i').replace(/İ/g,'I').toLowerCase();
        const all = in2.map(r => (r[0] || '').trim()).filter(Boolean);
        setAllTeams(all);
        setTeamsBeyaz(in2.filter(r => norm(r[1]).includes('beyaz')).map(r => (r[0] || '').trim()));
        setTeamsKirmizi(in2.filter(r => norm(r[1]).includes('kirmizi')).map(r => (r[0] || '').trim()));
      } catch {}
    };
    load();
  }, []);

  // 2) Seçilen takıma göre grup, fikstür ve puan durumu
  React.useEffect(() => {
    if (!clubName || in2Rows.length === 0) return;
    const norm = (s='') => s.normalize('NFKD').replace(/[\u0300-\u036f]/g,'').replace(/ı/g,'i').replace(/İ/g,'I').toLowerCase();
    const isGroup = (s, grp) => {
      const n = norm(s);
      return grp === 'Beyaz' ? n.includes('beyaz') : n.includes('kirmizi');
    };
    const me = in2Rows.find(r => norm(r[0]) === norm(clubName));
    const leagueStr = me ? (me[1] || '').trim() : '';
    const g = norm(leagueStr).includes('kirmizi') ? 'Kırmızı' : 'Beyaz';
    setGroupName(g);
    setLeagueShown('2. Lig ' + g);
    const teamList = in2Rows.filter(r => isGroup(r[1], g === 'Kırmızı' ? 'Kirmizi' : 'Beyaz')).map(r => (r[0] || '').trim());
    const teams = teamList.length > 0 ? teamList : in2Rows.map(r => (r[0] || '').trim());
    setGroupTeams(teams);
    const st = teams.map(t => ({ team: t, O:0,G:0,B:0,M:0,A:0,Y:0,AV:0,PN:0 }));
    setStandings(st);
    const opps = teams.filter(t => t.toLowerCase() !== clubName.toLowerCase());
    const single = opps.map((t,i)=> ({ hft: i+1, home: i%2===0 ? clubName : t, away: i%2===0 ? t : clubName, a:'-', y:'-', result:'' }));
    const second = opps.map((t,i)=> ({ hft: opps.length + i + 1, home: single[i].home===clubName? t: clubName, away: single[i].home===clubName? clubName: t, a:'-', y:'-', result:'' }));
    setFixtures([...single, ...second]);

    // Squad generation scaled to 2. Lig values (20-30 avg)
    const parseVal = (x) => parseFloat(String(x||'0').replace(',','.')) || 0;
    const values = in2Rows.filter(r=>r[1] && r[1].includes('2. Lig')).map(r=>parseVal(r[2]));
    const vmin = Math.min.apply(null, values);
    const vmax = Math.max.apply(null, values);
    const meRow = in2Rows.find(r => (r[0]||'').trim().toLowerCase() === clubName.toLowerCase());
    const myVal = meRow ? parseVal(meRow[2]) : vmin;
    setClubValue(myVal);
    const normStrength = (vmax>vmin) ? (myVal - vmin) / (vmax - vmin) : 0.5;
    setClubStrength(Math.max(0, Math.min(1, normStrength)));
    const targetAvg = (vmax>vmin) ? (20 + (myVal - vmin) * 10 / (vmax - vmin)) : 25;
    setSquad(generateSquad(targetAvg));
  }, [clubName, in2Rows]);

  // helpers for squad generation
  const rand = () => Math.random();
  const pick = (arr) => arr[Math.floor(rand()*arr.length)];
  const generateSquad = (targetAvg=25) => {
    const firstNames = ['Emre','Ahmet','Mehmet','Ali','Burak','Okan','Can','Mert','Serkan','Umut','Deniz','Enes','Furkan','Hakan','Kerem','Oguz','Yasin','Efe','Baris','Kaan','Gokhan','Bora'];
    const lastNames = ['Yilmaz','Demir','Sahin','Celik','Ozturk','Aydin','Arslan','Dogan','Yildiz','Yalcin','Kaya','Korkmaz','Aksoy','Gunes','Kara','Polat','Tas','Guler','Koc','Erdogan'];
    const positions = [ { code:'GK', count:2 }, { code:'DF', count:8 }, { code:'MF', count:8 }, { code:'FW', count:4 } ];
    const players = [];
    const total = positions.reduce((s,p)=>s+p.count,0);
    let sum = 0;
    positions.forEach(p => {
      for(let i=0;i<p.count;i++){
        const name = pick(firstNames) + ' ' + pick(lastNames);
        let rating = Math.round(targetAvg + (rand()-0.5)*6);
        rating = Math.max(15, Math.min(40, rating));
        const age = 18 + Math.floor(rand()*18);
        const year = 2026 + Math.floor(rand()*5);
        const form = Math.floor(rand()*5) - 2; // -2..+2
        const salary = calcSalary(rating); // TRY/hafta varsayalım
        players.push({ name, pos:p.code, rating, age, year, form, salary });
        sum += rating;
      }
    });
    const desired = Math.round(targetAvg * total);
    const delta = desired - sum;
    if (players.length){
      const i = Math.floor(rand()*players.length);
      players[i].rating = Math.max(20, Math.min(30, players[i].rating + delta));
      players[i].salary = calcSalary(players[i].rating);
    }
    return players;
  };

  const calcSalary = (rating) => {
    // İstenen aralık: 20–60 ₺ (reyting 20–30 arası doğrusal)
    const base = 20;
    const perPoint = (60 - 20) / 10; // 4 ₺/puan
    const r = Math.max(20, Math.min(30, rating));
    return Math.round(base + (r - 20) * perPoint);
  };

  // Manager candidates and hire flow
  const genManagers = (strength=0.5) => {
    const first = ['A','B','C','D','E','F','G','H','J','K','L','M','N','O','P','R','S','T','U','V','Y','Z'];
    const last = ['Bell','Quinn','Hewitt','Booth','Swift','Morgan','Turner','Fisher','Reeves','Carter','Brown','Wood','Parker','Hill','Walsh'];
    const styles = ['4-4-2 Dikine Oyun','3-4-3 Pas Oyunu','4-3-3 Kanat Oyun','3-5-2 Dengeli'];
    const fans = ['TAMAM','Kararsız','Mutlu','Memnun Değil'];
    const list = [];
    for (let i=0;i<5;i++){
      const base = 27 + Math.round(strength*6); // 27..33
      const jitter = () => (Math.floor(Math.random()*5) - 2); // -2..+2
      const M = Math.max(20, Math.min(40, base + jitter()));
      const A = Math.max(20, Math.min(40, base + jitter()));
      const T = Math.max(20, Math.min(40, base + jitter()));
      const comp = Math.random()<0.5 ? 0 : (2000 + Math.floor(Math.random()*1500));
      const fn = first[Math.floor(Math.random()*first.length)];
      const ln = last[Math.floor(Math.random()*last.length)].toUpperCase();
      const avg = (M+A+T)/3;
      // wage scaled by average skill (TRY/hafta): 30..100 arası
      const wage = Math.round(30 + (avg - 20) * (70/20));
      list.push({ name: `${fn} ${ln}`, M, A, T, style: styles[Math.floor(Math.random()*styles.length)], fan: fans[Math.floor(Math.random()*fans.length)], comp, wage });
    }
    return list;
  };

  React.useEffect(() => {
    if (stage === 'hire-manager' && candidates.length === 0){
      setCandidates(genManagers(clubStrength));
    }
  }, [stage, clubStrength, candidates.length]);

  const recomputePositions = React.useCallback((stArr) => {
    const arr = [...stArr].map(r => ({...r, AV: (r.A - r.Y)}));
    arr.sort((a,b)=> b.PN - a.PN || (b.A - b.Y) - (a.A - a.Y) || b.A - a.A || a.team.localeCompare(b.team));
    return arr;
  }, []);

  const playWeek = () => {
    const week = currentWeek;
    const idx = fixtures.findIndex(f => f.hft === week && f.result==='');
    if (idx < 0) { setCurrentWeek(w=>w+1); return; }
    const f = fixtures[idx];
    // simulate
    const gf = Math.floor(Math.random()*4); // our goals if we are home
    const ga = Math.floor(Math.random()*4);
    const homeIsUs = f.home.toLowerCase() === clubName.toLowerCase();
    const ourGoals = homeIsUs ? gf : ga;
    const theirGoals = homeIsUs ? ga : gf;
    const resChar = ourGoals>theirGoals? 'G' : (ourGoals<theirGoals? 'M' : 'B');
    const newFixtures = [...fixtures];
    newFixtures[idx] = { ...f, a: ourGoals, y: theirGoals, result: resChar };
    setFixtures(newFixtures);
    // update finance
    const inc = resChar === 'G' ? incomeWin : (resChar === 'B' ? incomeDraw : incomeLoss);
    setBankBalance(b => b + inc - weekCost);
    // update standings for both teams
    const sArr = standings.map(x=>({...x}));
    const upd = (team, gf1, ga1) => {
      const r = sArr.find(x => x.team === team);
      if (!r) return;
      r.O += 1; r.A += gf1; r.Y += ga1; r.AV = r.A - r.Y;
      if (gf1>ga1){ r.G+=1; r.PN+=3; } else if (gf1<ga1){ r.M+=1; } else { r.B+=1; r.PN+=1; }
    };
    upd(f.home, gf, ga);
    upd(f.away, ga, gf);
    setStandings(sArr);
    setCurrentWeek(w=>w+1);
  };

  // Full-page Setup: team selection
  if (stage === 'setup-team') {
    return (
      <div className="fc-container">
        <div className="fc-header">
          <div className="fc-title">FOOTBALL CHAIRMAN</div>
          <div className="fc-ribbon">TAKIM SEÇ</div>
        </div>
        <div style={{padding:16}}>
          <div style={{display:'flex', gap:8, justifyContent:'center', marginBottom:8}}>
            <button onClick={()=>setGroupName('Beyaz')} className="fc-btn" style={{background: groupName==='Beyaz'?'#3e5c2c':'#8aa08a'}}>Beyaz</button>
            <button onClick={()=>setGroupName('Kırmızı')} className="fc-btn" style={{background: groupName==='Kırmızı'?'#3e5c2c':'#8aa08a'}}>Kırmızı</button>
          </div>
          <div style={{maxWidth:420, margin:'0 auto 8px'}}>
            <select style={{width:'100%', padding:8, borderRadius:6}} value={clubName} onChange={(e)=>setClubName(e.target.value)}>
              <option value="" disabled>Takım seçin</option>
              {(groupName==='Kırmızı'? teamsKirmizi : teamsBeyaz).map((t,i)=> (
                <option key={i} value={t}>{t}</option>
              ))}
            </select>
          </div>
          <div style={{maxWidth:420, margin:'0 auto'}}>
            <input value={managerName} onChange={(e)=>setManagerName(e.target.value)} placeholder="İsminiz" style={{width:'100%', padding:8, borderRadius:6, marginBottom:8}}/>
          </div>
          <div className="fc-btn-row">
            <button className="fc-btn" onClick={()=>{ if(!clubName){alert('Takım seçin'); return;} setCurrentWeek(0); setStage('hire-manager'); }}>Devam</button>
          </div>
        </div>
      </div>
    );
  }

  // Full-page Manager Hire
  if (stage === 'hire-manager') {
    return (
      <div className="fc-container">
        <div className="fc-header">
          <div className="fc-title">BİR MENAJER KİRALA</div>
          <div className="fc-ribbon">{clubName || '—'}</div>
        </div>
        <div style={{padding:16, maxWidth:700, margin:'0 auto'}}>
          {candidates.map((c, idx)=> (
            <div key={idx} className="mgr-card">
              <div style={{width:80, height:80, background:'#bfe3bf', borderRadius:8, display:'flex', alignItems:'center', justifyContent:'center', fontWeight:900}}>M</div>
              <div className="mgr-right">
                <div className="mgr-name">{c.name}</div>
                <div className="mgr-badges">
                  <span className="mgr-badge g">M {c.M}</span>
                  <span className="mgr-badge k">A {c.A}</span>
                  <span className="mgr-badge g">T {c.T}</span>
                </div>
                <div className="mgr-meta">{c.style} &nbsp; Taraftarlar: {c.fan} &nbsp; Ücret: {new Intl.NumberFormat('tr-TR',{style:'currency',currency:'TRY',maximumFractionDigits:0}).format(c.wage)}/hafta &nbsp; Tazminat: {c.comp>0? new Intl.NumberFormat('tr-TR',{style:'currency',currency:'TRY',maximumFractionDigits:0}).format(c.comp): 'Yok'}</div>
                <div className="mgr-actions">
                  <button className="fc-btn" onClick={()=>{ setCurrentManager(c); setManagerName(c.name); setStage('game'); }}>Seç</button>
                </div>
              </div>
            </div>
          ))}
          <div className="fc-btn-row">
            <button className="fc-btn" onClick={()=>setCandidates(genManagers(clubStrength))}>Reddet ve daha fazla aday bekle</button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fc-container">
      <div className="fc-header">
        <div className="fc-title">FOOTBALL CHAIRMAN</div>
        <div className="fc-ribbon">{clubName.toUpperCase()}</div>
        <div className="fc-sub">2025/26 | {leagueShown} | Hafta {currentWeek}</div>
        {managerName && <div style={{fontSize:12, opacity:.9, marginTop:4}}>Menajer: {managerName}</div>}
      </div>

      {/* setup modal removed */}

      {/* Hızlı kartlar */}
      <div className="fc-grid">
        <button className="fc-card white" onClick={()=>setShowFixture(true)}>
          {STR.HF_SONU}<span className="big">{currentWeek}</span>
        </button>
        <button className="fc-card white" onClick={() => setShowStandings(true)}>
          {STR.LIG}<span className="big">-</span>
        </button>
        <button className="fc-card white" onClick={()=>setShowFinance(true)}>
          {STR.PARA}
          <span className="big" style={{color:'#2c7b2c'}}>
            {new Intl.NumberFormat('tr-TR',{style:'currency',currency:'TRY',maximumFractionDigits:0}).format(bankBalance)}
          </span>
        </button>
      </div>

      {showManager && (
        <div style={{position: 'fixed', inset: 0, background: 'rgba(44,70,107,0.95)', zIndex: 1000, display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
          <div style={{background: 'rgba(255,255,255,0.95)', color: '#2c466b', borderRadius: 18, padding: 20, minWidth: 360, maxWidth: '95vw', maxHeight: '85vh', overflowY: 'auto', boxShadow: '0 2px 16px rgba(0,0,0,0.2)'}}>
            <div style={{fontSize: '1.2em', fontWeight: 'bold', textAlign: 'center', marginBottom: 12}}>{STR.MENAJER}</div>
            <div style={{display:'flex', gap:8, marginBottom:12}}>
              <button onClick={() => setManagerTab('records')} style={{flex:1, border:'none', borderRadius:8, padding:'8px', cursor:'pointer', fontWeight:'bold', background: managerTab==='records' ? '#bfe3bf' : '#fff', color:'#2c466b', boxShadow:'0 1px 4px rgba(0,0,0,0.08)'}}>{STR.KAYITLAR}</button>
              <button onClick={() => setManagerTab('staff')} style={{flex:1, border:'none', borderRadius:8, padding:'8px', cursor:'pointer', fontWeight:'bold', background: managerTab==='staff' ? '#bfe3bf' : '#fff', color:'#2c466b', boxShadow:'0 1px 4px rgba(0,0,0,0.08)'}}>{STR.TEKNIK_KADRO}</button>
            </div>
            {managerTab==='records' && (
              <div className="fc-panel" style={{marginBottom:12}}>
                {currentManager ? (
                  <div style={{display:'flex', gap:12}}>
                    <div style={{width:90, height:90, background:'#bfe3bf', borderRadius:8, display:'flex', alignItems:'center', justifyContent:'center', fontWeight:900}}>M</div>
                    <div style={{flex:1}}>
                      <div style={{fontWeight:900, fontSize:18, marginBottom:6}}>{currentManager.name}</div>
                      <div style={{display:'flex', gap:6, marginBottom:6}}>
                        <span className="mgr-badge g">M {currentManager.M}</span>
                        <span className="mgr-badge k">A {currentManager.A}</span>
                        <span className="mgr-badge g">T {currentManager.T}</span>
                      </div>
                      <div style={{color:'#333', fontSize:14}}>{currentManager.style} &nbsp; Ücret: {new Intl.NumberFormat('tr-TR',{style:'currency',currency:'TRY',maximumFractionDigits:0}).format(currentManager.wage)}/hafta</div>
                    </div>
                  </div>
                ) : (
                  <div style={{textAlign:'center', color:'#333'}}>Henüz menajer yok. <button className="fc-btn" style={{marginLeft:8}} onClick={()=>{ setCandidates(genManagers(clubStrength)); setShowHire(true); }}>Menajer Kirala</button></div>
                )}
              </div>
            )}
            {managerTab==='staff' && (
              <div>
                <div style={{display:'grid', gridTemplateColumns:'repeat(4, minmax(70px,1fr))', gap:12, marginTop:8}}>
                  {staffSlots.map((s, idx) => (
                    <button key={idx} onClick={()=>onStaffClick(s)} style={{border:'none', background:'transparent', cursor:'pointer'}}>
                      <div style={{width:70, height:70, borderRadius:8, background: s.filled ? '#e6f4e6' : '#d9d9d9', display:'flex', alignItems:'center', justifyContent:'center', boxShadow:'0 1px 4px rgba(0,0,0,0.06)'}}>
                        {s.filled ? (
                          s.icon ? <img src={s.icon} alt="" style={{width:36,height:36,objectFit:'contain'}}/> : <span style={{fontSize:'1.6em'}}>*</span>
                        ) : (
                          <span style={{fontSize:'1.6em'}}>*</span>
                        )}
                      </div>
                      <div style={{fontSize:'0.9em', marginTop:4, color:'#333'}}>{s.role}</div>
                    </button>
                  ))}
                </div>
              </div>
            )}
            <div style={{display:'flex', justifyContent:'space-between', marginTop:16}}>
              <button style={{background: '#3e5c2c', color: '#fff', border: 'none', borderRadius: 6, padding: '8px 16px', fontWeight: 'bold', cursor:'pointer'}} onClick={() => setShowManager(false)}>{STR.GERI}</button>
            </div>
          </div>
        </div>
      )}

      {showInfo && (
        <div style={{position:'fixed', inset:0, zIndex:1100, background:'rgba(0,0,0,0.3)', display:'flex', alignItems:'center', justifyContent:'center'}} onClick={()=>setShowInfo(null)}>
          <div style={{background:'#fff', borderRadius:12, padding:20, minWidth:300, maxWidth:'80vw', boxShadow:'0 2px 16px rgba(0,0,0,0.25)'}} onClick={(e)=>e.stopPropagation()}>
            <div style={{fontWeight:'bold', fontSize:'1.2em', marginBottom:8}}>{showInfo.title}</div>
            <div style={{color:'#333', marginBottom:16}}>{showInfo.message}</div>
            <div style={{display:'flex', justifyContent:'flex-end'}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 16px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowInfo(null)}>{STR.TAMAM}</button>
            </div>
          </div>
        </div>
      )}

      {showFixture && (
        <div style={{position:'fixed', inset:0, background:'rgba(44,70,107,0.95)', zIndex:1000, display:'flex', alignItems:'center', justifyContent:'center'}}>
          <div style={{background:'#fff', color:'#2c466b', borderRadius:12, padding:16, minWidth:360, maxWidth:'95vw', maxHeight:'85vh', overflowY:'auto'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:8}}>{STR.FIKSTURLER_SONUCLAR}</div>
            <table style={{width:'100%', borderCollapse:'collapse'}}>
              <thead>
                <tr style={{background:'#e6f4e6'}}>
                  <th style={{padding:6}}>{STR.HFT}</th>
                  <th style={{padding:6}}>{STR.RAKIP}</th>
                  <th style={{padding:6}}>{STR.A}</th>
                  <th style={{padding:6}}>{STR.Y}</th>
                  <th style={{padding:6}}>{STR.SONUC}</th>
                </tr>
              </thead>
              <tbody>
                {fixtures.map((f,i)=> (
                  <tr key={i} style={{background: i%2===0? '#f5f5f5':'#e6f4e6'}}>
                    <td style={{padding:6, textAlign:'center'}}>{f.hft}</td>
                    <td style={{padding:6}}>{f.home} - {f.away}</td>
                    <td style={{padding:6, textAlign:'center'}}>{f.a}</td>
                    <td style={{padding:6, textAlign:'center'}}>{f.y}</td>
                    <td style={{padding:6, textAlign:'center'}}>{f.result}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div style={{textAlign:'center', marginTop:12}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 16px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowFixture(false)}>{STR.GERI}</button>
            </div>
          </div>
        </div>
      )}

      {showStandings && (
        <div style={{position:'fixed', inset:0, background:'rgba(44,70,107,0.95)', zIndex:1000, display:'flex', alignItems:'center', justifyContent:'center'}}>
          <div style={{background:'#fff', color:'#2c466b', borderRadius:12, padding:16, minWidth:360, maxWidth:'95vw', maxHeight:'85vh', overflowY:'auto'}}>
            <div style={{fontSize:'1.2em', fontWeight:'bold', textAlign:'center', marginBottom:8}}>{STR.PUAN_DURUMU}</div>
            <table style={{width:'100%', borderCollapse:'collapse'}}>
              <thead>
                <tr style={{background:'#e6f4e6'}}>
                  <th style={{padding:6}}>#</th>
                  <th style={{padding:6}}>{STR.TAKIM}</th>
                  <th style={{padding:6}}>{STR.O_COL}</th>
                  <th style={{padding:6}}>{STR.G_COL}</th>
                  <th style={{padding:6}}>{STR.B_COL}</th>
                  <th style={{padding:6}}>{STR.M_COL}</th>
                  <th style={{padding:6}}>{STR.PN_COL}</th>
                </tr>
              </thead>
              <tbody>
                {recomputePositions(standings).map((row,i)=>(
                  <tr key={i} style={{background: i%2===0? '#f5f5f5':'#e6f4e6'}}>
                    <td style={{padding:6, textAlign:'center'}}>{i+1}</td>
                    <td style={{padding:6}}>{row.team}</td>
                    <td style={{padding:6, textAlign:'center'}}>{row.O}</td>
                    <td style={{padding:6, textAlign:'center'}}>{row.G}</td>
                    <td style={{padding:6, textAlign:'center'}}>{row.B}</td>
                    <td style={{padding:6, textAlign:'center'}}>{row.M}</td>
                    <td style={{padding:6, textAlign:'center'}}>{row.PN}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div style={{textAlign:'center', marginTop:12}}>
              <button style={{background:'#3e5c2c', color:'#fff', border:'none', borderRadius:6, padding:'8px 16px', fontWeight:'bold', cursor:'pointer'}} onClick={()=>setShowStandings(false)}>{STR.GERI}</button>
            </div>
          </div>
        </div>
      )}

      {showFinance && (
        <div className="fc-modal-backdrop">
          <div className="fc-modal" style={{background:'rgba(255,255,255,0.96)'}}>
            <h3>{STR.FINANS}</h3>
            <div className="fc-panel">
              <div className="fc-row">
                <div className="fc-bank-icon">💰</div>
                <div>
                  <div className="fc-bank-title">Banka Bakiyesi</div>
                  <div className="fc-badge-green">{new Intl.NumberFormat('tr-TR',{style:'currency',currency:'TRY',maximumFractionDigits:0}).format(bankBalance)}</div>
                  <div className="fc-muted" style={{marginTop:6}}>Limit Aşımı: {new Intl.NumberFormat('de-DE',{style:'currency',currency:'EUR',maximumFractionDigits:0}).format(4000)}</div>
                </div>
              </div>
              <div className="fc-fin-list">
                <div className="item"><span>Mevcut Ücretler</span><strong>{new Intl.NumberFormat('de-DE',{style:'currency',currency:'EUR',maximumFractionDigits:0}).format(800)}</strong></div>
                <div className="item"><span>Ücretler + Galibiyet Primi (10%)</span><strong>{new Intl.NumberFormat('de-DE',{style:'currency',currency:'EUR',maximumFractionDigits:0}).format(880)}</strong></div>
                <div className="item"><span>Önerilen En Yüksek Ücret</span><strong>{new Intl.NumberFormat('de-DE',{style:'currency',currency:'EUR',maximumFractionDigits:0}).format(624)}</strong></div>
                <div className="item"><span>Bu seneki kar/zarar</span><strong style={{color:'#c00'}}>{new Intl.NumberFormat('de-DE',{style:'currency',currency:'EUR',maximumFractionDigits:0}).format(-982)}</strong></div>
              </div>
              <div style={{marginTop:10, fontWeight:900}}>Daha fazla para ister misin?</div>
              <div className="fc-cta">Yatırım yaparak kulübünün mali durumunu iyileştir!</div>
            </div>
            <div className="fc-footerbar">
              <button className="fc-btn" onClick={()=>setShowFinance(false)}>{STR.GERI}</button>
              <button className="fc-btn">{STR.TRANSFERLER}</button>
              <button className="fc-btn">{STR.GECMIS_BTN}</button>
            </div>
          </div>
        </div>
      )}
      {/* Orta lacivert kartlar */}
      <div className="fc-grid" style={{marginTop:8}}>
        <button className="fc-card navy" onClick={()=>setShowSquad(true)}>{STR.KADRO}<span className="big">-</span></button>
        <button className="fc-card navy" onClick={()=>setShowManager(true)}>{STR.MENAJER}<span className="big">-</span></button>
        <div className="fc-card navy">{STR.ANTRENMAN}<span className="big">-</span></div>
        <div className="fc-card navy">{STR.ALTYAPI}<span className="big">-</span></div>
      </div>

      {/* Yeşil özellik kartları */}
      <div className="fc-grid" style={{marginTop:4}}>
        <div className="fc-card green">{STR.KUPALAR}<span className="big">0</span></div>
        <div className="fc-card green">{STR.BASARIMLAR}<span className="big">0</span></div>
        <div className="fc-card green">{STR.REKORLAR}<span className="big">*</span></div>
        <button className="fc-card green" onClick={()=>setShowFixture(true)}>{STR.GECMIS}<span className="big">*</span></button>
      </div>

      {/* Açık yeşil bilgi kartları */}
      <div className="fc-grid" style={{marginTop:4}}>
        <div className="fc-card light" style={{minWidth:220}}>
          {STR.TARAFTARLAR_MUTLU_MU}
          <span className="big">-</span>
        </div>
        <div className="fc-card light">{STR.STADYUM}<span className="big">⚽</span></div>
        <div className="fc-card light">{STR.BASKAN}<span className="big">👕</span></div>
      </div>

      {/* Son sonuçlar */}
      <div className="fc-dots">
        {['M','G','G','G','B','B'].map((r,i)=> (
          <div key={i} className="fc-dot" style={{background: r==='G'? '#2c7b2c' : (r==='B'? '#b88c2c':'#c00')}}>{r}</div>
        ))}
      </div>

      {/* Sonraki maç */}
      <div className="fc-next">{groupName? (groupName + ' | '): ''}{STR.FIKSTUR}: {fixtures[0] ? `${fixtures[0].home} - ${fixtures[0].away}` : '-'}</div>

      {showSquad && (
        <div className="fc-modal-backdrop">
          <div className="fc-modal fc-squad">
            <h3>Senin Kadron</h3>
            <div className="legend"><span>İlk Takım</span><span>Ortalama: {squad.length? Math.round(squad.reduce((a,b)=>a+b.rating,0)/squad.length):'-'}</span></div>
            <table className="fc-squad-table">
              <thead>
                <tr>
                  <th>Ad</th>
                  <th>Poz</th>
                  <th>Rey</th>
                  <th>Form</th>
                  <th>Maaş</th>
                  <th>Yaş</th>
                  <th>Söz Bit</th>
                </tr>
              </thead>
              <tbody>
                {squad.map((pl, i)=> (
                  <tr key={i}>
                    <td>{pl.name}</td>
                    <td>
                      <span className={`fc-badge-pos pos-${pl.pos}`}>{pl.pos==='GK'?'K':pl.pos==='DF'?'D':pl.pos==='MF'?'O':'F'}</span>
                    </td>
                    <td style={{textAlign:'center', fontWeight:800}}>{pl.rating}</td>
                    <td style={{textAlign:'center'}}>
                      <span className={`form-badge ${pl.form>0?'form-pos':(pl.form<0?'form-neg':'form-zero')}`}>{pl.form>0?`+${pl.form}`:pl.form}</span>
                    </td>
                    <td style={{textAlign:'right', fontVariantNumeric:'tabular-nums'}}>{new Intl.NumberFormat('tr-TR',{style:'currency',currency:'TRY',maximumFractionDigits:0}).format(pl.salary)}</td>
                    <td style={{textAlign:'center'}}>{pl.age}</td>
                    <td style={{textAlign:'center'}}>{pl.year}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="fc-footerbar" style={{marginTop:12}}>
              <button className="fc-btn" onClick={()=>setShowSquad(false)}>{STR.GERI}</button>
              <button className="fc-btn">İstatistikleri</button>
              <button className="fc-btn">Maçlar/Goller</button>
            </div>
          </div>
        </div>
      )}

      {false && (
        <div className="fc-modal-backdrop">
          <div className="fc-modal" style={{maxWidth: '700px'}}>
            <h3>Bir Menajer Kirala</h3>
            <div>
              {candidates.map((c, idx)=> (
                <div key={idx} className="mgr-card">
                  <div style={{width:80, height:80, background:'#bfe3bf', borderRadius:8, display:'flex', alignItems:'center', justifyContent:'center', fontWeight:900}}>M</div>
                  <div className="mgr-right">
                    <div className="mgr-name">{c.name}</div>
                    <div className="mgr-badges">
                      <span className="mgr-badge g">M {c.M}</span>
                      <span className="mgr-badge k">A {c.A}</span>
                      <span className="mgr-badge g">T {c.T}</span>
                    </div>
                  <div className="mgr-meta">{c.style} &nbsp; Taraftarlar: {c.fan} &nbsp; Ücret: {new Intl.NumberFormat('tr-TR',{style:'currency',currency:'TRY',maximumFractionDigits:0}).format(c.wage)}/hafta &nbsp; Tazminat: {c.comp>0? new Intl.NumberFormat('tr-TR', {style:'currency', currency:'TRY', maximumFractionDigits:0}).format(c.comp): 'Yok'}</div>
                    <div className="mgr-actions">
                      <button className="fc-btn" onClick={()=>{ setCurrentManager(c); setManagerName(c.name); setShowHire(false); }}>Seç</button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            <div className="fc-btn-row">
              <button className="fc-btn" onClick={()=>setCandidates(c=>genManagers())}>Reddet ve daha fazla aday bekle</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
