      'Sakaryaspor': 9.0, 'Şanlıurfaspor': 6.5, 'Ümraniyespor': 7.0, 'Altay': 5.0, 'Adanaspor': 5.5,
      'Ankara Keçiörengücü': 6.0, 'Boluspor': 6.5, 'Bandırmaspor': 7.0, 'Tuzlaspor': 4.5, 'Giresunspor': 6.5,
