import { 
  generateRandomName, 
  generateRandomAge, 
  generateContractEnd, 
  generateFormFitnessMorale 
} from './turkishNames';

// Kadro oluşturma fonksiyonu
export const generateSquad = (teamName, leagueName, teamValue) => {
  // Lig bazında ortalama reyting ve sapma değerleri
  const leagueSettings = {
    'Süper Lig': { average: 65, deviation: 20 },
    '1. Lig': { average: 45, deviation: 10 },
    '2. Lig Beyaz': { average: 29, deviation: 4 },
    '2. Lig Kırmızı': { average: 29, deviation: 4 }
  };

  const settings = leagueSettings[leagueName] || { average: 29, deviation: 4 };
  
  // Takım değerine göre ortalama reyting hesapla
  const baseRating = Math.max(20, Math.min(80, teamValue * 2)); // Takım değerini reytinge çevir
  const averageRating = Math.round((baseRating + settings.average) / 2);
  
  // İlk takım (11 oyuncu)
  const firstTeam = [];
  const positions = ['K', 'D', 'D', 'D', 'D', 'O', 'O', 'O', 'O', 'F', 'F']; // 4-4-2 formasyonu
  
  for (let i = 0; i < 11; i++) {
    const position = positions[i];
    const rating = generatePlayerRating(averageRating, settings.deviation, position);
    
    firstTeam.push({
      id: `ft_${i}`,
      name: generateRandomName(),
      position: position,
      rating: rating,
      form: generateFormFitnessMorale(),
      age: generateRandomAge(),
      contractEnd: generateContractEnd(),
      isFirstTeam: true
    });
  }
  
  // Yedek oyuncular (9 oyuncu)
  const substitutes = [];
  const subPositions = ['K', 'D', 'D', 'D', 'O', 'O', 'F', 'F', 'F']; // Yedek pozisyonlar
  
  for (let i = 0; i < 9; i++) {
    const position = subPositions[i];
    const rating = generatePlayerRating(averageRating - 5, settings.deviation, position); // Yedekler biraz daha düşük
    
    substitutes.push({
      id: `sub_${i}`,
      name: generateRandomName(),
      position: position,
      rating: rating,
      form: generateFormFitnessMorale(),
      age: generateRandomAge(),
      contractEnd: generateContractEnd(),
      isFirstTeam: false
    });
  }
  
  return {
    teamName: teamName,
    league: leagueName,
    firstTeam: firstTeam,
    substitutes: substitutes,
    totalPlayers: 20,
    averageRating: Math.round(
      [...firstTeam, ...substitutes].reduce((sum, player) => sum + player.rating, 0) / 20
    )
  };
};

// Oyuncu reytingi oluşturma fonksiyonu
const generatePlayerRating = (average, deviation, position) => {
  // Pozisyona göre reyting ayarlamaları
  const positionModifiers = {
    'K': { min: 25, max: 85 }, // Kaleci
    'D': { min: 20, max: 80 }, // Defans
    'O': { min: 20, max: 80 }, // Orta Saha
    'F': { min: 20, max: 80 }  // Forvet
  };
  
  const modifier = positionModifiers[position] || { min: 20, max: 80 };
  
  // Normal dağılım benzeri reyting oluştur
  let rating = average + (Math.random() - 0.5) * deviation * 2;
  rating = Math.round(rating);
  
  // Pozisyon sınırlarına uygula
  rating = Math.max(modifier.min, Math.min(modifier.max, rating));
  
  return rating;
};

// Kadro istatistikleri hesaplama
export const calculateSquadStats = (squad) => {
  const allPlayers = [...squad.firstTeam, ...squad.substitutes];
  
  const stats = {
    totalPlayers: allPlayers.length,
    averageRating: Math.round(allPlayers.reduce((sum, p) => sum + p.rating, 0) / allPlayers.length),
    averageAge: Math.round(allPlayers.reduce((sum, p) => sum + p.age, 0) / allPlayers.length),
    positionCount: {
      K: allPlayers.filter(p => p.position === 'K').length,
      D: allPlayers.filter(p => p.position === 'D').length,
      O: allPlayers.filter(p => p.position === 'O').length,
      F: allPlayers.filter(p => p.position === 'F').length
    },
    bestPlayer: allPlayers.reduce((best, current) => current.rating > best.rating ? current : best),
    youngestPlayer: allPlayers.reduce((youngest, current) => current.age < youngest.age ? current : youngest),
    oldestPlayer: allPlayers.reduce((oldest, current) => current.age > oldest.age ? current : oldest)
  };
  
  return stats;
};

// Tüm takımlar için kadro oluşturma
export const generateAllSquads = (turkishLeagues) => {
  const allSquads = {};
  
  Object.keys(turkishLeagues).forEach(leagueName => {
    allSquads[leagueName] = {};
    
    turkishLeagues[leagueName].forEach(team => {
      allSquads[leagueName][team.name] = generateSquad(team.name, leagueName, team.value);
    });
  });
  
  return allSquads;
};
