import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { turkishLeagues, allSquads } from '../data';

const StandingsModal = ({ setShowStandings, club, onShowSquad }) => {
  const [hoveredTeam, setHoveredTeam] = useState(null);
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  const leagueTeams = [...turkishLeagues[club.league] || []].sort((a, b) => {
    // Önce puana göre sırala (yüksek puan üstte)
    if (b.points !== a.points) {
      return b.points - a.points;
    }
    // Puan eşitse averaja göre sırala
    const aAvg = a.goalsFor - a.goalsAgainst;
    const bAvg = b.goalsFor - b.goalsAgainst;
    if (bAvg !== aAvg) {
      return bAvg - aAvg;
    }
    // Averaj eşitse gol sayısına göre sırala
    return b.goalsFor - a.goalsFor;
  });

  const handleTeamHover = (teamName, event) => {
    setHoveredTeam(teamName);
    setTooltipPosition({ x: event.clientX, y: event.clientY });
  };

  const handleTeamLeave = () => {
    setHoveredTeam(null);
  };

  return (
    <div className="fc-modal-backdrop">
      <div className="fc-modal">
        <h3>PUAN DURUMU - {club.league}</h3>
        <div className="fc-panel">
          <table className="fc-table">
            <thead>
              <tr>
                <th>Pos</th>
                <th>Takım</th>
                <th>O</th>
                <th>G</th>
                <th>B</th>
                <th>M</th>
                <th>A</th>
                <th>Y</th>
                <th>AV</th>
                <th>P</th>
              </tr>
            </thead>
            <tbody>
              {leagueTeams.map((team, index) => (
                <tr 
                  key={index}
                  style={{
                    background: team.name === club.name ? 'var(--win)' : '',
                    color: team.name === club.name ? '#fff' : '',
                    fontWeight: team.name === club.name ? 'bold' : 'normal'
                  }}
                >
                  <td>{team.position}</td>
                  <td 
                    style={{ cursor: 'pointer' }}
                    onMouseEnter={(e) => handleTeamHover(team.name, e)}
                    onMouseLeave={handleTeamLeave}
                    onClick={() => onShowSquad && onShowSquad(team.name)}
                  >
                    {team.name}
                  </td>
                  <td>{team.wins + team.draws + team.losses}</td>
                  <td>{team.wins}</td>
                  <td>{team.draws}</td>
                  <td>{team.losses}</td>
                  <td>{team.goalsFor}</td>
                  <td>{team.goalsAgainst}</td>
                  <td>{team.goalsFor - team.goalsAgainst}</td>
                  <td>{team.points}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Kadro Tooltip */}
        {hoveredTeam && (
          <div
            style={{
              position: 'fixed',
              left: tooltipPosition.x + 10,
              top: tooltipPosition.y - 10,
              background: 'rgba(0, 0, 0, 0.9)',
              color: 'white',
              padding: '10px',
              borderRadius: '6px',
              fontSize: '12px',
              maxWidth: '300px',
              zIndex: 1000,
              pointerEvents: 'none'
            }}
          >
            <div style={{ fontWeight: 'bold', marginBottom: '5px' }}>
              {hoveredTeam} Kadrosu
            </div>
            {allSquads[hoveredTeam]?.players?.slice(0, 8).map((player, index) => (
              <div key={index} style={{ marginBottom: '2px' }}>
                {player.name} ({player.age}) - {player.rating}
              </div>
            ))}
            {allSquads[hoveredTeam]?.players?.length > 8 && (
              <div style={{ color: '#ccc', fontSize: '11px' }}>
                +{allSquads[hoveredTeam].players.length - 8} oyuncu daha...
              </div>
            )}
          </div>
        )}

        <div className="fc-footerbar">
          <button className="fc-btn" onClick={() => setShowStandings(false)}>Geri</button>
        </div>
      </div>
    </div>
  );
};

StandingsModal.propTypes = {
  setShowStandings: PropTypes.func.isRequired,
  club: PropTypes.object.isRequired,
  onShowSquad: PropTypes.func,
};

export default StandingsModal;
