import React, { useState } from 'react';

function TrainingMenu() {
  const [rating, setRating] = useState(37);
  const [weeklyCost, setWeeklyCost] = useState(84);
  const [expense, setExpense] = useState(3808);
  const [newWeeklyCost, setNewWeeklyCost] = useState(95);
  const [bankBalance, setBankBalance] = useState(10222);

  const increaseRating = (value) => {
    setRating((prev) => prev + value);
    setExpense((prev) => prev + value * 100);
    setNewWeeklyCost((prev) => prev + value);
  };

  const decreaseRating = (value) => {
    setRating((prev) => prev - value);
    setExpense((prev) => prev - value * 100);
    setNewWeeklyCost((prev) => prev - value);
  };

  return (
    <div style={{
      background: 'rgba(255, 255, 255, 0.96)',
      borderRadius: '12px',
      padding: '20px',
      maxWidth: '400px',
      margin: '0 auto',
      boxShadow: '0 2px 16px rgba(0,0,0,0.2)',
    }}>
      <h3 style={{ textAlign: 'center' }}>ANTRENMAN TESİSLERİ</h3>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '16px' }}>
        <img src="/path/to/image" alt="Training" style={{ width: '80px', height: '80px' }} />
        <div>
          <div style={{ fontSize: '1.2em', fontWeight: 'bold' }}>REYTİNG {rating}</div>
          <div>Haftalık Ücret: €{weeklyCost}</div>
        </div>
      </div>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '16px' }}>
        <button onClick={() => decreaseRating(5)} style={{ background: 'red', color: 'white', padding: '10px', borderRadius: '6px' }}>«5</button>
        <button onClick={() => decreaseRating(1)} style={{ background: 'red', color: 'white', padding: '10px', borderRadius: '6px' }}>«1</button>
        <div style={{ fontSize: '1.5em', fontWeight: 'bold' }}>{rating}</div>
        <button onClick={() => increaseRating(1)} style={{ background: 'green', color: 'white', padding: '10px', borderRadius: '6px' }}>1»</button>
        <button onClick={() => increaseRating(5)} style={{ background: 'green', color: 'white', padding: '10px', borderRadius: '6px' }}>5»</button>
      </div>
      <div style={{ marginBottom: '16px' }}>
        <div>Masraf: €{expense}</div>
        <div>Yeni Haftalık Maliyet: €{newWeeklyCost}</div>
        <div>Banka Bakiyesi: €{bankBalance}</div>
      </div>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <button style={{ background: 'green', color: 'white', padding: '10px', borderRadius: '6px' }}>Geri</button>
        <button style={{ background: 'green', color: 'white', padding: '10px', borderRadius: '6px' }}>Yükselt</button>
      </div>
    </div>
  );
}

export default TrainingMenu;
