import React, { useState } from 'react';
import PropTypes from 'prop-types';

const ManagerDetailModal = ({ setShowManagerDetail, selectedManager }) => {
  const [activeTab, setActiveTab] = useState('records'); // 'records' or 'staff'
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [confirmData, setConfirmData] = useState(null);
  const [technicalStaff, setTechnicalStaff] = useState({
    assistant: [
      { name: 'Boş', avatar: '', filled: false, available: true },
      { name: 'Boş', avatar: '', filled: false, available: false }
    ],
    physio: [
      { name: 'Boş', avatar: '', filled: false, available: true },
      { name: 'Boş', avatar: '', filled: false, available: false }
    ],
    coach: [
      { name: 'Boş', avatar: '', filled: false, available: true },
      { name: 'Boş', avatar: '', filled: false, available: true },
      { name: 'Boş', avatar: '', filled: false, available: false },
      { name: 'Boş', avatar: '', filled: false, available: false }
    ],
    scout: [
      { name: 'Boş', avatar: '', filled: false, available: true },
      { name: 'Boş', avatar: '', filled: false, available: true },
      { name: 'Boş', avatar: '', filled: false, available: false },
      { name: 'Boş', avatar: '', filled: false, available: false }
    ]
  });
  
  // Sadece seçilen menajer (yeni oyun başlangıcı)
  const managerRecords = [
    {
      name: selectedManager?.name || 'E FRIAS',
      avatar: selectedManager?.name?.split(' ')[0][0] || 'E',
      mbp: '-',
      cups: 0,
      period: 'Ağu 2024 -',
      stats: { played: 0, won: 0, drawn: 0, lost: 0 },
      status: 'current'
    }
  ];



  const renderManagerProfile = () => (
    <div style={{ 
      display: 'flex', 
      alignItems: 'center', 
      gap: '15px',
      padding: '15px',
      background: '#f5f5f5',
      borderRadius: '8px',
      marginBottom: '20px'
    }}>
      {/* Avatar */}
      <div style={{
        width: '60px',
        height: '60px',
        borderRadius: '50%',
        background: '#4CAF50',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        fontSize: '24px',
        fontWeight: 'bold',
        color: '#fff'
      }}>
        {selectedManager?.name?.split(' ')[0][0] || 'E'}
      </div>
      
      {/* Manager Info */}
      <div style={{ flex: 1 }}>
        <div style={{ 
          fontWeight: 'bold', 
          fontSize: '20px',
          marginBottom: '10px'
        }}>
          {selectedManager?.name || 'E FRIAS'}
        </div>
        
        {/* Attributes */}
        <div style={{ display: 'flex', gap: '8px', marginBottom: '10px' }}>
          <span style={{
            background: '#4CAF50',
            color: '#fff',
            padding: '4px 8px',
            borderRadius: '4px',
            fontSize: '14px',
            fontWeight: 'bold'
          }}>
            M {selectedManager?.management || 37}
          </span>
          <span style={{
            background: '#4CAF50',
            color: '#fff',
            padding: '4px 8px',
            borderRadius: '4px',
            fontSize: '14px',
            fontWeight: 'bold'
          }}>
            A {selectedManager?.attacking || 37}
          </span>
          <span style={{
            background: '#4CAF50',
            color: '#fff',
            padding: '4px 8px',
            borderRadius: '4px',
            fontSize: '14px',
            fontWeight: 'bold'
          }}>
            T {selectedManager?.tactical || 38}
          </span>
        </div>
        
        {/* Tactic & Status */}
        <div style={{ fontSize: '16px', marginBottom: '5px' }}>
          {selectedManager?.tactic || '4-3-3 Pas Oyunu'}
        </div>
        <div style={{ fontSize: '16px', marginBottom: '5px' }}>
          86% mutlu
        </div>
        <div style={{ fontSize: '16px' }}>
          Ücret: €69 hftlk
        </div>
      </div>
    </div>
  );

  const renderTabs = () => (
    <div style={{ display: 'flex', marginBottom: '20px' }}>
      <button
        onClick={() => setActiveTab('records')}
        style={{
          flex: 1,
          padding: '10px',
          background: activeTab === 'records' ? 'var(--win)' : '#fff',
          color: activeTab === 'records' ? '#fff' : '#666',
          border: '1px solid #ddd',
          cursor: 'pointer',
          fontSize: '14px',
          fontWeight: 'bold'
        }}
      >
        Menajer Rekorları
      </button>
      <button
        onClick={() => setActiveTab('staff')}
        style={{
          flex: 1,
          padding: '10px',
          background: activeTab === 'staff' ? 'var(--win)' : '#fff',
          color: activeTab === 'staff' ? '#fff' : '#666',
          border: '1px solid #ddd',
          cursor: 'pointer',
          fontSize: '14px',
          fontWeight: 'bold'
        }}
      >
        Teknik Kadro
      </button>
    </div>
  );

  const renderManagerRecords = () => (
    <div>
      {managerRecords.map((record, index) => (
        <div key={index} style={{
          display: 'flex',
          alignItems: 'center',
          gap: '15px',
          padding: '15px',
          background: '#fff',
          borderRadius: '8px',
          marginBottom: '10px',
          border: '1px solid #ddd'
        }}>
          {/* Avatar */}
          <div style={{
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            background: record.status === 'current' ? '#4CAF50' : '#666',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '16px',
            fontWeight: 'bold',
            color: '#fff'
          }}>
            {record.avatar}
          </div>
          
          {/* Record Info */}
          <div style={{ flex: 1 }}>
            <div style={{ 
              fontWeight: 'bold', 
              fontSize: '16px',
              marginBottom: '5px'
            }}>
              {record.name} - {record.mbp} MBP, {record.cups} kupalar
            </div>
            <div style={{ fontSize: '14px', marginBottom: '5px' }}>
              {record.period} {record.status === 'fired' ? '(Kovuldu)' : ''}
            </div>
            <div style={{ fontSize: '14px' }}>
              O {record.stats.played} G {record.stats.won} B {record.stats.drawn} M {record.stats.lost}
            </div>
          </div>
        </div>
      ))}
    </div>
  );

  const renderTechnicalStaff = () => (
    <div>
      {/* Asistan */}
      <div style={{ marginBottom: '20px' }}>
        <h4 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>Asistan</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
          {technicalStaff.assistant.map((staff, index) => (
            <div 
              key={index} 
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '10px',
                padding: '10px',
                background: staff.filled ? '#f0f8ff' : (staff.available ? '#e8f5e8' : '#f5f5f5'),
                borderRadius: '8px',
                border: '1px solid #ddd',
                cursor: staff.available && !staff.filled ? 'pointer' : 'default'
              }}
              onClick={() => {
                if (staff.available && !staff.filled) {
                  const staffNames = ['Ahmet Yılmaz', 'Mehmet Demir', 'Ali Kaya', 'Mustafa Özkan'];
                  const randomName = staffNames[Math.floor(Math.random() * staffNames.length)];
                  if (window.confirm(`${randomName} isimli kişiyi Asistan görevine almak istiyor musunuz?\n\nMaliyet: 50.000 TL\n\nEk Bilgi: Asistan +1 M (Yönetim) puanı sağlar.`)) {
                    // Burada gerçek uygulamada staff'ı doldurma işlemi yapılacak
                    console.log(`${randomName} asistan olarak alındı`);
                  }
                }
              }}
            >
              <div style={{
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                background: staff.filled ? '#4CAF50' : (staff.available ? '#28a745' : '#ccc'),
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
                fontWeight: 'bold',
                color: '#fff'
              }}>
                {staff.avatar || (staff.available ? '+' : '?')}
              </div>
              <span style={{ fontSize: '12px' }}>{staff.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Fizyoterapist */}
      <div style={{ marginBottom: '20px' }}>
        <h4 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>Fizyoterapist</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
          {technicalStaff.physio.map((staff, index) => (
            <div 
              key={index} 
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '10px',
                padding: '10px',
                background: staff.filled ? '#f0f8ff' : (staff.available ? '#e8f5e8' : '#f5f5f5'),
                borderRadius: '8px',
                border: '1px solid #ddd',
                cursor: staff.available && !staff.filled ? 'pointer' : 'default'
              }}
              onClick={() => {
                if (staff.available && !staff.filled) {
                  const staffNames = ['Dr. Kemal Yıldız', 'Dr. Ayşe Demir', 'Dr. Hasan Kaya', 'Dr. Fatma Özkan'];
                  const randomName = staffNames[Math.floor(Math.random() * staffNames.length)];
                  if (window.confirm(`${randomName} isimli kişiyi Fizyoterapist görevine almak istiyor musunuz?\n\nMaliyet: 50.000 TL\n\nEk Bilgi: Fizyoterapist +1 M (Yönetim) puanı sağlar.`)) {
                    // Burada gerçek uygulamada staff'ı doldurma işlemi yapılacak
                    console.log(`${randomName} fizyoterapist olarak alındı`);
                  }
                }
              }}
            >
              <div style={{
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                background: staff.filled ? '#4CAF50' : (staff.available ? '#28a745' : '#ccc'),
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
                fontWeight: 'bold',
                color: '#fff'
              }}>
                {staff.avatar || (staff.available ? '+' : '?')}
              </div>
              <span style={{ fontSize: '12px' }}>{staff.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Antrenör */}
      <div style={{ marginBottom: '20px' }}>
        <h4 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>Antrenör</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
          {technicalStaff.coach.map((staff, index) => (
            <div 
              key={index} 
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '10px',
                padding: '10px',
                background: staff.filled ? '#f0f8ff' : (staff.available ? '#e8f5e8' : '#f5f5f5'),
                borderRadius: '8px',
                border: '1px solid #ddd',
                cursor: staff.available && !staff.filled ? 'pointer' : 'default'
              }}
              onClick={() => {
                if (staff.available && !staff.filled) {
                  const staffNames = ['Burak Yılmaz', 'Emre Demir', 'Can Kaya', 'Deniz Özkan'];
                  const randomName = staffNames[Math.floor(Math.random() * staffNames.length)];
                  if (window.confirm(`${randomName} isimli kişiyi Antrenör görevine almak istiyor musunuz?\n\nMaliyet: 50.000 TL\n\nEk Bilgi: Antrenör +1 A (Hücum) puanı sağlar.`)) {
                    // Burada gerçek uygulamada staff'ı doldurma işlemi yapılacak
                    console.log(`${randomName} antrenör olarak alındı`);
                  }
                }
              }}
            >
              <div style={{
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                background: staff.filled ? '#4CAF50' : (staff.available ? '#28a745' : '#ccc'),
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
                fontWeight: 'bold',
                color: '#fff'
              }}>
                {staff.avatar || (staff.available ? '+' : '?')}
              </div>
              <span style={{ fontSize: '12px' }}>{staff.name}</span>
            </div>
          ))}
        </div>
      </div>

      {/* Yetenek Avcısı */}
      <div style={{ marginBottom: '20px' }}>
        <h4 style={{ margin: '0 0 10px 0', color: 'var(--blue)' }}>Yetenek Avcısı</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' }}>
          {technicalStaff.scout.map((staff, index) => (
            <div 
              key={index} 
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '10px',
                padding: '10px',
                background: staff.filled ? '#f0f8ff' : (staff.available ? '#e8f5e8' : '#f5f5f5'),
                borderRadius: '8px',
                border: '1px solid #ddd',
                cursor: staff.available && !staff.filled ? 'pointer' : 'default'
              }}
              onClick={() => {
                if (staff.available && !staff.filled) {
                  const staffNames = ['Yusuf Yılmaz', 'Zeynep Demir', 'Yasin Kaya', 'Zara Özkan'];
                  const randomName = staffNames[Math.floor(Math.random() * staffNames.length)];
                  if (window.confirm(`${randomName} isimli kişiyi Yetenek Avcısı görevine almak istiyor musunuz?\n\nMaliyet: 50.000 TL\n\nEk Bilgi: Yetenek Avcısı +1 T (Taktik) puanı sağlar.`)) {
                    // Burada gerçek uygulamada staff'ı doldurma işlemi yapılacak
                    console.log(`${randomName} yetenek avcısı olarak alındı`);
                  }
                }
              }}
            >
              <div style={{
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                background: staff.filled ? '#4CAF50' : (staff.available ? '#28a745' : '#ccc'),
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '12px',
                fontWeight: 'bold',
                color: '#fff'
              }}>
                {staff.avatar || (staff.available ? '+' : '?')}
              </div>
              <span style={{ fontSize: '12px' }}>{staff.name}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  return (
    <div className="fc-modal-backdrop">
      <div className="fc-modal" style={{ maxWidth: '600px', maxHeight: '90vh', overflow: 'auto' }}>
        <h3>MENAJRLR</h3>
        
        {renderManagerProfile()}
        {renderTabs()}
        
        <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
          {activeTab === 'records' ? renderManagerRecords() : renderTechnicalStaff()}
        </div>
        
        <div className="fc-footerbar" style={{ marginTop: '20px' }}>
          <button className="fc-btn" onClick={() => setShowManagerDetail(false)}>
            Geri
          </button>
          <button className="fc-btn" style={{ background: 'var(--loss)' }}>
            Menajeri Kov
          </button>
          <button className="fc-btn" style={{ background: 'var(--green)' }}>
            Diğer Mnjrlr
          </button>
        </div>
      </div>
    </div>
  );
};

ManagerDetailModal.propTypes = {
  setShowManagerDetail: PropTypes.func.isRequired,
  selectedManager: PropTypes.object,
};

export default ManagerDetailModal;
