import React from 'react';
import PropTypes from 'prop-types';
import { generateFixtures, turkishLeagues } from '../data';

const FixtureModal = ({ setShowFixture, club }) => {
  // Seçilen takımın ligindeki tüm takımlar için fikstür oluştur
  const leagueTeams = turkishLeagues[club.league] || [];
  const fixtures = generateFixtures(leagueTeams, club.league);

  // Seçilen takımın maçlarını filtrele
  const teamFixtures = fixtures.filter(fixture => 
    fixture.homeTeam === club.name || fixture.awayTeam === club.name
  ).slice(0, 10); // İlk 10 maçı göster

  const getResult = (fixture) => {
    if (fixture.homeTeam === club.name) {
      if (fixture.homeScore > fixture.awayScore) return 'G';
      if (fixture.homeScore < fixture.awayScore) return 'M';
      return 'B';
    } else {
      if (fixture.awayScore > fixture.homeScore) return 'G';
      if (fixture.awayScore < fixture.homeScore) return 'M';
      return 'B';
    }
  };

  const getResultColor = (result) => {
    if (result === 'M') return 'var(--loss)';
    if (result === 'G') return 'var(--win)';
    if (result === 'B') return 'var(--draw)';
    return '#ccc';
  };

  return (
    <div className="fc-modal-backdrop">
      <div className="fc-modal">
        <h3>FİKSTÜR - {club.name}</h3>
        <div className="fc-panel">
          <table className="fc-table">
            <thead>
              <tr>
                <th>Hafta</th>
                <th>Ev Sahibi</th>
                <th>Skor</th>
                <th>Deplasman</th>
                <th>Sonuç</th>
              </tr>
            </thead>
            <tbody>
              {teamFixtures.map((fixture, index) => {
                const isHome = fixture.homeTeam === club.name;
                const result = getResult(fixture);
                const score = `${fixture.homeScore} - ${fixture.awayScore}`;

                return (
                  <tr key={index}>
                    <td>{fixture.matchday}</td>
                    <td style={{ fontWeight: isHome ? 'bold' : 'normal' }}>
                      {fixture.homeTeam}
                    </td>
                    <td>{score}</td>
                    <td style={{ fontWeight: !isHome ? 'bold' : 'normal' }}>
                      {fixture.awayTeam}
                    </td>
                    <td>
                      <span 
                        style={{ 
                          background: getResultColor(result),
                          color: '#fff',
                          padding: '2px 8px',
                          borderRadius: '4px',
                          fontWeight: 'bold'
                        }}
                      >
                        {result}
                      </span>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        <div className="fc-footerbar">
          <button className="fc-btn" onClick={() => setShowFixture(false)}>Geri</button>
        </div>
      </div>
    </div>
  );
};

FixtureModal.propTypes = {
  setShowFixture: PropTypes.func.isRequired,
  club: PropTypes.object.isRequired,
};

export default FixtureModal;
